@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hattrick.org"), domain("hat-trick.net") {

div#hattrick {
       width:850px !important;
}

div#page {
       width:850px !important;
       margin-top: 5px !important;
       margin-left:0px !important;
       border:0px !important;
       padding:0px !important;
       background-color: #aa0000 !important;
       border-color: #ffffff !important;
       -moz-border-radius:10px;
}

div#top {
      height: 0 !important;
      background-image: none !important;
}

/* Replace background image */ 
body { 
       background-image: none !important;
       background-color:#aa0000 !important;
}

div#bottom {
      display:block !important;
      background-image:none !important;
      background-color:#ffffff !important;
      -moz-border-radius:0 0 15px 15px;
}

div#footer {
      width:850px !important;
}

/* --- Header --- */

#header {
      margin:0;
      height:137px !important;
      padding:0px !important;
      background-color:#ffffff important;
      background: url(http://www.gothwalker.se/htpics/arsenal/arsenal_header_850.gif) no-repeat !important;
}

#logo {
      border: none !important;
      background: url(http://www.gothwalker.se/htpics/arsenal/skin_arsenallogo.png) no-repeat !important;
      width:100px !important;
      height:100px !important;
      top: 10px !important;
      left: 25px !important;
}

#menu {
      position:relative;
      top:112px !important;
      left:0 !important;
      right:0;
      height:23px !important;
      width:850px !important;
      z-index:auto !important;
      margin:0px !important;
      padding:0px !important;
      border-top:2px solid #eeeeee;
      background: url(http://www.gothwalker.se/htpics/arsenal/mainnav-background.png) repeat-x !important;
      background-color: #ffffff !important;
      background-position:0 0 !important;
}

#menu a {
      margin:-2px 2px 0 0 !important;
      border-top:2px solid #cccccc;
      border-right:1px solid #ffffff;
      background-image: url(http://www.gothwalker.se/htpics/arsenal/mainnav-background.png);
      background-color: #ffffff !important;
      color: #000000 !important;
      height:23px !important;
      text-transform:uppercase;
      font-size:10px !important;
      font-weight:normal !important;
      line-height:23px !important;
}

#menu a#ctl00_ucMenu_hypLogout {
      margin:-2px 0 0 0 !important;
      border-left:1px solid #ffffff;
}

#menu a:hover {
      border-top:2px solid #db0007;
      background-image: url(http://www.gothwalker.se/htpics/arsenal/mainnav-background.png);
      background-color: #ffffff !important;
      text-decoration:none;
}

#teamLinks {
      position:absolute !important;
      top: 11px;
      left:200px;
      width:400px !important;
      margin-top:0px !important;
}

#teamLinks a {
      color: #ffffff !important;
      text-decoration:none !important;
}

#teamLinks a:hover {
      color: #ffffff !important;
      text-decoration:underline !important;
}

div#ticker {
      position:absolute !important;
      left: 200px !important;
      top:52px !important;
}

div#online {
      position:absolute !important;
      top:23px !important;
      right:22px !important;
      background: url(http://www.gothwalker.se/htpics/arsenal/hattrick_red_130x32.gif) no-repeat !important;
      padding-top:34px !important;
}

div#time {
      position:absolute !important;
      top:71px !important;
      right:22px !important;
}

/* - top icons for Search etc. - */

div#shortcuts {
      position:absolute !important;
      right: 22px !important;
      top:12px !important;
}

img.scSearch {
      background-image: url(http://www.gothwalker.se/htpics/scSearch.gif) no-repeat !important;
}

img.scTransfer {
      background-image: url(http://www.gothwalker.se/htpics/scTransfer.gif) no-repeat !important;
}

img.scLive {
      background-image: url(http://www.gothwalker.se/htpics/scLive.gif) no-repeat !important;
}

img.scMail {
      background-image: url(http://www.gothwalker.se/htpics/scMail.gif) no-repeat !important;
}

img.scMySupporters {
      background-image: url(http://www.gothwalker.se/htpics/scMySupporters.gif) no-repeat !important;
}

img.scBookmarks {
      background-image: url(http://www.gothwalker.se/htpics/scBookmarks.gif) no-repeat !important;
}

img.scManager {
      background-image: url(http://www.gothwalker.se/htpics/scManager.gif) no-repeat !important;
}

/* --- Main area --- */

div#content {
      margin-bottom:0 !important;
      padding: 10px 6px 0 6px !important;
      background-color:#ffffff !important;
      background-image:none !important;
      height:auto !important;
}

img.splash {
      display:none !important;
}

div.mainRegular {
      width:454px !important;
      margin-right:10px !important;
}

div.mainRegular div.boxBody {
      padding-left:0px !important;
}

div.mainRegular div.boxBody h1 {
      margin-left:0px !important;
}

div.byline {
      margin-left: 0px !important;
}

div#ctl00_CPMain_pnlplayerInfo {
      width:326px !important;
}

div.mainWide {
      width:645px !important;
      margin-right:0px !important;
}

div.mainWide div.boxBody {
      padding-left:0px !important;
}

div.mainWide div.boxBody h1 {
      margin-left:0px !important;
}

div.mainFull {
      width:838px !important;
}

div.main a {
      color: #aa0000 !important;
}

div.main h2 a {
      color: #ffffff !important;
}

div.main h2 a:hover {
      text-decoration:underline !important;
}

/* div.subMenuBox h2, div.boxLeft h2, div.sidebarBox h2, div.mainBox h2, div#ctl00_CPMain_pnlShowLeagueEvents h2 {
      font-size:11px !important;
      //height:15px !important;
      color: #ffffff !important;
      background-image: url(http://www.gothwalker.se/htpics/arsenal/navbk.gif) !important;
      background-repeat: repeat-x !important;
      background-color: #ef0000 !important;
      //border-left:1px solid #cccccc !important;
      //border-right:1px solid #cccccc !important;
      border-bottom: 0px !important;
      padding: 6px !important;
} */

div.mainBox h2, div#ctl00_CPMain_pnlShowLeagueEvents h2, div.subMenuBox h2, div.boxLeft h2, div.sidebarBox h2, h2.tblBox, table.naked h2 {
      margin-top:0px !important;
      font-size:11px !important;
      color: #ffffff !important;
      background: url(http://www.gothwalker.se/htpics/arsenal/navbk.gif) repeat-x !important;
      background-color: #ef0000 !important;
      //border-left:1px solid #cccccc !important;
      //border-right:1px solid #cccccc !important;
      border-bottom: 0px !important;
      padding: 3px 5px 3px 6px !important;
}

