css:
@-moz-document domain("ilias.studium.kit.edu")
{
:root {
    --background: #222222;
    --level-1: #333333;
    --level-2: #444444;
    --level-3: #555555;
    --highlight: #999999;
    --warn: #481616;
    --text: white;
    --border: white;
}

div.ilc_section_Attention, a.ilc_section_Attention {
    background-color: var(--highlight) !important;
}
.il-layout-page {
    background: var(--background) !important;
}
.ilContainerBlockHeader {
    background-color: var(--level-2) !important;
}
body {
    color: var(--text);
}
.header-inner {
    background: var(--background) !important;
}
.breadcrumbs {
    background-color: var(--background) !important;
}
.breadcrumb_wrapper {
    background: var(--background) !important;
}
select, textarea {
    color: var(--background);
}
.ilObjListRow {
    border-bottom: 2px solid var(--level-2);
    background-color: var(--background);
}
div.ilc_text_block_Information, html.il-no-tiny-bg body#tinymce.ilc_text_block_Information {
    background-color: var(--level-1);
}
h2.ilHeader {
    color: var(--highlight);
}
.form-horizontal {
    margin-bottom: 20px;
    background: var(--level-2);
}
.ilFormHeader, .ilFormFooter {
    color: var(--text);
    background-color: var(--level-1);
}
button, html input[type="button"], input[type="reset"], input[type="submit"] {
    background: var(--level-1);
}
.il-panel-listing-std-container {
    background-color: var(--level-1);
}
.il-panel-listing-std-container > h2 {
    color: var(--highlight);
}
.il-item-group {
    background-color: var(--level-2);
}
.il-item-group > h3 {
    color: var(--highlight);
}
.il-item {
    background-color: var(--level-3);
}
.il-item .il-item-property-name {
    color: var(--text);
}
.panel .panel-heading.ilHeader, .panel .panel-heading.ilBlockHeader {
    background-color: var(--level-1);
}
.panel-flex .il-viewcontrol-pagination, .panel-flex .il-viewcontrol-section {
    background-color: var(--level-1); 
}
.panel .panel-body {
    background-color: var(--level-1);
}
.panel .panel-heading.ilHeader h4, .panel .panel-heading.ilBlockHeader h4, .panel .panel-heading.ilHeader h4.ilBlockHeader, .panel .panel-heading.ilBlockHeader h4.ilBlockHeader {
    color: var(--highlight);
}
ul.dropdown-menu {
    background-color: var(--level-1);
}
ul.dropdown-menu > li > .btn {
    
    color: var(--text);
}
table.calmini tr:nth-child(odd) {
    background-color: var(--level-2);
}
table.calmini tr, table.calmini td, table.calmini th {
    color: var(--text);
}
table.calmini tr:nth-child(even) {
    background-color: var(--level-3);
}
.btn.engaged, .btn.btn-tag.engaged, .btn.engaged:hover, .btn.btn-tag.engaged:hover, .btn.engaged:active, .btn.btn-tag.engaged:active, .btn.engaged.active, .btn.btn-tag.engaged.active {
    background-color: var(--level-3);
    color: #00876c;
}
a#il_mhead_t_focus {
    color: #324b89;
}
.il_VAccordionHead {
    background-color: var(--level-1);
}
.ilExcAssignmentBody {
    background-color: var(--level-2);
}
.ilInfoScreenSec.form-horizontal .form-group {
    background-color: var(--level-3);
}
.il_InfoScreenPropertyValue {
    background-color: var(--level-3);
}
.il_InfoScreenProperty {
    color: var(--text);
}
.dropdown-menu li > a {
    color: var(--text);
}
.form-horizontal .help-block {
    color: var(--text);
}
span.ilc_text_inline_Mnemonic {
    background-color: var(--level-3);
}
div.ilc_text_block_Attention, html.il-no-tiny-bg body#tinymce.ilc_text_block_Attention {
    background-color: var(--level-3);
}
.navbar-default .navbar-collapse, .navbar-default .navbar-form {
    border-color: var(--level-1);
}
.ilToolbar .container-fluid {
    background-color: var(--level-1);
}
div.ilTableOuter .table{
    background-color: var(--level-1);
}
.table > caption + thead > tr:first-child > th, .table > colgroup + thead > tr:first-child > th, .table > thead:first-child > tr:first-child > th, .table > caption + thead > tr:first-child > td, .table > colgroup + thead > tr:first-child > td, .table > thead:first-child > tr:first-child > td {
     background-color: var(--level-1);
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: var(--level-2);
}
div.ilTableCommandRow, div.ilTableCommandRowTop {
    background-color: var(--level-1);
}
div.ilTableSelectAll {
    background-color: var(--level-1);
}
.il-maincontrols-slate.il-maincontrols-slate-notification .il-maincontrols-slate-notification-title {
    background-color: var(--level-2);
}
.il-maincontrols-mainbar .btn-bulky, .il-maincontrols-metabar .btn-bulky, .il-maincontrols-mainbar .il-link.link-bulky, .il-maincontrols-metabar .il-link.link-bulky {
    background-color: var(--background);
}
.il-link.link-bulky .bulky-label {
    color: var(--text);
}
.il-mainbar-triggers .btn-bulky:hover, .il-mainbar-triggers .il-link.link-bulky:hover, .il-mainbar-triggers .btn-bulky.engaged, .il-mainbar-triggers .il-link.link-bulky.engaged, .il-mainbar-triggers .btn-bulky:focus-visible, .il-mainbar-triggers .il-link.link-bulky:focus-visible {
    background-color: var(--highlight);
}
.il-layout-page.with-mainbar-slates-engaged .il-maincontrols-mainbar .il-mainbar-slates .il-maincontrols-slate {
    background-color: var(--level-1);
}
div.ilc_section_Lerneinheiten, a.ilc_section_Lerneinheiten {
    background-color: var(--level-1);
    border-style: none;
}
div.ilc_text_block_Headline111, html.il-no-tiny-bg body#tinymce.ilc_text_block_Headline111 {
    color: var(--highlight);
}
.form-horizontal .control-label {
    color: var(--text);
}
.form-horizontal .radio, .form-horizontal .checkbox, .form-horizontal .radio-inline, .form-horizontal .checkbox-inline {
    color: var(--text);
}
.il-mainbar-tools-button .btn-bulky.engaged {
    background-color: var(--level-1);
    color: var(--text);
}
.il-maincontrols-metabar .btn-bulky.engaged {
    background-color: var(--level-3);
}
.il-mainbar-tools-entries-bg {
    background: var(--level-1);
}
.il-mainbar-tools-entries .btn-bulky.engaged, .il-mainbar-tools-entries .btn-bulky.engaged, .il-mainbar-tools-entries .il-link.link-bulky.engaged {
    background-color: var(--level-1);
    color: var(--text);
}
div.ilSideBarHead h3, div.ilSideBarHead h1 {
    color: var(--highlight);
}
#button_ilias_login {
    color: var(--level-1);
}
#button_shib_login {
    color: var(--level-1);
}
}
