css:
@-moz-document url-prefix("https://kerava.inschool.fi/")
{
  :root
  {
    /* add css variables here */
    --white: #dbdbdb;
    --ghost1: #dbdbdb22;
    --ghost2: #222121c2;
    --dark1: #242222;
    --dark2: #2d2b2b;
    --border1: #000;
    --turquoise1: #0e8c75;
    --green1: #599900;
    --alert1: #4e3d1a;
    --alert2: #d0a235;
    --gradient1: linear-gradient(to bottom right,#1a78b3 0,#0e8c75 100%);
  }

  body
  {
    background: var(--dark1) !important;
  }

  .navbar
  {
    background: var(--gradient1);
  }

  .navbar .nav > li > a,
.dropdown.tabdrop > .dropdown-toggle,
.navbar .nav .dropdown.tabdrop.open .dropdown-toggle
  {
    border-color: transparent !important;
    border-right: 0px solid transparent !important;
    background: transparent !important;
    filter: none !important;
    box-shadow: none !important;
  }

  .navbar .nav > li > a:hover,
.dropdown.tabdrop > .dropdown-toggle:hover
  {
    border-color: transparent !important;
    background: var(--ghost1) !important;
    filter: none !important;
    box-shadow: none !important;
  }

  .navbar .nav .dropdown.tabdrop.open .dropdown-toggle > .icon-align-justify,
.navbar .nav .dropdown.tabdrop.open .dropdown-toggle > .icon-align-justify::after,
.navbar .nav .dropdown.tabdrop.open .dropdown-toggle > .icon-align-justify::before
  {
    background: var(--white);
  }

  .navbar .nav .dropdown.tabdrop > .dropdown-toggle[aria-expanded="true"]
  {
    background: var(--ghost1) !important;
  }

  .dropdown-menu
  {
    border: 0px solid transparent !important;
    border-radius: 0;
    background: var(--dark2);
    opacity: 0.8;
    transition: background 0.2s, opacity 0.2s;
    backdrop-filter: blur(2px) invert(20%);
  }

  .dropdown-menu:hover
  {
    background: var(--dark2);
    opacity: 1;
  }

  .dropdown-menu a
  {
    color: var(--white) !important;
  }

  .dropdown-menu a:hover
  {
    background-color: var(--ghost1) !important;
    color: var(--white) !important;
  }

  .btn.btn-default
  {
    background: var(--dark1);
    color: var(--white);
    border-color: transparent;
  }

  .btn.btn-default:hover
  {
    background: var(--gradient1);
    color: var(--white);
    border-color: transparent;
  }

  .panel
  {
    background: var(--dark2);
    border-color: transparent;
  }

  .panel .panel-side
  {
    background: transparent;
    border-left: none;
  }

  td.no-events
  {
    background-color: var(--dark1) !important;
  }

  td.no-events a.vismaDatePickerDays
  {
    color: var(--white) !important;
  }

  td.no-events a.vismaDatePickerDays.vismaDatePickerWeekend
  {
    color: var(--ghost1) !important;
  }

  table.vismaDatePickerCalendar th span, table.vismaDatePickerCalendar td span, table.vismaDatePickerCalendar th a, table.vismaDatePickerCalendar td a
  {
    border-radius: 9em;
    display: block;
    margin: 0 auto;
    text-align: center;
    text-decoration: none;
    width: 25px;
    color: unset !important;
  }

  table.vismaDatePickerCalendar td a:hover
  {
    background: none repeat scroll 0 0 var(--ghost2) !important;
    border: 1px solid var(--ghost1) !important;
  }

  table.vismaDatePickerCalendar td a:hover
  {
    background: none repeat scroll 0 0 #ebf4fa;
    border: 1px solid #d8eaf5;
  }

  .datepick-popup, .date-inline
  {
    background: var(--dark2);
    border: 1px solid var(--dark1);
    ;
  }

  .alert
  {
    background: var(--alert1);
    border-color: var(--alert2);
  }

  .alert p
  {
    color: var(--alert2);
  }

  .alert
  {
    color: var(--white);
  }

/* J L 6 0 7 9 */

  img[src="https://cdn.inschool.fi/2.33.10.3/nc3/img/visma-logo.svg"]
  {
    display: none;
  }

/* modal */

  .modal-content
  {
    background: var(--dark1) !important;
  }

  .modal-header
  {
    background: none !important;
    box-shadow: none !important;
    border: none !important;
  }

  .modal-title
  {
    color: var(--white) !important;
  }

  .modal-footer
  {
    border: none !important;
  }

/* sessionexpired */

  .login-page
  {
    background: var(--gradient1) !important;
  }

  .login-page .alert,
.login-page .login-box
  {
    background: var(--ghost2);
  }

  .login-page .login-box > *
  {
    background: none !important;
  }

/* sidepanel */

  .sidepanel
  {
    background: var(--dark1);
  }

/* text and such */

  *
  {
    color: var(--white);
  }

  .errorPage-container .error-page
  {
    background: var(--dark2);
  }

  #cookie-disclaimer
  {
    margin: 0 3em 0 3em;
  }

  #cookie-disclaimer
  {
    color: var(--white) !important;
    width: unset !important;
  }

  .popover
  {
    background-color: var(--dark1);
    border: none;
    border-color: var(--dark1);
  }

  .popover-title
  {
    border-bottom: none;
  }

  .popover.bottom>.arrow,
.popover.bottom>.arrow:after
  {
    border: none;
  }

  .form-group label
  {
    color: var(--white);
  }

  .table>tbody td.letter-text
  {
    color: var(--white);
    background-color: var(--dark1) !important;
  }

/* viestit */

  .nav-tabs:not(.navbar-nav) > li > a,
.nav-tabs:not(.navbar-nav) > li > a::before,
.nav-tabs:not(.navbar-nav) > li > a::after,
.nav-tabs:not(.navbar-nav) > li.active:not(.tabdrop) > a,
.nav-tabs:not(.navbar-nav) > li.active:not(.tabdrop) > a::before,
.nav-tabs:not(.navbar-nav) > li.active:not(.tabdrop) > a::after
  {
    background: transparent !important;
    color: var(--white);
  }

  .nav-tabs:not(.navbar-nav) > li.active:not(.tabdrop) > a
  {
    background: var(--dark2) !important;
    border-radius: 1em 1em 0 0;
    border: 2px solid var(--green1);
    border-bottom: none;
    transform: translateY(-1px);
  }

  .table > tbody
  {
    background-color: var(--dark2);
  }

  .table > tbody > tr:nth-child(2n+1) > td,
.table > tbody > tr:nth-child(2n+1) > th
  {
    background-color: var(--dark1) !important;
  }

  .dock-floating
  {
    background-color: var(--ghost1);
    padding: 0.25em 1em;
    border: 0px solid transparent;
  }

  .dock-floating > .pull-left
  {
    margin-top: 0.3em;
  }

  .recipients-selected .recipient-item
  {
    background: var(--dark2);
    border: 1px solid var(--ghost1);
  }

  #message-wrapper *
  {
    color: var(--dark1);
    ;
  }

/* työjärjestys */

  .schedule .block-area-container .schedule-container .day-separator
  {
    border-right: 1px solid var(--ghost2);
  }

  .schedule .block-area-container .hours-container div
  {
    border-top: 1px solid var(--dark1);
  }

  .schedule .block-area-container .schedule-container .hour
  {
    border: 1px solid var(--dark1);
  }

  .schedule .block-area-container .schedule-container .block
  {
    background-color: var(--dark1) !important;
    border-color: transparent!important;
  }

  .tooltip
  {
    background: var(--dark1) !important;
    border-color: var(--ghost1) !important;
  }

/* opinnot */

  #choices-tree
  {
    background: none;
    border: none;
  }

  #choices-tree *
  {
    color: unset;
  }

  .well
  {
    background: var(--dark1);
    border: none;
  }

  .well label
  {
    color: var(--white) !important;
  }

  .tab-content .table:not(.index-table)
  {
    border: 1px solid var(--ghost1);
  }

  .table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th
  {
    border-left: 1px dotted var(--ghost1);
  }

  .table tfoot.summary td
  {
    border-top: 1px solid var(--ghost1);
  }

  .table>tfoot>tr>th, .table>thead>tr>th
  {
    border-bottom: 1px solid var(--ghost1);
  }

  .table > thead > tr
  {
    background: var(--gradient1);
  }

  .table > thead > tr > th
  {
    color: var(--white) !important;
  }

  select > option
  {
    background: var(--dark2);
  }

  .table .table, .table
  {
    background-color: var(--ghost1) !important;
    border-color: var(--ghost1);
  }

  .table.table-hover>tbody>tr:hover>td
  {
    background: var(--ghost2) !important;
  }

  #choices-table .level1 TD
  {
    background: var(--dark2);
    border: 1px solid var(--ghost1) !important;
    border-left: none;
    border-right: none;
  }
}
