css:
@-moz-document domain("corobizar.com") {
/*General*/
body.chakra-ui-dark .css-1ugf590{
	background: #121212;
}

/*Mode studio Barre*/
.css-1nl8ga3{
	opacity: 1;
}
/*Mode studio open*/
.css-1i0qh12 .css-1h33bb7 > :not(style), .css-1ogo19g .css-1h33bb7,  .css-1h33bb7{
	/*height: calc(100% - 50px);*/
    height: calc(100vh - 50px);
    top: 0;
}
/*Mode studio close*/
.css-1lm5zqr .css-1h33bb7 > :not(style){
	/*height: calc(100% - 50px);*/
    height: calc(100vh - 80px);
}
.css-2ccfn3{
	/*height: calc(100% - 50px);*/
    height: calc(100vh - 50px);
}
.css-1ogo19g{
	top: 50px;	
}


/*Stream et tchat*/
.css-1b07fhr, .css-o0bkpq, .css-2f1php{
	width: 700px;
}
.css-o0bkpq{
	height: calc(700px / 1.7777777);
}
.css-2f1php{
	height: calc(100% - 700px / 1.7777777);
}
.css-wllpgz{
	width: calc(100% - 700px);
}
.css-1i0qh12{
	height: 100%;
}
.css-mnytc0{
	position: relative;
    display: block;
    overflow: auto;
}
.css-19buzo3{
	margin-top: 10px;
}

/*Barre du haut*/
.css-1236gdv, .css-51oseh{
	height: 80px;
}
.css-1lm5zqr{
	height: calc(100vh - 80px);
}
.css-1lm5zqr{
	top: 80px;
}
.css-1vqwc7o{
	width: 40px;
	height: 40px;
    min-width: 40px;
	min-height: 40px;
}
.css-6ds7qi {
    bottom: 35px;
}

.css-1qq18sm{
	float: right;
}
.css-1h4ws66 {
    width: 350px;
    display: block;
}
.css-h8fysx, .css-n21gh5{
    display: inline-flex;
}
.css-h8fysx{
    padding-top: 0;
}
.css-n21gh5 {
    padding-left: 40px;
    margin-top: 0 !important;
}
.css-gmuwbf{
	display: block;
}
.css-gmuwbf img{
	height: auto;
}
.css-1juxobg{
	right: -15px;
}
.css-128nlh6 {
    font-size: 10px;
}
.css-1juxobg{
	width: 20px;
	height: 20px;
}
.css-xgni61{
	margin-top: 0;
}
.css-xgni61 > :not(style) ~ :not(style){
		margin-top: 15px;
}
.css-1fphnng{
	width: 40px;
}
.css-186o2ck{
	width: 45px;
}

/*PP + XP*/
.css-15xbpqr {
    width: 100%;
    height: 100%;
  transform: scale(1.1);
}

/*footer*/
.css-1a6scmc{
	margin-bottom: 20px;
}


/*page FAQ*/
.css-1xkyozy{
	right: 750px;
}
.css-om3715 {
    padding-right: 250px;
}

/*Corrige probleme FAQ cache la barre du haut au clique sur index*/
body{
    height: 100vh;
    /*display: contents;*/
}
#root{
	min-height: 100%;
    min-width: 100%;
    position: fixed;
    top: 0;
    left: 0;
}
/*.css-1ugf590{
	height: 100%;
}*/
.css-9t7zlm {
    height: calc(100vh - 100px);
}

/*Corrige probleme du bouton de volume sous le tchat twitch*/
.css-1h33bb7{
	z-index: 1;
}
.css-1qq679y{
	left: -20px !important;
}

/*Media query - HD et HD+*/
@media (min-width: 1280px) and (max-width: 1919px) {
	/*Barre du haut*/
    .css-1236gdv, .css-51oseh{
        height: 100px;
    }
    .css-1lm5zqr{
        height: calc(100vh - 100px);
    }
    .css-1lm5zqr{
        top: 100px;
    }
    
    /*Infos utilisateur au dessus du tchat*/
    .css-116zn0b{
        padding: 0;
    }
    .css-1h4ws66 {
  		margin-inline-start: 25px !important;
	}
    .css-gmuwbf{
    	margin-inline-start: 0;                         
    }
    .css-n21gh5 {
        padding-left: 10px;
    }
    .css-6ds7qi{
        right: 35px;
    }
}

/*Media query - HD*/
@media (min-width: 1280px) and (max-width: 1599px) {
    /*Infos utilisateur au dessus du tchat*/
    .css-n21gh5 {
  		display: block;
	}
    
    /*Stream et Tchat*/
	.css-1b07fhr, .css-o0bkpq, .css-2f1php, .css-1nmodvy{
        width: 29vw;
    }
    .css-o0bkpq{
        height: calc(29vw / 1.7777777);
    }
    .css-2f1php, .css-1nmodvy{
        height: calc(100% - 29vw / 1.7777777);
    }
    .css-wllpgz{
        width: calc(100% - 29vw);
    }
    
    /*Fanarts page*/
    .css-1n6l7p7, .css-1rbgdf8{
        max-width: calc(100% - 29vw);
    }
    .css-oaytql {
    	grid-template-columns: repeat(2, minmax(0px, 1fr));
	}

    /*page FAQ*/
    .css-1n6l7p7, .css-1rbgdf8{
        padding-left: 0;
    }
    .css-1xkyozy{
        right: 29vw;
        max-width: 320px;
        top: 100px;
    }
    .css-om3715 {
        padding-right: 350px;
    }
}


/*Media query - HD+*/
@media (min-width: 1600px) and (max-width: 1919px) {
    
    /*Stream et Tchat*/
	.css-1b07fhr, .css-o0bkpq, .css-2f1php, .css-1nmodvy{
        width: 550px;
    }
    .css-o0bkpq{
        height: calc(550px / 1.7777777);
    }
    .css-2f1php, .css-1nmodvy{
        height: calc(100% - 550px / 1.7777777);
    }
    .css-wllpgz{
        width: calc(100% - 550px);
    }
    
    /*Infos utilisateur au dessus du tchat*/
    .css-6ds7qi {
        right: 60px;
    }
    
    /*Fanarts page*/
    .css-1n6l7p7, .css-1rbgdf8{
        max-width: calc(100% - 550px);
    }
    .css-oaytql {
    	grid-template-columns: repeat(2, minmax(0px, 1fr));
	}

    /*page FAQ*/
    .css-1n6l7p7, .css-1rbgdf8{
        padding-left: 0;
    }
    .css-1xkyozy{
        right: 550px;
        max-width: 320px;
        top: 100px;
    }
    .css-om3715 {
        padding-right: 350px;
    }
    
    /*Taille du jeu*/
    #game-webgl, .css-19buzo3 {
  		width: calc(100vw - 550px) !important;
  		height: calc( ( 100vw - 550px ) / 1.777777 ) !important;
	}
}

/*Media query - Full HD*/
@media (min-width: 1920px) {
    /*Infos utilisateur au dessus du tchat*/
    .css-116zn0b{
        padding: 25px 20px;
    }
    
    /*Stream et Tchat*/
	.css-1b07fhr, .css-o0bkpq, .css-2f1php, .css-1nmodvy{
        width: 700px;
    }
    .css-o0bkpq{
        height: calc(700px / 1.7777777);
    }
    .css-2f1php, .css-1nmodvy{
        height: calc(100% - 700px / 1.7777777);
    }
    .css-wllpgz{
        width: calc(100% - 700px);
    }
    
    .css-1lm5zqr .css-1h33bb7{width: 450px;} /*largeur du tchat par defaut en mode studio off*/
    /*si le bandeau dernieres videos est affiche*/
    .css-1sm8rq4 {
        width: calc(100% - (340px + 450px) - 40px);
        left: 360px;
    	transform: translate(0);
    } 
    .css-n9dw3g{width: calc(100% - 500px);} /*si le bandeau dernieres videos est masque*/
    
    /*Fanarts page*/
    .css-oaytql {
    	grid-template-columns: repeat(3, minmax(0px, 1fr));
	}
    .css-1n6l7p7, .css-1rbgdf8{
        max-width: calc(100% - 700px);
	}

    /*page FAQ*/
    .css-1n6l7p7, .css-1rbgdf8{
        padding-left: 20px;
    }
    .css-1xkyozy{
        right: 750px;
        max-width: auto;
    }
}
.css-19buzo3{background-color: transparent;}
}
