css:
@-moz-document domain('xueqiu.com'), url-prefix('https://www.xueqiu.com/') {
  html,
  body,
  a,
  .modal--lg,
  .snbim-sview,
  .search_box,
  .im_message_wrap,
  .im_dialog_footer,
  #im_dialogs .snbim_card_msg .msg_icw,
  .snbim-sessionlist-wrap .session_item {
    color: rgb(146, 144, 147) !important;
    background-color: rgb(36, 37, 37) !important;
  }
  .snbim_bigcard .snbim_card_title,
  .snbim_bigcard .snbim_card_link,
  #im_dialogs .im_dlg_editor {
    color: rgb(146, 144, 147) !important;
  }
  blockquote,
  font {
    color: rgb(106, 115, 125) !important;
  }

  blockquote {
    background: #000 !important;
    margin: 1em 0 !important;
    padding: 10px 40px !important;
  }
  .nav {
    position: unset !important;
    background-color: #000 !important;
    color: rgb(106, 115, 125) !important;
  }
  .nav__placeholder,
  .chart-container-box,
  .footer,
  .stock-links,
  .stock__side,
  .quote-container,
  .nav__logo,
  .nav__menu__item + .nav__menu__item,
  .home__stock-index,
  .user__col--lf,
  .home__col--rt,
  .sortable td + td,
  .optional__tb > .sortable,
  .profiles__side {
    display: none !important;
  }
  .red-rise-green-fall .stock-rise {
    color: #a34838 !important;
  }
  .lite-editor,
  .lite-editor__textarea {
    background-color: #202224 !important;
  }
  .timeline__item__content .content,
  .timeline__item__forward .content {
    color: rgb(146, 144, 147) !important;
  }
  .timeline__item__content .content a,
  .timeline__item__forward .content a {
    color: rgb(64, 159, 231) !important;
  }
  .container-sm .stock-price .stock-current {
    font-size: 22px !important;
  }
  .stock__main,
  .user__col--middle {
    width: unset !important;
  }
  .container-sm .stock-name {
    color: #545866 !important;
  }
  .timeline__item__content--longtext:hover {
    background-color: #000000 !important;
    outline: none !important;
  }
  .timeline__item__title.column:before {
    color: rgb(64, 159, 231) !important;
  }
  .profiles__hd__bg {
    background: none !important;
  }
  .profiles__hd__bg > .container {
    background: none !important;
  }
  .pagination > .active {
    color: #fff !important;
  }
  .timeline__tab__tags > a.active {
    background: #000 !important;
  }
  .stock-timeline .stock-timeline-header .left .sort-types a.active{
    color: #06c !important;
  }
}
