css:
@-moz-document domain("xakep.ru") {
 html, body {
    background:#fdf6e3!important;
    color:#002b36!important;
}
.bdaia-post-content p {
    color:#586e75!important;
}
.bdaia-post-content h1, .bdaia-post-content h2, .bdaia-post-content h3, .bdaia-post-content h4, .bdaia-post-content h5, .bdaia-post-content h6 {
    color:#dc322f!important;
}
a, a:link, a:active {
    color: #dc322f;
}
.bdaia-post-template-style7 .bdaia-post-style7-head {
    display: none;
}
.bdaia-post-sharing ul, .bdaia-post-sharing ul li {
    line-height: 0;
    list-style: outside none none !important;
    margin: 0 !important;
    padding: 0 !important;
    display: none;
}
#navigation .bdaia-alert-new-posts {
    background-color: #ea0253;
    color: #fff;
    cursor: pointer;
    display: none !important;
    float: right;
    height: 48px;
    padding: 0;
    position: relative;
}
.bdaia-header-default .topbar .top-right-area {
    float: right;
    display: none;
}
#toc {
    background: none!important;
}
/*Menu*/
 .bdaia-header-default #navigation .navigation-wrapper {
    background-color: #111 !important;
}
.bdaia-header-default #navigation .primary-menu #menu-primary>li:hover>a {
    position:relative;
    color:#4172b7;
    z-index:1000004
}
body.rtl .bdaia-header-default #navigation .primary-menu ul#menu-primary>li.fa-icon.menu-item-has-children>a {
    padding-right:16px;
    padding-left:16px
}
.bdaia-header-default #navigation .primary-menu ul#menu-primary > li:hover > a, .bdaia-header-default #navigation .primary-menu ul#menu-primary > li.current-menu-item > a, .bdaia-header-default #navigation .primary-menu ul#menu-primary > li.current-menu-ancestor > a, .bdaia-header-default #navigation .primary-menu ul#menu-primary>li.current-menu-parent>a {
    background-color:#333
}
/**/
 .bdMain {
    background-color: #fdf6e3;
}
.bdaia-post-template-style5 .bdaia-post-template {
    background: #171717 none repeat scroll 0 0;
    padding: 30px 15px;
}
/*Box*/
 .bdaia-block-wrap, .bdaia-blocks, .bdaia-blocks.bdaia-block6 .block-article {
    background: none!important;
}
.bdaia-blocks.bdaia-block6 .block-article .block-article-content-wrapper {
    background-color: none;
    margin: 6px 30px;
    padding: 20px 20px 0;
    position: relative;
    z-index: 3;
}
.bd-sidebar .widget {
    background: #eee8d5!important;
}
/*header page*/
 .bdaia-template-head {
    display: none
}
/**/
 #ss2, text-34 {
    display: none
}
.bd-sidebar, .widget {
    display: none
}
.bd-cat-link {
    background-color: #839496;
}
.bdaia-block-wrap {
    box-shadow: none;
}
/*page*/
 .bd-main {
    float: none!important;
    margin: 10px auto
}
/*poup*/
 .et_bloom .et_bloom_popup.et_bloom_animated::after, .et_bloom_header_outer, .et_bloom .et_bloom_animated .et_bloom_animation_slidedown, .et_bloom .et_bloom_inline_form .et_bloom_image.et_bloom_image_slidedown, .et_bloom .et_bloom_widget_content .et_bloom_image.et_bloom_image_slidedown, .et_bloom .et_bloom_visible_image.et_bloom_image_slidedown {
    display: none!important;
}
.xakepcut, .xakepcut:before {
    display: none!important;
}
.post-image {
    display: none!important;
}
.bdayh-post-header {
    background: none!important;
}
.bdaia-posts-grid-post-inner {
    background: none!important;
}
/*tag*/
 .tagcloud a, .tagcloud span {
    background: #000;
    border:1px solid #111!important;
}
h4.block-title, h4.block-title span {
    background: #eee8d5
}
/*footer*/
 .bwb-article-img-container {
    display:none!important;
}
/*coding*/
 .block-article-img-container {
    display: none!important;
}
.bdaia-block-wrap {
    background-color:#fdf6e3;
}
.bdaia-blocks {
    background-color: #fdf6e3;
}
.bdaia-blocks.bdaia-block6 .block-article {
    background: none!important;
}
.bdaia-blocks.bdaia-block6 .block-article .block-article-content-wrapper {
    background-color: #fdf6e3;
}
.bdaia-blocks.bdaia-block6 p.block-exb {
    color:#002b36;
}
.bdaia-blocks.bdaia-block6 .block-article.bd-col-md-6 .block-article-content-wrapper {
    margin: 0 20px;
}
/*Content*/
 #content .bdaia-post-content .lead, #content .bdaia-post-content .excerpt {
    color:#002b36!important;
}
#content .bdaia-post-content .panel {
    background-color: #eee8d5!important;
    border: 1px #002b36 solid;
}
.xmd .highlight {
    background: #eee8d5;
}
xmd .highlight .wrapper .l, .xmd .highlight .wrapper {
    background:#eee8d5;
    padding: 5px 12px;
    border: 1px #002b36 solid;
}
.xmd p code, .xmd li > code {
    color: #268bd2;
}
}
