css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ch9.staemme.ch") {


a:link	{ font-weight:bold; color: #804000; text-decoration:none; }
a:visited	{  font-weight:bold; color: #804000; text-decoration:none; }
a:active	{  font-weight:bold; color: #0082BE; text-decoration:none; }
a:hover { font-weight:bold; color: #0082BE; text-decoration:none; }
td	{ font-size:9pt; }
.hidden { color:#DED3B9; }
table.main { border-width:2px; border-style: solid; border-color:#804000; background-color:#F1EBDD; }
table.news { background-color:#F1EBDD; }
.in_border { background-color:#F1EBDD; text-align:left;}

span.timer_replace span { display:none; }

form { margin: 0px; }
p:first-child, h1:first-child, h2:first-child, h3:first-child, h4:first-child, 
	h5:first-child, h6:first-child, h1+p, h2+p, h3+p, h4+p, h5+p, h6+p { margin-top: 0px; }

table.blind { border-spacing:0px; }
table.blind td { padding: 0px; }

table.vis td { background-color:#F8F4E8; }
table.vis td.selected { background-color:#DED3B9; }
table.vis tr.nowrap td { white-space:nowrap; background-color:#F8F4E8; }
table.vis tr.units_own_home td { background-color:#F8F4E8; }
table.vis tr.units_there td { background-color:#F3EBCF; }
table.vis tr.units_away td { background-color:#EDE1B7; }
table.vis tr.row_a td { background-color:#F8F4E8; }
table.vis tr.row_b td { background-color:#F3EBCF; }

tr.nowrap td { white-space:nowrap; }

table.intro td { background-color:#E0E0E0; line-height:1.5em; padding:6px;   font-size:8pt}

/* MenÃ¼leiste */
table.menu {
	background-color: #DED3B9;
        margin-top: 0px;
}
table.menu td a {
	background-color: #E0E0E0;
	border: 1px solid #3D3D3D;
        -moz-border-radius: 15px;
        margin: 1px;
        padding-left: 2px;
        padding-right: 2px;
}
table.menu:hover td a {
	background-color: #B8B8B8;
	border: 1px solid #7D7D7D;
        color: #9E9E9E;
}
table.menu td a:hover {
	background-color: #616161;
	border: 1px solid #75BAFF;
        color: #999999;
        -moz-border-radius: 15px;
}

/* Quickbar */
table.menu#quickbar {
	border-spacing: 0px;
}


/* Top-MenÃ¼-Item mit Hover */
table.menu tr td {
	padding: 0px; padding-left: 3px; padding-right: 3px;
        margin-top: 0px;
	background-color: #DED3B9;
}
table.menu tr td:before {
        content: "";
}
table.menu tr td:first-child:before {
        content: "";
}

/* Quickbar MenÃ¼Item */
table.menu#quickbar tr td {
	padding-top: 0px; padding-bottom: 0px;
}

/* Das gesamte UntermenÃ¼ */
table.menu tr td table {
	z-index:100;
	position:absolute;
	border-top: 1px solid black;
	border-top: 1px solid black;
	margin-left: -5px; margin-top:1px;
	visibility: hidden;
}

/* Das gesamte UntermenÃ¼ in sichtbar */
table.menu tr td:hover table, table.menu tr td.hover table {
	visibility: visible;
}

/* Ein UntermenÃ¼punkt */
table.menu tr td table tr td {
	border: 1px solid black;
	border-top: none;
}
/* Ein Link in einem UntermenÃ¼punkt */
table.menu tr td table tr td a{
	display: block;
	width: 100%;
}

table.map td { font-size:8pt; text-align:center; vertical-align:middle;}
td.this { background-color:#FFFFFF; }
td.tribe { background-color:#F0C800; }
td.ally { background-color:#0000F4; }
td.partner { background-color: rgb(0, 160, 244); }
td.nap { background-color: rgb(128, 0, 128); }
td.enemy { background-color: rgb(244, 0, 0); }
td.other { background-color: rgb(180, 0, 0); }
td.sleep { background-color:#000040; }
td.grey { background-color:gray; }

td.space-left { border-left:1px solid #175E36; } /* zwischen normalen Feldern */
td.space-bottom { border-bottom:1px solid #175E36; } 
td.border-left { border-left:1px solid #103530; } /* "Sektorgrenzen" S1/S2/S6 */
td.border-bottom { border-bottom:1px solid #103530; }
td.sector-left { border-left:1px solid #DED3B9; } /* Sektorgrenzen */
td.sector-bottom { border-bottom:1px solid #DED3B9; }

td.space-left-new {}
td.space-bottom-new {}
td.border-left-new { border-left:1px solid #214B18; } 
td.border-bottom-new { border-bottom:1px solid #214B18; }
td.sector-left-new { border-left:1px solid #DED3B9; }
td.sector-bottom-new { border-bottom:1px solid #DED3B9; }
td.con-left-new { border-left:1px solid #000000; }
td.con-bottom-new { border-bottom:1px solid #000000; }

tr.center td { text-align:center; }
tr.lit td { font-weight:bold; background-color:#FADC9B; }
tr.lit2 td { font-weight:bold; background-color:#F0E6C8; }
tr.admin td { background-color:#F0E6C8; }
tr.mini td { font-style: italic; font-size: xx-small }
th		{ font-size:9pt; text-align: left; font-weight:bold; background-color:#DED3B9; }
body	{ background-color: #DED3B9; font-size:9pt; font-family: Verdana, Arial }
h1		{ font-size:16pt;}
h2		{ font-size:14pt;}
h3      { font-size:12pt; font-weight:bold; }
h4      { font-size:10pt; font-weight:bold; font-style:italic; margin-bottom:4pt}
img   { border: none; }
img.middle { vertical-align:middle; }

.grey  { color: gray; }
.warn  { color: rgb(255, 0, 0); }
.error { color: rgb(180, 0, 0); font-weight:bold; }
.inactive { color: rgb(160, 160, 160); }
.nowrap { white-space: nowrap; }
.small { font-size: xx-small; }

hr		{ color: #804000; }
table.box { border-width:1px; border-style: solid; border-color:#000000; background-color:#F1EBDD; vertical-align: middle; }
table.box tr td { white-space:nowrap; }
input, select { font-size: 8pt; }
input.attack { background-color: rgb(250, 210, 210) }
input.support { background-color: rgb(210, 250, 210) }

input.check { margin:0px; margin-right:5px; }

table.quote td.quote_author { font-weight: bold; font-size: 8pt; }
table.quote td.quote_message { background-color:#FFFFFF; font-size: 8pt; }

.igmline { border: 1px solid #804000; background-color: #F3EBCF;  padding: 1px; display:block;}


input[type=submit] {
  background-color: #E8F4F8;
  border: 2px solid #004080;
  font-weight: bold;
  color: #002040;
  -moz-border-radius: 15px;
}
input[type=submit].attack {
  background-color: #F8E8E8;
  border: 2px solid #800000;
  font-weight: bold;
  color: #400000;
}
input[type=submit].support {
  background-color: #E8F8F4;
  border: 2px solid #008040;
  font-weight: bold;
  color: #004020;
}
input[type=submit]:hover {
  border-color: #0082BE;
}
input[type=text] {
  border: 1px solid #F0C0A0;
  color: #402000;
  padding-left: 1px;
  padding-right: 1px;
  -moz-border-radius: 10px;
}
input[type=text]:focus {
  background-color: #D8E4E8;
  color: #802000;
}
}
