css:
@-moz-document domain("bilibili.com") {
  :root {
  --bg1:#25272a;
  --bg2:#3f3f3f;
  --bg3:#414141;
  --bg1_float:#25272a;
  --bg2_float:#414141;
  --text1:#e2e2e2;
  --text2:#cacaca;
  --text3:#bababa;
  --text4:#4e4e4e;
  --line_light:#414141;
  --line_regular:#484848;
  --line_bold:#4e4e4e;
  --graph_white:#25272a;
  --graph_bg_thin:#3f3f3f;
  --graph_bg_regular:#414141;
  --graph_bg_thick:#484848;
  --graph_weak:#4e4e4e;
  --graph_medium:#bababa;
  --graph_icon:#cacaca;
  }
  .eva-banner,
  .eva-extension-area,
  .ad-report,
  .slide-ad-exp {
    position: absolute;
    overflow: hidden !important;
    white-space: nowrap !important;
    margin: 0 !important;
    padding: 0 !important;
    height: 1px !important;
    width: 1px !important;
    clip: rect(0 0 0 0) !important;
    clip-path: inset(100%) !important;
  }
  ::-webkit-scrollbar {
    max-width: 6px;
    max-height: 12px;
  }
  ::-webkit-scrollbar-thumb {
    border-radius: 6px;
    background-color: var(--text3);
  }
}
