@-moz-document domain(gab.com) {
  :root[theme="muted"] {
      --solid_color_primary: #161922 !important;
      --solid_color_primary-opaque: rgba(34,34,34,0.6) !important;
      --solid_color_secondary: #262b3b !important;
      --solid_color_secondary-dark: #262b3b !important;
      --solid_color_tertiary: #12151c !important;
      --solid_color_block: #262b3b !important;
      --text_color_primary: #fff !important;
      --text_color_secondary: #999 !important;
      --text_color_alt: var(--text_color_primary);
      --border_color_secondary: hsl(225, 26%, 20%) !important;
      --color_input_placeholder: var(--text_color_secondary);
      --navigation_search: #262b3b !important;
  }
  :root[theme="muted"] {
      --solid_color_toast: #202327 !important;
      --text_color_tertiary: #606878 !important;
      --navigation_background: #161922 !important;
      --navigation_blend: var(--text_color_secondary) !important;
      --navigation_blend--light: var(--solid_color_block);
      --navigation_primary: var(--text_color_primary) !important;
      --navigation_brand: var(--color_brand) !important;
  }
  ::selection {
      background: #7f0608;
  }
