css:
@-moz-document domain("habr.com") {
html, body, .tm-layout__wrapper,
.tm-navigation-search__input .tm-input-text-decorated__input,
.tm-comment-form__editor .editor,
.article-formatted-body pre,
.layout,
.tm-editor__textarea,
.layout__row_footer-links,
.tm-article-presenter__origin,
.article-formatted-body code,
.tm-comment__body-content pre,
.context-menu__item,
.context-menu{
	background:#fdf6e3;
    background-color:#fdf6e3;
}

.tm-articles-list__item,.tm-page__top,
.tm-block, .tm-article-presenter__body, 
.tm-article-comments__article-snippet, 
.tm-comments-wrapper__wrapper, 
.tm-project-block,
.tm-hubs-list:not(:empty),
.tm-users-list, .tm-users-list__sorting-panel,
.tm-companies, .tm-companies__placeholder,
.tm-companies__sorting-panel,
.tm-navigation-sorting,
.tm-section-name,
.tm-tabs,
.tm-pagination,
.tm-profile__body,
.tm-user-card,
.tm-user-comments__comment,
.tm-dropdown__body,
.tm-comment-form,
.tm-comment-navigation__block,
.tm-article-sticky-panel__icons,
.tm-editor__wrapper,
.tm-editor__bottom,
.tm-navigation-panel, .tm-suggest-button,
.tm-hub-card,
.tm-feed-settings-block,
.tm-feed-settings-block__title,
.tm-my-feed-page__settings-button,
.default-block,
.author-panel__user-info,
.tm-editor_comments,
.megapost-teasers,
.layout__row_navbar,
.promo-block,
#tracker-page .tracker-table__row.new,
.comment__message_v2 pre code,
.post__translatation,
.post__text-html pre code,
.loads,
.tm-salary-distribution

{
	background:#eee8d5;
    background-color:#eee8d5;
}

/*Menu*/
.tm-header,.tm-base-layout__header, 
#TMpanel .logo-wrapper .logo,
#TMpanel,
#TMpanel .dropdown{
background-color:#eee8d5;

}

#TMpanel .projects-dropdown{
	border-left:1px solid #0085a1;
}

.tm-main-menu__item,.tm-our-projects__description,
#TMpanel .logo-wrapper .logo,
.nav-links__item-link,
#TMpanel .container .bmenu > a,
#TMpanel .projects-dropdown
{
color:#0085a1;
}


.layout__elevator{
background:#fdf6e3;
background-color:#fdf6e3;
}

.layout__elevator:hover{
color:#0085a1;
background:#eee8d5;
background-color:#eee8d5;
}

.tm-our-projects__item:hover,#TMpanel .current, #TMpanel .service:hover {
  background:#fdf6e3;
  opacity:1
 }

.tm-header__icon{
color:#2aa198;
}

.tm-header__become-author-btn{
color:#6c71c4;
border:#6c71c4 1px solid;
}


/*Header*/

.tm-company-card {
	background-color:#eee8d5;
}

/*tabs*/
.tm-navigation-filters,.tm-navigation-filters__options{
background-color:#eee8d5;
}


/*ico rss*/
.tm-rss-button_default{
background-color:#6c71c4;
color:#5096b1;
}

/*Paganation*/
.tm-pagination__navigation-link:first-child::after, .tm-pagination__navigation-link:last-child::before{
background: none;
}


.tm-tabs::before,
.tm-tabs::after{
	background:none;
    background-image:none;
}

.tm-hubs-list__sorting-panel{
	background-color:#eee8d5;
}

.tm-project-block_variant-vacancies
{
	border-left-color:#d33682;
}

.tm-user-snippet__nickname{
	color:#d33682;
}

.tm-notice{
	background-color:#fdf6e3;
}

.tm-contribution-list__contribution{
	color:#d33682;
}

.tm-notice_positive::before {
	background:#d33682;
}

.tm-article-title__link, .tm-article-title__text{
	color: #dc322f;
}
.tm-contribution-list__progress-bar{
	background-color:#dc322f;
}
.tm-contribution-list__progress{
	background-color:#2aa198;
}

.tm-editoral-subscription {
background-color:#eee8d5;
}

.tm-input-text-decorated__input{
background-color:#fdf6e3;
}
a:hover{
	color:#586e75;
}



.tm-article-title__link:visited, .tm-article-title__text:visited{
	color:#268bd2;
}

.tm-company-basic-info__link:hover,.tm-widget-links__link:hover{
	color:  #268bd2; 
}


.tm-administration-block__link, .tm-company-basic-info__link,.tm-widget-links__link{
	color:  #dc322f; 
}

.article-formatted-body a{
	color:#d33682;
}

/*icon*/
.tm-icon-counter__icon, .tm-article-comments-counter-link__icon{
	color:#2aa198;
}


.tm-icon-counter__value{
	color:#cb4b16;
}

.tm-article-comments-counter-link__unread-counter, .tm-article-comments-counter-link__value{
	color: #6c71c4;
}

/*content*/

.tm-page-progress-bar{
	background:#333;
}


.article-formatted-body_version-2 details{
	background:#fdf6e3;
}

/*AD*/
.roxotAdContent,.tm-adfox-banner__container{
display:none;
}


/*sidebar*/

.tm-sexy-sidebar{
padding:0!important;
}

/*Foter*/
.tm-footer-menu{
	background-color:#fdf6e3;
}


.tm-footer-menu__block-title{
	color:#002b36;

}

.tm-footer-menu__list-item a{
	color:#073642;
}
}
