css:
@-moz-document url-prefix("https://www3.animeflv.net/") {
body {
    background: url(https://puu.sh/IBDDW.jpg) #fff;
    color: #838383;
}

.Title.Page h1, .Title.Page h2, .Title.Page h3, .Title.Page h4, .Title.Page h5, .Title.Page h6 {
    color: #c6ddf1;
}

.Anime.alt>a>.Title {
    color: #ddd;
}

.Title .Order>a:hover, .Title .Order>a.Active {
    background-color: #a54dcd;
}

.Title .Order>a.Active:before {
    border-top: 5px solid #a54dcd;
}
*, :b

.Anime.alt .Image {
    background-color: #1e1e1e;
}

.Anime.alt .Image {
    background-color: #222;
}

.Wdgt {
    background-color: #1e1e1e;
    border-radius: 10px;
    padding: 10px;
}

.Wdgt>.Top {
    color: #ff4949;
}

a, .Title {
    color: #f7f7f7;
}

.Header>.Mid .Menu>li.Current>a:before {
    border-top: 5px solid #a54dcd;
}

.btn-default {
    color: #fff;
    background-color: #1c1c1c;
    border-color: #333;
}

.NvCnAnm .pagination span, .NvCnAnm .pagination a {
    background-color: #1c1c1c;
    color: #fff;
}

.CpCnA .CapiTop .Title {
    color: #f7f7f7;
}

.CpCnA .CapiTop .SubTitle {
    color: #9d9d9d;
}

.WdgtCn {
    background-color: #1c1c1c;
}

.BtnNw {
    background-color: #2e3437;
}

.BtnNw {
    background-color: #2e3437;
    color: #f1f1f1;
}

.ShrCnB .Title {
    color: #f7f7f7;
}

.BtnNw.CVst, .BtnNw.Clgt, .BtnNw.Xpnd {
    color: #b83341;
}

.BtnNw.Dwnd {
    background-color: #a744a1;
}

.Nvgnrs a, .CatLnk {
    background-color: rgba(1,188,242,.2);
}

.NwBxCn figure span {
    background-color: #3f409d;
    color: #fff;
}

.textarea-outer-wrapper .textarea-wrapper {
    background: #262626 !important;
    border: 2px solid #1a1a1a !important;
}

.textarea {
    color: #89bed3 !important;
}

section#conversation {
    visibility: hidden;
}

.Wdgt>.Top {
    border-bottom: 1px solid #ff5353;
}

/* VERSIÓN 1.1 */

.ListAnmRel>li>a {
    color: #fff;
}

.ListCaps>li .Stts {
    color: rgba(255, 255, 255, 0.5);
}

.AnimeCover {
    background-color: rgba(0,0,0,0);
    filter: drop-shadow(0px 0px 8px rgba(0,0,0,0.6));
}

.DpdwCnt.TtCn {
    background-color: #2c2c2c;
}

.Anime.alt>.Title>a {
    color: #fff;
}

.Anime.alt>.Title>a:hover {
    color: #01bcf3;
}

div.Logo a img {
    content: url("https://puu.sh/IBXOA.png")
}

/* 	VERSIÓN 1.2 */

aside.Sidebar a img {
    border-radius: 8px;
}

.WdgtCn.Sm .Top {
    border-bottom: 1px solid #333;
}

.ListAnmRel {
    border-top: 1px solid #333;
}

.ListCaps>li {
    border-bottom: 1px solid #333;
}

.form-control {
    background-color: #1f1f1f;
    border: 1px solid #333;
}

.ListCaps>li .Stts:after {
    background-color: rgba(157, 157, 157, 0.2);
}

li.fa-play-circle:hover {
    color: white !important;
}

.dropdown-menu {
    background-color: #333;
}

.dropdown-menu>li>a {
    color: #eee;
}

.filters .multiselect-container:before {
    border-bottom: 5px solid #333;
}

.CpCnC a img{
    border-radius: 8px;
}

/* 	VERSIÓN 1.3 */

.FollowUs div {
	display: none !important;
}

.ListCaps>li>a, .panel-title {
    color: #777;
}

.ListCaps>li>a:hover {
    color: white;
}

.ListResult>li {
    border-bottom: 1px solid #222;
}

.ListUser>li:last-child {
    border-top: 1px solid #333;
}

.panel, .panel-default>.panel-heading {
    background-color: #1b1b1b !important;
    border-color: #1a1a1a !important;
}

.panel-default {
    border-color: #1a1a1a;
}

.list-group-item {
    background-color: #222;
    border: #555 1px solid;
}

.list-group-item:hover {
    background-color: #333 !important;
    border: #555 1px solid;
}
}

@-moz-document url-prefix("https://www3.animeflv.net/") {
::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: #333; 
}
 
::-webkit-scrollbar-thumb {
  background: #555;
  border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
  background: #7c5190; 
}

div.WdgtCn, #disqus_thread {
    display: none;
}
}
