css:
@-moz-document url("https://fantasy.premierleague.com/transfers") {
:root {
  --lightpurple: #7e008a;
  --purple: #4E00A8;
  --darkpurple: #37003C;
  --darkmodegray: #181818;
  --infogray: #DAD7D2;
  --disabledgray: #282C2D;
}

td {
  border: none !important;
}

th {
  border: none !important;
}

/* User-Agent Style */
html {
  background-color: #181a1b;
}
html,
body,
input,
textarea,
select,
button {
  background-color: #181a1b;
}
html,
body,
input,
textarea,
select,
button {
  border-color: #736b5e;
  color: #e8e6e3;
}
a {
  color: #3391ff;
}
table {
  border-color: #545b5e;
}
::placeholder {
  color: #b2aba1;
}
input:-webkit-autofill,
textarea:-webkit-autofill,
select:-webkit-autofill {
  background-color: #555b00;
  color: #e8e6e3;
}
::-webkit-scrollbar {
  background-color: #202324;
  color: #aba499;
}
::-webkit-scrollbar-thumb {
  background-color: #454a4d;
}
::-webkit-scrollbar-thumb:hover {
  background-color: #575e62;
}
::-webkit-scrollbar-thumb:active {
  background-color: #484e51;
}
::-webkit-scrollbar-corner {
  background-color: #181a1b;
}
::selection {
  background-color: #004daa;
  color: #e8e6e3;
}
::-moz-selection {
  background-color: #004daa;
  color: #e8e6e3;
}

/* Modified CSS */
a {
  background-color: transparent;
}
a:active,
a:hover {
  outline-color: initial;
}
abbr[title] {
  border-bottom-color: initial;
}
mark {
  background-image: initial;
  background-color: rgb(204, 204, 0);
  color: rgb(232, 230, 227);
}
img {
  border-color: initial;
}
button,
input,
optgroup,
select,
textarea {
  color: inherit;
}
fieldset {
  border-color: rgb(66, 71, 74);
}
legend {
  border-color: initial;
}
p {
  color: rgb(209, 205, 199);
}
p a {
  text-decoration-color: initial;
  color: rgb(255, 41, 116);
}
p a:hover {
  text-decoration-color: initial;
}
#mainContent:focus {
  outline-color: initial;
}
abbr[title] {
  border-color: initial;
}
::selection {
