@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(netlog.com) {

    /* PRIMARY MENU */
    ul#menuPrimary li a { display: block !important; text-shadow: -1px -1px 0 black !important }
    ul#menuPrimary li a:focus { color: white !important; background: #444 !important }

    ul#menuPrimary li.first strong,
    ul#menuPrimary li.first a,
    ul#menuPrimary li.selected strong,
    ul#menuPrimary li.selected a { color: grey !important; text-shadow: -1px -1px 0 black !important; background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEQAAAAiCAIAAABp3OiiAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH2QkJCAkZMkeIjQAAAHtJREFUeNrt19sNgDAMQ9GAOgajsisr8FdIH2ELFN06GxzFddTtOC+jTPG7cTCzdg6mV20mKWYMDiacFDMSxiJAm2mOwewGmmKkNrPZFTNhhFm2zUIFIMwPR/MhHc2mmCWN2avNJH0zlXQ0Sd9mVDWHgzA21WbCCLMo5gOsoTAWvHvlgwAAAABJRU5ErkJggg==") repeat-x !important }
   
    ul#menuPrimary li.first a:hover,
    ul#menuPrimary li.first a:focus,
    ul#menuPrimary li.selected a:hover,
    ul#menuPrimary li.selected a:focus { text-decoration: underline !important }

    ul#menuPrimary li.first strong:active,
    ul#menuPrimary li.first a:active,
    ul#menuPrimary li.selected strong:active,
    ul#menuPrimary li.selected a:active { background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAiCAIAAAARTyRGAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH2QkJCCouNFNa4wAAAEhJREFUeNpljLENgEAMA51LnCcdW7Akw1O8aKCwLJ1P1rpuJH0CikZ5oDBiNs+1G+/t9aP+HxTKRBK4UQS0iTZkwhQ1hin69AOMwwG6xwiM0gAAAABJRU5ErkJggg==") repeat-x !important }
    

    ul#menuPrimary li.first strong,
    ul#menuPrimary li.first a { -moz-border-radius-topleft: 10px !important }

    ul#menuPrimary li.last strong,
    ul#menuPrimary li.last a { -moz-border-radius-topright: 10px !important }
    
    /* HEADER BORDER BOTTOM */
    div#headerWrapper{ border-bottom-color: #0741BF !important }

    /* SECONDARY MENU */
    ul#menuSecondary li.selected a { color: #0941BF !important }
    ul#menuSecondary li.selected a strong { font-weight: bold !important }

}

@-moz-document url-prefix("http://de.netlog.com/go/") {

    /* CONTENT */
    div.content h1 a,
    div.content h2,
    div.content h2 a,
    ul#menuSecondary li.selected a strong,
    div.content strong { color: #0941BF !important }
    ul#menuSecondary li strong { color: black !important }

    div.content h1 a:hover,
    div.content h1 a:focus,
    div.content h2 a:hover,
    div.content h2 a:focus { text-decoration: underline !important }

    span.counter a:link,
    span.counter a:visited,
    span.counter a:hover,
    span.counter a:focus,
    span.counter a:active { color: white !important }

    div.contentSideBox { background: #F7F2F2 !important }

}

#hud .hudButton
{
background:none repeat-x scroll left top ;
-moz-box-shadow: 10px -10px 60px rgb(255,0,0)
}
