css:
@-moz-document domain("drednot.io") {
.tip {
	position:absolute;
	bottom:42px;
	left:50%;
	transform:translateX(-50%) !important;
	font-size:10pt;
	margin-bottom:4px;
	pointer-events:none;
	visibility:hidden;
	width:200px;
	z-index:2;
	backdrop-filter: none!important;
}
.btn, button {
	border:2px solid #000!important;;
	border-radius:5px;
	background:#666!important;;
	font-size:12pt;
	padding:5px;
	margin:5px;
	color:#fff!important;
	text-decoration:none;
	cursor:pointer;
	white-space:nowrap;
	vertical-align:middle;
	user-select:none
}
.btn-small {
	font-size:10pt;
	padding:3px;
	margin:3px
}
.btn-large {
	font-size:20pt;
	padding:10px;
	margin:10px
}
.btn-meme {
	font-size:30pt;
	background:#a4a!important
}
.btn-red {
	background:#c80000 !important;
}
.btn-orange {
	background:#db7d00 !important;
}
.btn-yellow {
	background:#b9a500 !important;
}
.btn-green {
	background:#00a200 !important;
}
.btn-blue {
	background:#44a8a8 !important;
}
.darker {
	color:#fff!important;
	background: rgba(0,0,0,0.8) !important;
	border-radius:0!important;
	border:2px solid #000!important;
	backdrop-filter: blur(5px);
}
.dark {
	color:#fff!important;
	background:rgba(0, 0, 0, .7) !important;
	border-radius:5px !important;
	border:2px solid #000!important;
	backdrop-filter: blur(5px);;
}
.item-ui-item {
	position:relative;
	width:32px;
	height:32px;
	border:2px solid black;
	display:inline-block;
	background-size:cover;
	vertical-align:middle;
	pointer-events:auto;
	background-color:#aaa;
	transition:width .1s, height .1s, border .1s, background .1s, margin .1s;
	cursor:pointer;
	margin:6px 0;
	border-radius: 5px;
}
.item-ui-item.active {
	width:40px;
	height:40px;
	border:3px solid black;
	background-color:#53f953;
	border-radius:8px;
	margin:0
}
.item-ui-item.hover {
	border: 3px solid black;
	width: 40px;
	height: 40px;
	background-color: #ff8;
	border-radius: 8px;
}
#motd {
	color:#fff;
	background:rgba(0, 0, 0, .5);
	min-width:220px;
	border-radius:0 0 0 8px;
	pointer-events:auto;
	user-select:text;
	padding:10px;
	position:absolute;
	top:0;
	right:0;
	z-index:5
}
.item-picker {
	overflow-y:auto;
	display:inline-block;
	position:absolute;
	right: 0px;
	top: 50px;
	font-size:14pt;
	height: calc(100% - 60px);
}
}
