css:
@-moz-document domain("htmlacademy.ru") {
  
	.page-footer  {
		display: none;
	}	
 
	.course-extra  {
  		display: none;
	}  
  
  	.course-editor__label  {
  		display: none;
	 }
  
  	.course-container  {
  		max-height: 100%;
	}
  
  	.course-container__inner  {
  		max-width: 100%;
	}
	
	.course-theory__inner  {
  		min-width: 50vw;
	}

	.course-layout__sidebar {
		width: 0;
	}
  
  .course-interface--dark .course-theory__content-text {
  	color: rgba(182,189,115);
  }
  
  .course-interface--dark .course-theory__content-text  code {
    color: #ffab00;
  }

  
}
