css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kingdomofloathing.com"),
url(http://store.asymmetric.net/), url-prefix(http://127.0.0.1:60080) {

body { background-color: rgb(252,254,255) !important;} 

span, .small, .tinybutton {
  border: 0 none !important; 
  box-shadow: none !important;
  border-radius: inherit !important;
}
.tinybutton, .tinytext {
  -moz-appearance: button !important;
  font-size: 10px !important;
  border-radius: 3px !important;
}

/* Control elements */

input, option, textarea { 
  margin: 0px 1px 0px 1px !important; 
  -moz-outline-radius: 4px !important;}
textarea {padding: 2px !important;}

select { margin: 2px !important;}
select, input.button {
  display: inline !important;
  font-family: Corbel, "Segoe UI", "Trebuchet MS", Arial, Helvetica, sans-serif !important; 
  font-size: 9pt !important;
  font-weight: normal !important; 
  padding: 0px !important;
  margin-top: -1px !important;
  margin-bottom: -2px !important;
  max-height: 22px !important;
}

/* (I have removed all body-text shadowing; it made the text hard to read.) */

input[type*="checkbox"] {
  -moz-appearance: checkbox !important;
  margin: 2px !important; 
}
[class*="button"], input.text, #qtyentry {
  color: midnightblue !important;
  border-radius: 2px/4px !important;
  box-shadow: 1px 1px 4px -1px #244 !important;
}
[class*="button"]:hover, input[type*="text"]:hover, #qtyentry:hover {
  color: #004D88 !important;
  box-shadow: 2px 2px 4px -2px #011 !important;
}

/* --- Round border corners --- */

td[style*="border: 1px solid"], .tborder, #recipeContent {
  border: 2px solid !important;
  -moz-border-top-colors: rgb(65,65,152) darkblue !important;
  -moz-border-bottom-colors: rgba(0,0,0,0.8) rgb(46,116,177) !important;
  -moz-border-left-colors: rgba(0,0,0,0.8) rgb(46,116,177) !important;
  -moz-border-right-colors: rgba(0,0,0,0.8) rgb(46,116,177) !important;
  border-radius: 8px/12px !important;
}
[style*="border: 1px solid"], td[bgcolor*="e"] {
  border-bottom: 0.15em solid #333 !important; 
  border-radius: 8px/16px !important;
}
#InputForm, textarea, #recipeHeader { border-radius: 6px/12px !important;}



/* --- Blue headers --- */

td[bgcolor="blue"][width="50"] + td, 
td[bgcolor="blue"][width="50"]:first-child {
  border-right: none !important; border-radius: 0px !important;}
td[bgcolor="blue"][width="50"] + td, 
td[bgcolor="blue"][width="50"]:last-child {
  border-left: none !important; border-radius: 0px !important;}

td[bgcolor="blue"][width="50"]:first-child { 
  border-radius: 8px 0px 0px 6px !important;}
td[bgcolor="blue"][width="50"]:last-child {
  border-radius: 0px 8px 6px 0px !important;}
td[bgcolor*="e"] { border-bottom: none !important;}

td[bgcolor="blue"] *, td[bgcolor="orange"] * {
  background: transparent !important;
  background-repeat: repeat-x !important; }


/* --- Image styles --- */

img[src*="http://images.kingdomofloathing.com/itemimages/"],
img[class*="hand"],

/* this next section excludes image-maps that shouldn't have anything between images */
img[src^="http://images.kingdomofloathing.com/otherimages/"]:not([
 src^="http://images.kingdomofloathing.com/otherimages/main/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/guilds/"]):not([     /* Town */
 src^="http://images.kingdomofloathing.com/otherimages/clanhall/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/slimetube/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/town/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/town2/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/canadia/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/museum/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/manor/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/woods/"]):not([      /* Woods */
 src^="http://images.kingdomofloathing.com/otherimages/campground/"]):not([ /* Campground */
 src^="http://images.kingdomofloathing.com/otherimages/plains/"]):not([     /* Plains */
 src^="http://images.kingdomofloathing.com/otherimages/cyrpt/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/knob/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/knob2/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/knoll/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/bathole/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/hiddencity/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/stalktop/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/mountains/"]):not([  /* Mountains */
 src^="http://images.kingdomofloathing.com/otherimages/mclargehuge"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/cave/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/mine/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/beach/"]):not([      /* Beach */
 src^="http://images.kingdomofloathing.com/otherimages/pyramid/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/ocean/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/bigisland/"]):not([  /* Island */
 src^="http://images.kingdomofloathing.com/otherimages/island/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/cove/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/lair/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/valhalla/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/heart/"]):not([
 src^="http://images.kingdomofloathing.com/otherimages/letters/"]):not([
 src*="http://images.kingdomofloathing.com/otherimages/papersword_"]):not([
 src="http://images.kingdomofloathing.com/otherimages/scorch.gif"]),
img[src^="http://images.kingdomofloathing.com/adventureimages/"]
{
  opacity: 1 !important;
  border: 1px solid transparent !important;
  border-radius: 3px !important;
  box-shadow: 1px 1px 5px -2px #244 !important;
}
* a, a[target="mainpane"] { border: none !important;}

/* shadow underneath the island map */
img[src="http://images.kingdomofloathing.com/otherimages/bigisland/bottom.gif"] {
  box-shadow: 0px 4px 5px -4px #244 !important;}

/* less shadowing around monster pics */
#monpic { box-shadow: 0px 0px 2px #244 !important;
  margin: -4px 10px -1.15em 0px !important; padding: 1px!important; } 
 
/* snipped out the item style - see style 29012 */

/* Combat Action Bar */

#items, #skills,
#button1, #button2, #button3, #button4, 
#button5, #button6, #button7, #button8, 
#button9, #button10, #button11, #button12 { 
  border: 1px dotted #888 !important; 
  margin: -2px 1px -2px 1px !important; 
}
.spacer, td.page { background: transparent !important;}

img.disabled, img[title="Action not usable"] { 
  opacity: 0.3 !important;}
img.disabled:hover, img[title="Action not usable"]:hover {
  opacity: 0.8 !important;}

/* Header style / background image */

td[bgcolor="blue"], td[bgcolor="orange"], p.rcm:hover, #recipeHeader, #jumptobot, #fortuneCookieCounter, a[target="mainpane"] {
  background-image: url("data:image/png;base64,") !important;
 background-repeat: repeat-x !important;}


/* --- Chat pane --- */

#ChatWindow {
  background: rgb(252,253,255) !important; 
  font-size: 9pt !important; 
/* serif fonts *//* (disabled)
  font-family: constantia, century, times, serif !important; */
/* sans-serif fonts */
  font-family: "segoe ui", corbel, sans, sans-serif !important;
}
#InputForm {
  background: rgb(246,246,246) !important;
  border-top: 1px dotted rgb(24,24,26) !important;
  border-radius: 0px 0px 12px 12px !important;
}

/* (Jump to final round) link */

#jumptobot {
  font-size: 9pt !important; 
  position: relative !important; float: right !important; 
  left: -33px !important; top: 65px !important;
  background: rgba(122,122,122,.5) !important;
  border: thin solid darkblue !important; 
  text-decoration: none !important; 
  box-shadow: 1px 2px 4px black !important; 
}
#jumptobot:hover, #jumptobot:active{
  text-decoration: underline!important;}
#jumptobot:before { content:'â†“  ' !important;}

/* fixes the main pane's scrollbar mess during a fight. */
div[id="content_"] > div[style="overflow: auto;"] { 
  overflow: visible !important;}

/* experimental -- intended to add a background/border to certain content */
td > center > table > tbody > tr > td > center:not([style]) > td, 
table[style]:not([title]):not([class]):not([cellpadding="0"]):not([cellspacing="0"]) { 
  background-color: white !important; 
  border: thin solid ThreeDshadow !important;
  border-bottom: none !important;
  padding: 1px !important;
  margin-bottom: -5px !important;
  margin-top: -1px !important;
  box-shadow: 1px 2px 4px -2px #011 !important; 
  border-radius: 8px/12px !important;
}
/* without more unique table-cell IDs in the source code to begin with, 
 * it's really hard to specify specific table cells and not others. */

/* Red rollover message - "X minutes until daily maintenance begins." */ 

#rollover {
  background: white !important; 
  position: relative !important; top: -8px !important;
  width: auto !important; 
  border: thin solid rgb(24,24,26)!important;
  border-top: none !important;
  padding-bottom: 2px !important;
  margin-bottom: -4px !important; 
  border-radius: 0px 0px 12px 12px !important;
}



/* end of CSS */
}
