css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("toplessrobot.com") {

/* Hides the ad at the top of the page (killing it will mess up some things' positions) */
 div#Top {
    visibility: hidden !important;
    }

/* Kill these */
 div#Middle, div.whiteline, div#Footer, div.spacer {
    display: none !important;
    }

/* The black background surrounding the content, with added gradient */
 body {
    background: url("http://img121.imageshack.us/img121/1667/testef.png") repeat-x !important;
    background-color: #101010 !important;
    }
 #wrapper {
    background-color: transparent !important;
    }

/* Logo (still working on it) */
 #logo a img {
    visibility: hidden !important;
    display: block;
    }
 #logo {
    background-image: url("http://i.imgur.com/2PiwIgo.png") !important;
    }

/* Robot picture at the top (this will change soon) */
 #header {
    background-image: url("http://img233.imageshack.us/img233/3433/headerbackground2.png") !important;
    background-color: transparent !important;
    left: 250px !important;
    }

/* Most borders become grey */
 div {
    border-color: #666 !important;
    }

/* Changes the attributes of the blue top bar */
 div#wrapper2 {
    border-color: #222 !important;
    }

/* Changes the attributes of the featured articles wrapper (below the blue top bar) */
 div#wrapper3 {
    background-color: #444 !important;
    border-color: #222 !important;
    color: #ccc !important;
    }

/* Changes the attributes of the main content window */
 div#wrapper4 {
    background-color: #333 !important;
    border-color: #222 !important;
    color: #ccc !important;
    }

/* Links will behave in this manner */
 a:link, div#nav ul li a:link, div#nav ul li a:visited {
    color: white !important;
    }
 a:visited {
    color: #fdc425 !important;
    }
 a:hover, div#nav ul li a:hover {
    color: #d3b050 !important;
    }

/* Tweetblock */
 .Tweet_Meme {
    background-color: transparent !important;
    }

/* Quoted articles */
 blockquote {
    border-color: #111 !important;
    background-color: #222 !important;
    color: #DCDC98 !important;
    }

/* Changes attributes of "# comments" thing under articles */
 div.Entry_Footer {
    background-color: #222 !important;
    border-color: #111 !important;
    }

/* Zebra comments background color */
 div.fyre-comment-stream {
    background-color: #151515 !important;
    color: #bbb !important;
    }
 article.fyre-comment-article:nth-child(even) {
    background-color: #050505 !important;
    color: #bbb !important;
    }

/* Changes the color of the date posted text in the comments */
 p.Post_Date {
    color: #D5D500 !important;
    }

/* Sidebar */
 div#Sidebar {
    background-color: #555 !important;
    color: #bbb !important;
    }

/* Header of the sidebar changed */
 p.Tagline {
    color: white !important;
    }
 p.Editor_Credit {
    color: #bbb !important;
    }

/* Changes the text in the reply button to black, as well as the digg button text in the sidebar */
 div.Comment_Footer p a, a.digg-count, a.digg-count span, span.fb_share_count_inner, span.db-count {
    color: black !important;
    }

/* Removes that white picture in the widgets of the sidebar */
 #Sidebar .widget {
    background-image: url(http://img248.imageshack.us/img248/3069/gradientm.jpg) !important;
    }

/* Widgets behave like this */
 div.widget, div#Most_Popular, div#Most_Wrapper {
    border-color: black !important;
    background-color: #333 !important;
    color: #bbb !important;
    }

/* Widget headers behave like this */
 div.widget h4, div#Most_Popular h4 {
    color: #ddd !important;
    }

/* Changes the attributes of the RSS page */
 div#feedBody {
    background-color: #333 !important;
    border-color: #222 !important;
    color: #ccc !important;
    }

/* span and RSS date colors change */
 span, div#TixyyLink, div.lastUpdated {
    color: yellow !important;
    }
 div#wrapper {
     width: 1500px !important;
 }
 div#content {
     width: 1100px !important;
 }
 div.fyre {
     width: 900px !important;
 }
}
