@-moz-document domain("www.20minutos.es") {

    html, body {
        background-color: #000000 ! important; 
        background-image: url('data:image/png;base64,') ! important; 
/*        background-attachment: fixed ! important;*/
        background-repeat: repeat-x ! important; 
        color: #ffffff ! important;
}

    p, h1, h2, h3, h4 {
        background-color:transparent ! important; 
        color:#CCCCCC ! important;
    }
    th {
        background-color: #151026 ! important; 
        background-image: none ! important;
        color: #cccccc ! important;
    }
    A:link, a, a.ab, .q {
        color: #00ff00 ! important; 
        text-decoration: none ! important;
    }
    A:visited {
        color: #aaaa88 ! important;
    }
    A:active {
        color:red ! important; 
        font-style: italic ! important;
    }
    A:hover {
        color: #00ff00 ! important;
        text-decoration: underline ! important;
    }
    .contComentario, .num_comentarios, .nav_comments a, .votos {
        background: #46357C ! important;
        color: #e5e5e5 ! important;
        border: 0px ! important;
    }
    .comment, .comment_off, .pag_comentarios {
        background: #65538B ! important;
        border: 0px ! important;
    }
    .by, .nav_comments {
        background: #6F5C9A ! important;
        color: #e5e5e5 ! important;
        border: 0px ! important;
    }
    .footer-pic {
        color: #e5e5e5 ! important;
    }
    .contenido_noticia {
        width: 100% ! important;
        font-size: 150% ! important;
        text-align: justify;
        clear: both;
    }
    .fechaHora {
        color: #e5e5e5 ! important;
    }
    .img, .pic {
        clear: both ! important;
        width: 100% ! important;
        text-align: left;
    }

