css:
@-moz-document url-prefix("https://uiaservice.yeepay.com"), url-prefix("http://qafmc.yeepay.com"), url-prefix("http://ycetest.yeepay.com:30422/yuia-service-boss/login.html"), url-prefix("https://qauiaservice.yeepay.com")
{
  body {
    background: url("https://source.unsplash.com/random/1920x1080") !important;
    background-size: cover !important;
  }

  #app {
    height: 100vh;
  }

  .login-header {
    display: none;
  }

  .login-content {
    background: none;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: none;
  }

  .login-content > div:nth-child(1) {
    display: none;
  }
  
  .login-content > div:nth-child(2) {
    flex: 0 !important;
  }

  .login-content-right {
    box-shadow: none;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 16px;
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
  }
  
  #iamloadingdiv {
    line-height: inherit !important;
  }

  .login-div {
    height: auto;
    border: none;
    box-shadow: none;
    margin: 0;
    background: none;
  }
  
  .login-div .loginbtnimg {
    margin: 0 !important;
  }
  
  .login-div-title {
  	justify-content: center;
    margin: 20px 0;
  }

  .login-div-title > div {
    display: none;
  }

  .login-div-title:after {
    content: '';
    font-size: 2rem;
    text-align: center;
    color: #fff;
    font-family: 'Niconne', cursive;
    font-weight: 700;
    text-shadow: 1px 1px 0px #eb452b, 2px 2px 0px #efa032, 3px 3px 0px #46b59e, 4px 4px 0px #017e7f, 5px 5px 0px #052939, 6px 6px 0px #c11a2b, 7px 7px 0px #c11a2b, 8px 8px 0px #c11a2b, 9px 9px 0px #c11a2b;
  }
  
  br {
    display: none;
  }

  ul.el-menu-demo.el-menu--horizontal.el-menu {
    display: none;
  }
  
  #iamloginformtip {
    display: none !important;
  }

  .el-input-group__append {
    display: none;
  }

  .login-div-footer {
    display: none;
  }
  
  .login-footer {
    display: none;
  }

  .login-footer-navi {
    display: none;
  }

  input {
    background: black !important;
  }
}

@-moz-document url-prefix("https://uiaservice.yeepay.com") 
{
  .login-div-title:after {
    content: 'PROD Environment';
  }
}

@-moz-document url-prefix("http://qafmc.yeepay.com"), url-prefix("http://ycetest.yeepay.com:30422/yuia-service-boss/login.html"), url-prefix("https://qauiaservice.yeepay.com")
{
  .login-div-title:after {
    content: 'TEST Environment';
  }
}
