css:
@-moz-document domain("baron.msk.dlink.ru") {
/* Issues */

#history a, #history a:link, #history a:visited,
#update a, #update a:link, #update a:visited,
.details a, .details a:link, .details a:visited{
    color: #70A7CD;
}

div.issue {
    border-color: #504e4e;
    background-color: #373b41;
}

table.list tr.issue .assigned_to a {
	color: #0c53ff;
}
img.gravatar {
    background: #282a2e;
    border-color: #282a2e;
}
div.wiki pre {
    background-color: #282a2e;
    border-color: #373b41;
}
ul.tagit {
    background: #373b41;
}

div#history > div.journal {
    border-top: 1px solid #504e4e;
    background-color: rgb(45, 43, 43);
}

/* Issue Table */

table.list th {
    background-color: #373b41;
}
table.list thead th {
    border-top-color: #b4b7b4;
    border-right-color: #b4b7b4;
    border-left-color: #b4b7b4;
    border-bottom-color: #b4b7b4;
}
table tr.odd {
    background-color: #282a2e;
}
table tr.even,
table tr.even.priority-lowest,
tr.odd.priority-lowest {
    background-color: #373b41;
}
table tr.even.priority-lowest {
    color: #b294bb;
}
table tr.odd.priority-lowest {
    color: #b294bb;
}
table.list tbody tr:hover {
    color: #e0e0e0;
}
table.list tbody tr:hover a {
    color: #c5c8c6;
}
table.list tbody td,
table.list tbody tr:hover td {
    border: solid 1px #b4b7b4;
}



tr.odd.priority-lowest td,
tr.even.priority-lowest td,
tr.even.priority-lowest td,
tr.even.priority-lowest td {
    border-color: #b4b7b4;
}
select[multiple="multiple"] {
    background-color: #373b41 !important;
    color: #e0e0e0;
}
#ajax-modal p.buttons input:hover,
.ui-datepicker .ui-datepicker-buttonpane button.ui-state-hover,
input[type="submit"][name="continue"]:hover,
input[type="button"]:hover,
.details input[type="submit"]:hover,
input[type="submit"].small:hover,
input[type="submit"].button-small:hover {
    /* background: #1d1f21; */
    color: #e0e0e0;
}
#ajax-modal p.buttons input,
.ui-datepicker .ui-datepicker-buttonpane button,
.details input[type="submit"],
input[type="button"],
input[type="submit"],
input[type="submit"][name="continue"],
input[type="submit"].small,
input[type="submit"].button-small {
    background: #373b41 !important;
}
/* Body Text */

body {
    color: #e0e0e0;
}
/* Header */

input[type="text"],
input[type="password"],
fieldset#filters td.values input,
select {
    background-color: #373b41 !important;
    border: 1px solid #504e4e;
    color: #e0e0e0;
}
input[type="text"]:focus,
input[type="password"]:focus,
select:focus,
textarea:focus,
fieldset#filters td.values input:focus {
    border-color: #81a2be;
}
select {
    background: #2D2B2B;
    color: #e0e0e0;
    border: 1px solid #504e4e;
}
select option {
    background-color: #2D2B2B;
    color: #e0e0e0;
}
/* Main Background */

html {
    background-color: #2f2f2f;
}
#wrapper {
    background-color: #2f2f2f;
    background-image: none;
}
#content {
    background-color: #282a2e;
    border-right: none;
}
#content > h2 {
    background-image: none;
    color: #000;
}
#footer {
    background-color: #282a2e;
}
p.other-formats {
    color: #969896;
}
/* Main Header */

#header {
    background-color: #373b41;
    color: #c5c8c6;
}
#main-menu{
    background: #2F2F2F;
}
#main-menu li > a {
    color: #e0e0e0;
    background: none;
}
#main-menu li > a:hover {
    color: #e0e0e0;
    background: none;
    text-decoration: underline;
}
#main-menu li > a.new-object:hover{
    background-color: #e0e0e0;
    color: #000;
    text-decoration: none;
}
div#content{
    border: none;
}
div#content > h2 {
    background: #373b41;
    color: #ababab;
    border: none;
    margin: -6px -11px 10px;
}
/* Sidebar */

#sidebar {
    color: #e0e0e0;
    background-color: #373b41;
    border: none;
}
/* Calendar */

table.cal thead th,
table.cal td.week-number {
    background-color: #373b41;
}
table td.odd {
    background-color: #282a2e;
}
table td.even {
    background-color: #373b41;
    background: none;
}
div.tooltip:hover span.tip {
    background-color: #373b41;
    color: #e0e0e0;
}
table.cal td {
    border-color: #b4b7b4;
}
table.cal {
    border-color: #b4b7b4;
}
/* Wiki */

.wiki.wiki-page {
    color: #e0e0e0;
}
div.wiki table,
div.wiki td,
div.wiki th {
    border: 1px solid #4e4e4e;
}
div.wiki table {
	background-color: #191919;
    color: #4c8213;
}
div.wiki ul.toc a {
    color: #000000;
}
#content,
#main.nosidebar #content {
    background: #2d2b2b;
}
.jstElements button {
    background-color: #d8d8d8;
}
textarea.wiki-edit {
    background-color: #282a2e;
    border-color: #484c52;
    color: #9e9e9e;
}
code {
    color: #9e9e9e;
    /*font-weight: bold;*/
}
/* Editing */

.box {
    background: #373b41;
    color: #e0e0e0;
    border-color: #504e4e;
}
textarea {
    background: #1d1f21;
    color: #e0e0e0;
}
input[type="text"]:focus,
input[type="password"]:focus,
select:focus,
textarea:focus,
fieldset#filters td.values input:focus {
    border-color: #81a2be;
}
#ajax-modal p.buttons input[type="submit"],
.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close,
input[type="submit"] {
    background: #81a2be;
}
ul.tagit {
    background: #282a2e !important;
}
ul.tagit li.tagit-new input {
    background: #373b41 !important;
}
#attachments_fields input.filename {
    color: #e0e0e0;
}
#preview fieldset {
    background: inherit;
}
/* Settings */

#content {
    border-color: #464646;
    color: #c1baba;
}
#content .tabs ul li {
    background: #282a2e;
}
#content .tabs ul li a.selected {
    background-color: #2D2B2B;
    color: #e0e0e0;
}
#content .tabs ul li a.selected:hover,
#content .tabs ul li a:hover {
    background-color: #1d1f21;
    color: #e0e0e0;
}
#content .tabs ul li a {
    background-color: #282a2e;
    color: #e0e0e0;
}
legend {
    color: #c5c8c6;
}
.nodata,
.warning {
    color: #e0e0e0;
    background-color: #2d2b2b;
}
/* Date Picker */

.ui-widget-content,
.ui-datepicker .ui-state-default {
    background: #373b41;
    border-color: #969896;
    color: #e0e0e0;
}
.ui-datepicker .ui-state-default:hover {
    background: #1d1f21;
    color: #e0e0e0;
}
.ui-datepicker .ui-widget-header {
    background: #282a2e;
    border-color: #969896;
}
.ui-state-hover,
.ui-widget-content .ui-state-hover,
.ui-widget-header .ui-state-hover,
.ui-state-focus,
.ui-widget-content .ui-state-focus,
.ui-widget-header .ui-state-focus {
    background: #1d1f21;
    color: #e0e0e0;
}
/* Activity */

div#activity dt .time {
    color: #c5c8c6;
}
div#activity dd span.description,
#search-results dd span.description {
    color: #e0e0e0;
}

div.journal ul.details, ul.revision-info {
    border: none;
}

div.journal ul.details:hover,
ul.revision-info:hover {
    border: none !important;
    background: none;
}
h3, .wiki h2,
#sidebar h3 {
    color: #e0e0e0;
}
hr {
    width: 100%;
    height: 1px;
    background: #504e4e;
    border: 0;
}
div.details{
    border: none;
}
table.progress td.todo{
    background-color: #aaa;
}
table.list tr.issue span.tag-label-color,
span.tag-label-color{
    padding: 0 !important;
    display: contents;
}
table.list tr.issue .tag-label-color a,
.tag-label-color a,
span.tag-label-color a,
span.tag-label-color > span{
    color: #70A7CD;
    padding: 0px 5px 0px 0px !important;
    background: #373b41;
    font-size: 13px;
    font-weight: normal !important;
    font-size: normal !important;
    font-family: "Lucida Grande", verdana, arial, helvetica, sans-serif !important;
}
table.list tr.issue .tag-label-color a:hover,
.tag-label-color a:hover,
span.tag-label-color a:hover,
span.tag-label-color > span:hover{
    color: #70A7CD;
    text-decoration: underline;
}
blockquote{
    border-left: 3px solid #70A7CD;
}
div.wiki img {
    vertical-align: middle;
    max-width: 100%;
    max-height: 250px;
    opacity: 0.8;
}
div.wiki img:hover {
    max-height: initial;
    opacity: 1;
}

fieldset{
    border-color: #504e4e;
}
select {
    border-color: #504e4e;
}
table.list thead th {
    border-color: #504e4e;
    color: #e0e0e0;
}
table.list tbody td, table.list tbody tr:hover td{
    border-color: #504e4e;
    color: #e0e0e0;
}
table.list tr.issue a{
    color: #e0e0e0;
}
table.list tbody tr:hover {
    color: #e0e0e0;
    background-color: #269;
}
tr.odd.priority-5{
    color: #e0e0e0;
    background: #400;
}
tr.even.priority-5 {
    background: #500;
    color: #e0e0e0;
}
tr.odd.priority-5 td, tr.even.priority-5 td{
    border-color: #504e4e;
}
tr.odd.priority-5 td a, tr.even.priority-5 td a{
    color: #e0e0e0;
}
tr.odd.priority-6{
    color: #e0e0e0;
    background: #600;
}
tr.even.priority-6 {
    background: #700;
    color: #e0e0e0;
}
tr.odd.priority-6 td, tr.even.priority-6 td{
    border-color: #504e4e;
}
tr.odd.priority-6 td a, tr.even.priority-6 td a{
    color: #e0e0e0;
}

tr.odd.priority-7{
    color: #e0e0e0;
    background: #800;
}
tr.even.priority-7 {
    background: #900;
    color: #e0e0e0;
}
tr.odd.priority-7 td, tr.even.priority-7 td{
    color: #e0e0e0;
    border-color: #504e4e;
}

tr.odd.priority-3 td a, tr.even.priority-3 td a{
    color: #e0e0e0;
}
tr.odd.priority-3{
    color: #e0e0e0;
    background: #235;
}
tr.even.priority-3 {
    background: #346;
    color: #e0e0e0;
}
tr.odd.priority-3 td, tr.even.priority-3 td{
    color: #e0e0e0;
    border-color: #504e4e;
}
tr.odd.priority-3 td a, tr.even.priority-3 td a{
    color: #e0e0e0;
}

table.list tr.issue .assigned_to a {
    color: #e0e0e0;
}
tr.group td{
    background-color: #135;
}
table a, table a:link, table a:visited{
    color: #e0e0e0;
}

a, a:link, a:visited{
    color: #70A7CD;
}

/**********************************/

#context-menu ul {
    background-color: #2D2B2B;
    border-color:  #504e4e !important;
}
#context-menu li {
    border-color:  #2D2B2B !important;
}
#context-menu, #context-menu ul li ul{
    box-shadow: 0 0 3px #504e4e;
}

#context-menu li:hover{
    background-color: #269 !important;
    border-color:  #269 !important;
    color: #e0e0e0 !important;
}
#context-menu li a:hover{
    color: #e0e0e0 !important;
}

.pagination ul.pages li{
    border-color: #504e4e;
}

#project-jump .drdn-trigger{
    border-color: #504e4e;
    background: #373b41;
    color: #e0e0e0;
}
#project-jump .drdn-content{
    border: none;
}
div + .drdn-items {
    border-color: #504e4e;
}

.drdn-items > * {
    color: #e0e0e0 !important;
    border: 1px solid #373b41;
}
a, a:link, a:visited{
    color: #e0e0e0;
}

#quick-search #q{
    border-color: #504e4e;
    background: #373b41;
    color: #e0e0e0;
}

.drdn-content{
    background-color: #2D2B2B;
}

.contextual a:hover, .contextual a:visited:hover {
    color: #e0e0e0;
}

.add-filter{
    position: relative;
}

.filter .operator {
	position: relative;
}

.add-filter:after,
.drdn-trigger:after,
.filter .operator:after {
    content: "";
    display: block;
    border-style: solid;
    border-width: 6px 5px 0 5px;
    border-color: #e0e0e0 transparent transparent transparent;
    pointer-events: none;
    position: absolute;
    top: 50%;
    right: 8px;
    z-index: 1;
    margin-top: -3px;
}
.add-filter:after {
    margin-top: 5px;
}

.filter .operator:after {
    right: 12px;
}

.theme-A1 .select2-container--default .select2-selection--multiple,
.select2-container--default .select2-selection--multiple {
    background-color: #373b41;
    border: 1px solid #504e4e;
    color: #e0e0e0;
}

.select2-container--default .select2-selection--multiple input{
    color: #e0e0e0;
}

.select2-container--default .select2-results__option{
     background-color: #373b41;
     color: #e0e0e0;
}
.select2-container--default .select2-results__option--highlighted[aria-selected]{
     background-color: #282A2E;
     color: #e0e0e0;
}
#sidebar .select2-container--default .select2-selection--multiple .select2-selection__choice, .select2-container--default .select2-selection--multiple .select2-selection__choice{
    background-color: #269;
    color: #e0e0e0;
}
.select2-container--default .select2-results__option[aria-selected="true"]{
    background-color: #269;
    color: #e0e0e0;
}
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove{
    color: #e0e0e0;
}
input[type="text"], input[type="date"], input[type="password"], textarea, select{
    background-color: #373b41;
    border: 1px solid #504e4e;
    color: #e0e0e0;
}
ul.ui-autocomplete{
    background: #282A2E;
    border-color: #504e4e;
    color: #e0e0e0;
}

div.flash.notice {
    background-color: #005f00;
    color: #dfffdf;
}
table.list tr.issue .tag-label-color a, .tag-label-color a, span.tag-label-color a, span.tag-label-color > span {
	background: none;
}


#content, #main.nosidebar #content{
	border: none;
}
table.filecontent,
table.filecontent tr.spacing td{
    background-color: #282a2e;
    border-color: #373b41;
}
table.filecontent th.line-num,
table.filecontent th {
    border-color: #504e4e;
    background-color: #373b41;
}
table.filecontent th.filename {
    background-color: #373b41;
}
.diff_out{
	background: #400;
}
.diff_out span{
    background: #800;
}
.diff_in {
    background: #131;
}
.diff_in span{
    background: #171;
}
}
