css:
@-moz-document url-prefix("https://kuliah.ppkia.ac.id/") {
@font-face {font-family: "Poppins"; src: url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.eot"); src: url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.eot?#iefix") format("embedded-opentype"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.woff2") format("woff2"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.woff") format("woff"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.ttf") format("truetype"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.svg#Poppins") format("svg"); }

/* UNIVERSAL */

* {
    font-family: 'Poppins', sans-serif;
}

a {
    text-decoration: none!important;
}

#page-my-index, body {
    background-color: #1E1D2B;
}

#page-footer {
    border-top-left-radius: 50px;
    margin-left: 16px;
    background-color: #252736!important;
}

#region-main {
    background-color: #2B2C3E;
    color: #fff;
    border-radius: 8px;
}

#region-main a:hover {
    color: #0177FB;
}

nav.fixed-top.navbar.navbar-light.bg-white.navbar-expand.moodle-has-zindex {
    background-color: #1E1D2B!important;
    border-bottom: 3px solid #252736;
}

nav button.btn.nav-link.float-sm-left.mr-1.btn-light.bg-gray {
    background-color: #2F3042!important;
    color: #fff;
    border: 0px;
    border-radius: 8px;
    padding: 5px 12px;
}

nav button.btn.nav-link.float-sm-left.mr-1.btn-light.bg-gray:hover {
    background-color: #0177FB!important;
}

nav a, .navbar-light .navbar-brand, .dropdown-toggle.nav-link, .navbar-light .navbar-nav .nav-link {
    color: #fff;
}

nav a:hover, .navbar-light .navbar-brand:hover, .navbar-light .navbar-nav .show>.nav-link, .navbar-light .navbar-nav .nav-link:hover {
    color: #0177FB;
}

nav .popover-region-container {
    padding: 4px;
    border: 0px;
    background-color: #252736;
    border-radius: 8px;
    color: #fff;
}

nav .popover-region-header-container {
    border: 0px;
    border-bottom: 2px solid #0177FB;
}

nav .popover-region-content-container::-webkit-scrollbar {
    display: none;
}

nav .popover-region-footer-container {
    background-color: transparent;
    border-top: 2px solid #0177FB;
}

.d-print-none.moodle-has-zindex  {
    padding: 0px;
    background-color: #1E1D2B;
    border-right: 3px solid #252736;
}

body.drawer-open-left {
    margin-left: 223px;
}

#nav-drawer {
    width: 225px;
}

#nav-drawer::-webkit-scrollbar {
    display: none;
}

#nav-drawer .list-group-item.list-group-item-action.active.active_tree_node {
    background-color: transparent;
    border: 0px;
    border-right: 3px solid #0177FB;
    border-radius: 0px;
    color: #0177FB;
}

#nav-drawer .list-group-item.list-group-item-action, #nav-drawer .list-group-item  {
	border-radius: 0px;
    background-color: transparent;
    border: 0px;
    color: #adaecb;
}

#nav-drawer .list-group-item.list-group-item-action:hover, #nav-drawer .list-group-item:hover  {
	border-radius: 0px;
	border-right: 3px solid #fff;
    color: #fff;
}

#page-header.row .col-12.pt-3.pb-3 .card {
    background-color: transparent;
    border: 0px;
}

.page-item.disabled .page-link {
    color: #2B2C3E;
    border: 0px;
    background-color: #1E1D2B;    
}

.page-link {
    color: #fff;
    border: 0px;
    background-color: #1E1D2B;    
}

.dropdown-menu {
    background-color: #2B2C3E;
    border-radius: 8px;
}

.dropdown-item {
    color: #fff;
}

.dropdown-item:active, .dropdown-item:focus, .dropdown-item:hover {
    background-color: transparent;
    border-right: 3px solid #0177FB;
}

.dropdown-item:hover {
    color: #0177FB;
}

.card {
    border-radius: 8px;
    background-color: #252736;
    color: #fff;
}

.card-deck.dashboard-card-deck.one-row.fixed-width-cards.justify-content-center.overflow-hidden {
    background-color: #1E1D2B;
    padding-top: 8px;
    border-radius: 8px;
}

.card-img.dashboard-card-img {
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    box-shadow: 0px -6px 25px -8px black inset;;
}

.card-body.pr-1.course-info-container {
    background-color: #2F3042;
    padding: 9px;
	border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

.card-footer.dashboard-card-footer.border-0.bg-white {
    background-color: #2F3042!important;
    padding-top: 0px;
	border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

.course-summaryitem.mb-1.p-2 {
    background-color: #2F3042;
    border: 0px;
    border-radius: 8px;
    margin: 6px 0px;
}

.course-content ul li.section.main {
	border-bottom: 2px solid #0177FB;
}

.section li.activity.hasinfo {
	border-bottom: 2px solid #5895D9;
}

.list-group-item {
    background-color: #2F3042;
    border: 0px;
    border-radius: 8px;
    margin: 6px 0px;
}

.list-group-item-action {
    color: #fff;
}

.event-name {
    font-weight: bold;
}

.event-name:hover, .list-group-item-action:hover {
    color: #0177FB;
    background-color: transparent;
}

a.aalink.coursename:hover {
    color: #0177FB;
}

.btn.btn-link.btn-icon.icon-size-3.coursemenubtn:hover, .btn.btn-link.btn-icon.icon-size-3.coursemenubtn:focus {
    color: #0177FB;
    background-color: transparent;
}

.list-group-item.flex-column.py-2.pl-0.pr-0.border-0 {
    padding: 10px!important;
}

.container.p-0.pb-3.border-bottom {
	padding-bottom: 0px!important;
}

.border-bottom {
    border-bottom: 0px!important;
}

.text-muted {
    color: #abb4bb!important;
}

.mb-0.pt-2 {
    padding: 0px!important;
    margin-top: 10px;
}

.generaltable, tr:hover {
	color: #fff!important;
}

.filter-group.my-2.p-2.bg-light.border-radius.border {
	background-color: #1e1d2b!important;
	border: 0px!important;
	border-radius: 8px;
	padding: 16px!important;
	margin: 0px!important;
}

.border-radius.my-2.p-2.bg-white.border.d-flex.flex-column.flex-md-row.align-items-md-start {
	background-color: #2b2c3e!important;
	border: 0px!important;
	border-radius: 8px;
}

.float-right.border.mb-2.p-2 {
	border-radius: 8px;
	background-color: #1e1d2b;
	color: #adaecb;
	border: 0px!important;
}

.btn-primary, .form-control, .btn.btn-outline-secondary, .btn-outline-success, .alert-danger, .environmenttable .error, .filepicker-filelist .filepicker-container, .filemanager.fm-noitems .fm-empty-container, .alert, .badge  {
	border-radius: 8px;
}

.modal-content {
	border-radius: 8px;
	background-color: #1e1d2b;
	color: #fff;
	border: 0px!important;
}

.content-item-container.unread {
	background-color: #0a0c4c;
}

.message-app {
	background-color: #252736!important;
	padding: 8px;
	margin-top: 16px;
	margin-right: 16px;
	border-radius: 8px;
	color: #fff;
}

.bg-white.position-relative.border-bottom.p-1.px-sm-2, .border-top.bg-white.position-relative {
	background-color: transparent!important;
}

.message-app .message.received {
	background-color: #2F3042;
	color: #fff;
}

[data-region=right-hand-drawer].drawer.bg-white {
	background-color: transparent!important;
}

.popover.fade.bs-popover-top.show, .popover.fade.bs-popover-bottom.show {
	background-color: #2F3042;
	border-radius: 8px;
	border: 0px;
}

.popover-header {
	background-color: transparent;
	color: #fff;
	border-bottom: 1px solid #0177FB;
}

.popover-body {
	color: #fff;
}

.calendar_event_site, .calendar_event_category, .calendar_event_course, .calendar_event_group, .calendar_event_user, .calendar_event_other {
	border-radius: 6px;
}

.cal_courses_flt, .custom-select {
	border-radius: 8px;
	background-color: #1e1d2b;
	color: #adaecb;
	border: 0px;
}

.btn-secondary, .btn.btn-outline-secondary.dropdown-toggle {
    border-radius: 8px!important;
    background-color: #2F3042;
    color: #fff;
    border: 0px;
}

.progress.bg-white.border {
    background-color: #1E1D2B!important;
    border: 0px!important;
    height: 8px;
    border-radius: 4px;
    padding: 3px;
}

.progress-bar.bar {
    background-image: linear-gradient(to right, #0177FB, #7CBAFF);   
}

.fp-select .fp-thumbnail {
    background-color: transparent!important;
    border: 0px;
}



.moodle-dialogue-base .moodle-dialogue-wrap {
    background-color: #2B2C3E!important;
    border: 3px solid #0177FB;
    border-radius: 12px;
    color: #fff;
}

.moodle-dialogue-hd.yui3-widget-hd {
    border-bottom: 0px!important;
    color: #fff!important;
}

.moodle-dialogue-hd.yui3-widget-hd span.yui3-widget-buttons .yui3-button.closebutton {
    color: #fff!important;
}

::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: #2B2C3E;
}

::-webkit-scrollbar-thumb {
    background-color: #0177FB;
}

}
