css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("homadokht.blogspot.com") {
body{direction:rtl !important; text-align:right !important;background: #4088CF !important;background:url(http://4.bp.blogspot.com/_kMUpUqMmduA/SXfi_WNBPLI/AAAAAAAABms/Qw0Gd-pdpC8/s1600/bggk2.jpg) !important;font-family: Tahoma !important;}
.post {margin:1px 1px 1px 1px !important;border-bottom:1px dotted #cccccc !important;padding:1px 5px 1px 5px !important;}
#main-wrapper {background: #ffffff !important; width: 480px !important;}
#sidebar-wrapper {background: #ffffff !important;}
#outer-wrapper {background: #f1f1f1 !important; width: 710px !important;font-family: tahoma !important;font-size: 12px !important;font:normal normal 12px tahoma !important;}
#header-wrapper {background: #ffffff !important;}
#footer {background: #ffffff !important;}
a:hover { color:#AA1111 !important; }
a:link { color:#AA1111 !important; }
a:visited { color:#AA1111 !important; }
a:active { color:#AA1111 !important; }
a img{border: none !important;}
#comments h4 {margin:15px 5px 15px 5px !important; font-family:Georgia, "Times New Roman", Times, serif !important; font-size:16px !important; color:#333333 !important;}
#comments-block {margin:0 !important;padding:0 !important; width:96% !important; background-color:#FFFFFF !important; border:1px solid #ccc !important; color: #666666 !important;display:block !important;font-style:normal !important;padding:7px !important;font-size:11px !important; line-height:18px !important;direction:rtl;}
#comments-block .comment-author {font-family: Georgia, "Times New Roman", Times, serif !important;text-decoration:underline !important;font-size:13px !important; font-weight:bold !important;display:block !important;}
#comments-block .comment-timestamp {display:block !important;padding-left:20px !important; padding-bottom:2px !important; line-height:20px !important; font-weight:normal !important;}
#comments-block .comment-body {background-color: #F2F2F2 !important; color:#666 !important;padding:10px !important; border:1px solid #ccc !important;  line-height:20px !important; text-align:right !important;}
.comment-nuevo {margin:10px 5px 10px 5px !important; font-family:Georgia, "Times New Roman", Times, serif !important; font-size:16px !important; color:#333333 !important; padding-left:20px !important;}
.comment-nuevo a {color:#333333  !important; font-weight:bold !important;}
.deleted-comment {font-style:italic !important; color:gray !important;}
#sidebar-wrapper {background:#eeeeee !important;border-left:solid 1px #ccc !important;border-top:solid 1px #ccc !important;border-right:solid 1px #ccc !important;
width: 200px !important;padding-left:10px !important;padding-right:10px !important;float: right !important;word-wrap: break-word !important;overflow: hidden !important;}
h2 {
margin:1.5em 0 .75em !important;
font:normal bold 86% Arial, sans-serif !important;
line-height: 1.4em !important;
text-transform:uppercase !important;
letter-spacing:.2em !important;
color:#999999 !important;
}
/* Posts
-----------------------------------------------
*/
h2.date-header {
font-weight:normal !important;
margin:1.5em 0 0em !important;
font-size:10px !important;
}
.post h3 {
margin:.25em 0 0 !important;
padding:0 0 0px !important;
font-size:180% !important;
font-weight:bold !important;
line-height: 1em !important;
color: #35556a !important;
}
.post h3 a, .post h3 a:visited, .post h3 strong {
display:block !important;
text-decoration:none !important;
color:#35556a !important;
font-weight:normal !important;
}
.post h3 strong, .post h3 a:hover {
color:#333333 !important;
}
.post-body {
margin:0 0 .75em !important;
line-height:1.6em !important;
font-family: tahoma !important;font-size: 12px !important;font:normal normal 12px tahoma !important;
}
.post-body blockquote {
line-height:1.3em !important;
}
.post-footer {
margin: .75em 0 !important;
color:#999999 !important;
text-transform:uppercase !important;
letter-spacing:.1em !important;
font: normal normal 78% Arial, sans-serif !important;
line-height: 1.4em !important;
}
.comment-link {
margin-left:.6em !important;
}
.post img {
padding:4px !important;
border:1px solid #cccccc !important;
}
.post blockquote {
margin:1em 20px !important;
}
.post blockquote p {
margin:.75em 0 !important;
}
/* Sidebar Content
----------------------------------------------- */
.sidebar {direction:rtl !important; text-align:center !important;
color: #666666 !important;
line-height: 1.5em !important;
}
.sidebar ul {direction:rtl !important; text-align:right !important;
list-style:none !important;
margin:0 0 0 !important;
padding:0 10px 0 !important;
}
.sidebar li {
margin:0 !important;
padding-top:0 !important;
padding-right:0 !important;
padding-bottom:.25em !important;
padding-left:15px !important;
text-indent:-15px !important;
line-height:1.5em !important;
}
.sidebar .widget, .main .widget {
border-bottom:1px dotted #cccccc !important;
margin:0 0 1.5em !important;
padding:0 0 1.5em !important;
}
.main .Blog {
border-bottom-width: 0 !important;
}
}
