css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("scenereleases.info") {




body{

 color:#000 !important;
 background:#41566E !important;
}



.entrys p{ 
 color:#000 !important;
}


.footer, .footer_meta  {
 color:#000 !important;
 background: inherit !important;
 border: none !important;
}

.widget_categories li{
 color:#000 !important;
 background:#EAEDF0 !important;
 border: none !important;
}

.imgborder  {
 display: none;

}

.breadcrumb, .breadcrumb_info, .newsbar {
 -moz-border-radius: 3px !important;
 border: none !important;
 background:#EAEDF0 !important;
}

.breadcrumb a {
border: none !important;
}

.widget_block {
 color:#000 !important;
 background:#EAEDF0 !important;

}

#who-is-online, .cat-item,  .widget.widget_search, .widget_block.tablist, .widget.widget_text {
 -moz-border-radius: 20px !important;
 color:#000 !important;
 background:#EAEDF0 !important;
 border: none !important;
}

.entrys {
-moz-border-radius: 7px !important;
 color:#000 !important;
 background:#fff !important;
 border: none !important;
 margin-bottom:10px !important;
}



.widget_search input , input, textarea {
 color:#000 !important;
 background:#fff !important;
 border: inherit !important; 
}


.widgettitle {
 color:#555 !important;
}


#comments {
 color: #000 !important;
}

.sidebar a {
background: inherit !important;
color:#C02E13 !important;
text-decoration:none !important;
border: none !important;
}

a:hover {
text-decoration:underline !important;
}



.entry a, .breadcrumb a, .entrys a{
 color:#0792D3 !important;
 border: none !important;
}

.textwidget a, .trackback_list li a{
text-decoration:none !important;
border: none !important;
}

.meta, .metab {
 border: none !important;
}

.metab a{
color: inherit !important;
background: inherit !important;
border: none !important;
text-align: left !important;
padding:0px 17px !important;
margin-bottom:33px !important;
}


.page_item a:hover,  .page_item a  {
  background: inherit !important;
  border: none !important;
}


#head h1 a { display: none !important; top: 0px !important}

#top #head h1 {height: 0px !important; }

.firefox {display: none !important}

.icon_rss {left: 975px !important; height: 22px !important;}

.imgborder {display: none !important}

#head { background:#41566E !important; background-image: none !important; position:relative !important; height: 50px !important; background:none no-repeat !important}

#head a {border: none !important; }

#wrapper {height: 44px !important;  background:#41566E !important;}

#top #nav { background:#41566E !important; position: relative !important; top: 5px !important}


#top li a {  background: inherit !important;}

}
