css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org") {

#hattrick, #hattrickNoSupporter 
{ 
	width: 98% !important; 
	min-width: 600px !important; 
	margin-left: 1% !important; 
	margin-right: 1% !important;
}

#ticker
{ 
	width: 50% !important; 
	margin-left: 50px !important; 
	margin-right: 0px !important; 
}

#shortcuts
{
        top: 5px !important;
}

#time, #online
{
        margin-top: 23px !important;
}

#page, #footer 
{ 
	width: inherit !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important; 
	position: relative !important; 
}
 
.subMenu 
{ 
	width: 16% !important; 
	margin-left: 0px !important; 
	margin-right: 1% !important; 
}

.mainRegular 
{ 
	width: 61% !important; 
	margin-left: 0px !important; 
	margin-right: 1% !important; 
}
 
div#sidebar 
{ 
	width: 21% !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important; 
}
 
.mainWide 
{ 
	width: 83% !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important; 
}

.subMenuConf 
{ 
	width: 29% !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important;
}
 
.mainConf 
{ 
	width: 70% !important; 
	margin-left: 1% !important; 
	margin-right: 0px !important; 
}

.url
{ 
	width: 220px !important;
        max-width: 250px !important;
	overflow: hidden !important;
}

.fplLongThreadName
{ 
	width: 90% !important; 
	overflow: hidden !important;
}

.fplThreadName, .fplFolderName, .fplLongFolderName
{ 
	width: 75% !important; 
	overflow: hidden !important;
}

.subMenuBox, .sidebarBox, .ownerNotes textarea, .notebook, .gbPreviewMsg 
{ 
        width: 100% !important; 
}
 
img[class="scMySupporters"] 
{ 
	background:transparent url(http://img14.imageshack.us/img14/2262/benannt.png) no-repeat !important; 
	padding:0px 0px 22px 22px !important; 
	height:0px !important; 
	width:0px !important; 
	border: 0px !important;
}

img[title="Abonniert/In den Favoriten"]
{
        display: none !important;
}

div[class="main mainFull"]
{
       width: 99% !important;
}

.liveTabsContainer
{
       position: absolute !important;
       right: 0px !important;
}

.liveMatchContainer
{
       width: 77% !important;
}

.liveReport
{
margin-left: -10px !important;
margin-right: -10px !important;
}

.liveReportHeader
{
margin-top: -5px !important;
}

.liveEvent, td[class="date"], .liveEvent>div
{
padding-bottom: 1px !important;
padding-right: 2px !important;
padding-left: 2px !important;
}

#footer a
{
        color: white !important; 
}

#teamLinks
{
        color: #ffffff !important;
}

div#teamLinks a
{
        color: darkred !important;
}

/*Users online - week - date - time*/

div[id="online"] 
{
        color: darkred !important;
}

div[id="time"]
{
        color: darkred !important;
}

/*Page background*/
body
{  
  background: darkred !important;
}

/*centre title*/
h1 
{
        color: darkred !important;
        background: #FFFFFF !important;
}

/*centre title*/


/*centre news*/
.boxBody h2 
{ 
        color: #FFFFFF !important;
        
}


.subMenuBox .boxHead
{
	background: darkred !important;
}

.subMenuBox .boxHead .boxLeft
{
        background: darkred !important;
}

/*Centre box*/
.main .boxHead h2 
{
	background: darkred !important;
}

/*Right menu*/
.sidebarBox h2 
{
	background: darkred !important;
}

/* Press, GB, Team events */
.main .mainBox h2 
{
	background-color: #fff !important;
	color: darkred !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}


/*Central header paths*/

h2 a, h3 a
{
        color: #ffffff !important;
}

h2 a:hover, h3 a:hover 
{
        color:#008A8A !important;
}


/*News and team events date*/

.date
{
	color: #000000 !important;
	padding-right: 8px;
	font-weight: 400;
}

/*Left Menu titles*/
h3 
{
        color: #FFFFFF !important;
        background: darkred !important;
}

/*Menu titles*/
h2 
{
        color: #FFFFFF !important;
        background: darkred !important;
}

/*General Links*/
a
{
        color: darkred !important;
}

/*----subMenu and sidebar links----*/
.subMenuBox a, .subMenuBox a, .sidebarBox a 
{
	color: black !important;
}

/*Bot teams link*/

div a[class="shy"] 
{
        color: gray !important;
}

/* central page background */
#content
{
        background-image: none !important;
        background-color: #FFFFFF !important;
}

/*--------Top menu settings--------*/
#menu
{
	height: 20px !important;
        background: darkred !important;
}

#menu a
{
	line-height: 20px !important;  
        color: #ffffff !important;
}

div#menu a:hover 
{
        color:#FFFFFF !important; background:#008A8A !important;
}

a:hover
{
	color: #008A8A !important;
}

/*bottom white line*/
div[id="bottom"]
{
        background: #008A8A !important;
}


/*---------Forum---------*/
.cfHeader, div[class="float_right right"]
{
        background:  darkred !important;
}

#cfPollHeader
{
	background: darkred !important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
}

.cfWrapper
{
	border: solid 1px #008A8A !important;
	margin: 0 0 12px 0;
}

.cfHeader a
{
	color: #008A8A !important;
	font-weight: bold;
	text-decoration: none;
}

.folderHeader a:link 
{
	color: darkred !important;
	font-weight: bold !important;
}

.folderHeaderHighlight {
         background-color: #008A8A !important;
}

.cfFooter a
{ 
        color: black !important;
}


.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i53.photobucket.com/albums/g60/M_Oses/Test/a91dcf5d.png) no-repeat !important;
}

.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i53.photobucket.com/albums/g60/M_Oses/Test/9a541b82.png) !important;
}

.folderItem div.unignore:hover
{
        background: url(http://i53.photobucket.com/albums/g60/M_Oses/Test/b726d7c1.png) !important;
}

