css:
@-moz-document domain(amazon.de) {
/*
Stylus theme for https://amazon.de/
Amazon.de dark theme
author: zooid
GITHUB: https://github.com/zooid/amazon.de_dark_theme
License: CC BY-NC-ND 4.0
*/

body{
    color: #fff !important;
    background-color: #424242 !important;
}
.a-cardui{
    color: #fff !important;
    background-color: #424242 !important;
}
.a-color-base, .a-ordered-list .a-list-item, .a-unordered-list, .a-list-item, ol .a-list-item, ul .a-list-item {
    color: #fff !important;
    background-color: #424242 !important;
}
div{
    color: #fff !important;
    background-color: #424242 !important;
}
.sc-background-dark{
    color: #fff !important;
    background-color: #424242 !important;
}
a, a:link, a:visited, .a-color-link {
    text-decoration: none;
    color: #1cb9a4 !important;
}
#nav-search .nav-search-facade .nav-search-label{
    color: white !important;
}
.a-button{
    color: white !important;
    background-color: #2d5b95 !important;
}
.a-button .a-button-text{
    color: white !important;
    background-color: rgb(67, 108, 99) !important;
}
table.a-keyvalue th{
    color: #fff !important;
    background-color: #424242 !important;
}
table.a-bordered tr:nth-child(even){
    background-color: #424242 !important;
}
.comparison_table tr:nth-child(even) td.comparison_baseitem_column, .comparison_baseitem_column{
    Background-color: lightgrey !important;
}

#prodDetails #SalesRank ul li span.zg_hrsr_ladder, #prodDetails #SalesRank ul li span.zg_hrsr_rank, #prodDetails .wrapper, #prodDetails td{
    color: #fff !important;
    background-color: #424242 !important;
}

.a-color-price{
    color: salmon !important;
}
}
