css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url("https://portal.utoronto.ca/webapps/portal/tab/_1_1/index.jsp"){

/*----LINK COLORS---*/
a:link    { color: #bdcdd2 !important; }
a:visited { color: #76898f !important; }
a:hover   { color: #b8b8b8 !important; }

/*----RED ALERT TEXT---*/
span{color: #b8b8b8 !important;}

/*---Removes Welcome [user], Modify Page--*/
h1{display: none !important;} 


/*---Background---*/
html{
   border:              none !important;
   background:          #162c4b !important;
   background-image:    url("http://i43.photobucket.com/albums/e374/frank11a/blue-bldg.jpg") !important;
   background-repeat:   no-repeat !important;
   background-position: 0px 20px !Important;
   background-attachment: fixed !important;
   margin-top: -0px !Important;
}



body{
   margin-top:0px !important;
   margin-left:0px !important;
   margin-right:0px !important;
   padding-bottom:10px !important;
   border:              none !important;
   background:          none!important;
   background-image:    none !important;
   background-repeat:   no-repeat !important;
   background-position: 100% 100% !Important;
}

/*---LISTS--*/
li{
   margin-top:    2px !important;
   list-style-image: url("http://i43.photobucket.com/albums/e374/frank11a/arrow_right.png") !important;
}


.panelTitle{color: #063261!important; }

strong{color:#ccc !important}
font{color:#ccc   !important}

.nav{
   font-size:   large  !important;
   visibility:  hidden !important;
}

table{
   background:        none!important;
   background-repeat: repeat-x !important;
/* background-image:  url("http://i43.photobucket.com/albums/e374/frank11a/line.png") !important; /**/
}

/*--Controls Course Listing Box Backgrounds--*/
th{
   background:   none !important;
   padding:      5px  !important;
   padding-left: 3px  !important;

}

tbody{background:none !important;}

tr{background:none    !important;}

/*--Controls A LOT!!!---*/
td{
   color:       #000           !important;
   background:  none           !important;
   font-family: century gothic !important;
   font-weight: normal         !important;
   margin:      0px            !important;
   padding-top: 1px            !important;
}

/*--Controls dark shade outline, Alert Box BG---*/ 
/*--fading shade over modules--*/
td.moduleBody{
   color:       #ffffff  !important;
   background:  none     !important;
   background-image:url("http://i43.photobucket.com/albums/e374/frank11a/blue-fade1.png")    !important;

   background-repeat: repeat-x !important;
   margin:      0px            !important;
   padding-top: 0px            !important;
}

/*---Module Title Bars---*/
.moduleTitle{
   font-size:        12pt    !important;
   font-weight:      normal  !important;  
   font-family:      calibri !important;
   background-image:none !important;
   background-color: #12243d !important; 
   margin-left:      -5px    !important;
   padding-left:     5px     !important;
}

/*--WELCOME [USER]---*/
.pageTitle
{
   color:       #063261 !important;
   font-size:   13pt    !important;
   font-weight: normal  !important;
   font-family: arial   !important;
   padding-left: 5px !important;
   background: url("http://i43.photobucket.com/albums/e374/frank11a/text.png")!important;
   background-repeat:    no-repeat  !important;
   background-position:  center 5px !important;
}

/*--??--*/
.toolboxBorder
{
    WIDTH: 150px   !important;
    COLOR: #000000 !important;
    BACKGROUND-COLOR: #ff0000 !important;
}

/*--BUTTONS--*/
.inlineAction
{
    COLOR:         #063261 !important;
    BORDER-TOP:    #cccccc 0px outset !important;
    BORDER-LEFT:   #cccccc 0px outset !important;
    BORDER-RIGHT:  #cccccc 0px outset !important;
    BORDER-BOTTOM: #cccccc 0px outset !important;

    PADDING:       5px !important;
    PADDING-LEFT:  4px !important;
    PADDING-RIGHT: 4px !important;

    FONT-SIZE:     8pt    !important;
    FONT-WEIGHT:   bold !important;
    FONT-FAMILY:   verdana !important;

    MARGIN-LEFT:   0px !important;
    MARGIN-RIGHT:  0px !important;

    BACKGROUND-COLOR: #999 !important;
    BACKGROUND: url("http://i43.photobucket.com/albums/e374/frank11a/darkshade.png");

}
.titlebuttons{
padding:0px !important;
}
td.inlineAction{width:0px !Important;}
A.inlineAction{color:#b8b8b8 !important}

/*--Controls Marks - Colors---*/
.modernLight, .modernLightLeft{ color: #5aa071 !important}
.modernDark,  .modernDarkLeft { color: #067f00  !important}

/*------IMAGE REPLACE TECHNIQUE - Close Module ----*/
img[src*="images/console/icons/window_remove.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    7px !important;
        margin-top: 2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/button_cancel.png) !important;
background-position: center !important;
}

/*------IMAGE - Minimize Module ----*/
img[src*="images/console/icons/window_min.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    7px !important;
        margin-top: 2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/up.png) !important;
background-position: center !important;
}

/*------IMAGE - Maximize Module ----*/
img[src*="images/console/icons/window_max.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    6px !important;
        margin-top: 2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/download-mini.gif) !important;
background-position: center !important;
}

/*------IMAGE - Edit Module ----*/
img[src*="images/console/icons/window_edit.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    7px !important;
        margin-top: 2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/process-16x16.png) !important;
background-position: center !important;
}

/*------IMAGE - Announcements----*/
img[src*="images/ci/icons/announcement_li.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    8px !important;
        margin-top: 2px !important;
background-image: url("http://i43.photobucket.com/albums/e374/frank11a/exclamation.png") !important;
background-position: center !important;
}

/*------IMAGE - Send Mail----*/
img[src*="images/ci/icons/email_li.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    6px !important;
        margin-top: 2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/email.png) !important;
background-position: center !important;
}

/*------IMAGE - Personal Info----*/
img[src*="images/ci/icons/profile_li.gif"]{
    width:      0px !important;
    height:     1px !important;
    padding:    6px !important;
        margin-top: 2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/user-16x16.png) !important;
background-position: center !important;
}

/*------IMAGE - Course Map/Course Listed----*/
img[src*="images/ci/icons/bookopen_li.gif"]{
    width:      0px  !important;
    height:     1px  !important;
    padding:    10px !important;
        margin-top: 0px  !important;
background-image: url("
http://i43.photobucket.com/albums/e374/frank11a/book2.png") !important;
background-position: center !important;
}
/*-------------END---------*/
}
