css:
@-moz-document url-prefix('https://novel122.com/') {
body {
    background: #2d2d2d!important;
}
a {
    color: #606060 !important;
}
#main, .chapter-content, body.chapter-theme-light .chapter-content, #footer {
    background: #1c1c1c !important;
    border: none!important;
    color: #aeaeae!important;
}
#sidebar .block {
    background: #131313!important;
    border-color: #2c2c2c!important;
}
#sidebar .novel-list li, .game-list, .game-list .game-medium, #main-content {
    border-color: #2c2c2c!important;
}
#sidebar .block .title, .category #main-content .block h3.title, .home #main-content .block h3.title {
    background: none!important;
    color: #7B7B7B!important;
}
.game-list .game-medium:hover, .list-chuong ul li:hover {
    background: #131313 !important;
}
#navbar {
    /*background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(../images/navbar.png) top left repeat-x !important;*/
    background: #0d0d0c!important;
    border: none!important;
}
.sprites {
    background: none!important;
}
#navbar #search .search-container .top_search {
    background: #060606!important;
    border: 1px solid #202020!important;
}
#navbar #navbar-ul > .navbar-li:hover, #navbar #navbar-ul > .navbar-li > a.active {
    background: #000!important;
}
#navbar #navbar-ul > .navbar-li > .menu-expand {
    background: #090808!important;
}
#navbar #navbar-ul > .navbar-li > .menu-expand > .menu-expand-ul > li:hover {
    background: #171717!important;
}
}
