css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain(boards.4chan.org) {
body[bgcolor] {
background-color:#D4DFFF !important;
background-image:url(http://i46.tinypic.com/wwgjd1.png) !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important
}
.postblock, .pages {
background-color: #7F9FFB !important;
}
.pages, .reply, .replyhl {
-moz-border-radius-bottomright: 8px !important;
-moz-border-radius-topright: 8px !important;
-moz-border-radius-topleft: 8px !important;
-moz-border-radius-bottomleft: 8px !important;
}
.replyhl{
background-color: #FBC0DF !important;
}
.reply {
background-color: #9AB4FA !important;
}
.reply, .replyhl {
border: 1px solid #000000 !important;
background-image:none !important;
background-position: left !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
/*Posts text font below*/
color: #000000 !important;
}
.unkfunc{
color: #1b5b1b !important;
}
.postername, .commentpostername {
background: inherit;
color: #72261a !important;
font-weight: 800 !important;
}
DIV[class="4chan_ext_thread"] > BLOCKQUOTE {
max-width:800px !important
}
}
TABLE[class="4chan_ext_reply"] {
max-width:1100px !important
}
