    --glowshadow2: 0 0 3px var(--main), 0 0 10px var(--main);
}

.theme-dark, .theme-light {
    /**************/
    --background-primary: transparent;
    --background-secondary: transparent;
    --background-secondary-alt: transparent;
    --background-tertiary: transparent;
    --deprecated-store-bg: transparent;
    /*************/
    --background-floating: /*[[floatbg-key]]*/;
    --background-accent: hsla(217, 8%, 34%, .4);
    /*************/
    --channeltextarea-background: var(--textbox);
    /*************/
    --background-modifier-hover: hsla(0, 0%, 35%, .16);
    --background-modifier-active: hsla(0, 0%, 35%, 0.24);
    --background-modifier-selected: hsla(0, 0%, 35%, 0.32);
    --background-modifier-accent: hsla(0, 0%, 100%, .15);
    /*************/
    --background-message-hover: rgba(128, 128, 128, .09);
    --background-mentioned: hsla(38,calc(var(--saturation-factor, 1)*95.7%),54.1%,0.15);
    --background-mentioned-hover: hsla(38,calc(var(--saturation-factor, 1)*95.7%),54.1%,0.1);
    /*************/
    --elevation-stroke: initial;
    --elevation-low: initial;
    --elevation-medium: initial;
    --elevation-high: initial;
    /*************/
    --header-primary: #fff;
    --header-secondary: #9d9d9d;
    --text-normal: #dedede;
    --text-muted: var(--main);
    --channels-default: #92979c;
    /*************/
    --channel-icon: var(--main);
    --channel-text-area-placeholder: var(--main);
    /*************/
    --text-link: /*[[linkcolor-key]]*/;
    --text-link-low-saturation: /*[[lowsatlinkcolor-key]]*/;
    /*************/
    --interactive-normal: #bdbdbd;
    --interactive-hover: #fff;
    --interactive-active: #fff;
    --interactive-muted: rgba(87, 87, 87, .502);
    /*************/
    --scrollbar-thin-thumb: var(--main);
    --scrollbar-thin-track: transparent;
    --scrollbar-auto-thumb: var(--main);
    --scrollbar-auto-track: rgba(32, 32, 32, .8);
    --scrollbar-auto-scrollbar-color-thumb: var(--main);
    --scrollbar-auto-scrollbar-color-track: rgba(32, 32, 32, .8);
    /*************/
}

