css:
@-moz-document domain("monthly.com") {
.peergroup-content-inner-container,
.classroom-board-content-container,
.classroom-board-container{
    background: #23232d;
}
.monthly-sticky-header .classroom-top-bar
, .peergroup-right-sidebar-container
, .curriculum-section-container {
    background: #1c1c25;
    border-color: #111111;
    color: #999;
}
.activity-card-summary-info-person-name
, .activity-card-title
, .activity-card-date
, .ProseMirror
, .peer-group-sidebar-static-title
, .peergroup-sidebar-person-name .activity-card-summary-info-person-name
, .curriculum-section-title
, .curriculum-section-content-type-title
, .curriculum-section-content-title
, .peer-feedback-project-content-instructions.text-paragraph-m
, .curriculum-video-title
, h3
, ul li::before 
, .curriculum-prompt-page-description-line strong
, .curriculum-video-class-notes strong{
    color: #999;
}
.activity-card-container,
.curriculum-section-content-container{
    background: #333;
    border-color: #222;
}
.activity-card-row-bottom.activity-card-separator {
    background: #333;
    border-color: #333;
}
.peergroup-sidebar-person:hover {
    background: #334;
}
.profile-hover-card {
    background: #333;
    border-color: #222;
    color: #999;
}
}
