    border-radius: 20px 20px 0 0;
    padding-bottom: 36px;
  }

  .ytp-menuitem-toggle-checkbox:after
  {
    box-shadow: 0 0 0 0;
  }

  #masthead
  {
    background: var(--by-main-popup-color-55);
    border-radius: 0 0 20px 0;
  }

  #container.ytd-searchbox
  {
    margin-top: 3px;
    border: 0 !important;
    border-bottom: 3px solid transparent !important;
    clip-path: polygon(0 0, 100% 0, 100% calc(100% - 5px), 0% calc(100% - 5px));
    background: transparent;
    text-align: center;
    background-color: transparent;
  }

  ytd-searchbox[has-focus] #container.ytd-searchbox
  {
    border: 0 !important;
    border-bottom: 3px solid #1c62b9 !important;
    box-shadow: 0px 10px 30px 0px rgba(0,0,0,0.5);
    clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
    animation: searchboxborder .5s;
  }

  #search-icon-legacy.ytd-searchbox
  {
    display: none;
  }

  ::placeholder
  {
    color: rgba(255, 255, 255, 0.7);
  }

  div.gstl_50.sbdd_a
  {
    top: 56px !important;
  }

  .sbdd_b
  {
    box-shadow: inset 0px 125px 50px -125px rgba(0,0,0,0.3) !important;
  }

  .sbdd_b, .sbsb_a
  {
    border: 0;
    box-shadow: 0px 10px 30px 0px rgba(0,0,0,0.5);
    background: var(--by-main-popup-color-2);
    border-radius: 0 0 20px 20px;
  }

  .gsfs
  {
    color: rgba(255, 255, 255, 0.9);
    text-align: center !important;
  }

  .sbsb_d
  {
    background: rgba(255, 255, 255, 0.1);
  }

  .sbfl_a, .sbfl_b
  {
    font-style: normal;
    text-align: center;
    color: rgba(255, 255, 255, .5);
    background: transparent;
  }

  .sbfl_a, #search-input.ytd-searchbox-spt input
  {
    margin: 0;
  }

  .sbsb_c
  {
    padding: 4px 24px 4px 24px;
  }

