css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spacequadrat.de") {
.forum_user_box {
width:170px !important;}

.forum_user_box .username{
background:#1E81D5;

}
.username a {
background:#ffc514 url(http://famfamfam.com/lab/icons/mini/icons/icon_user.gif) no-repeat 140px 50% !important;

color:#1f1f1f !important;
padding:7px;
display:block;
}

h4 {padding:10px !important;}

.forum_adminOptions {display:none;}
.forum_user_box {background:#f9f9f9 !important;}
.forum_post_box {
width:768px !important;
border:1px solid #fff !important;
outline:1px solid #ccc;
margin-right:2px !important;
padding-right: 0 !important;
}
.benutzertitel {display:none;}
.avatar {width:170px;text-align:center;}
.avatar img {margin:10px 0 !important;padding:5px !important;border:1px solid #fff;outline:1px solid #ddd;background:white !important;}
.webid {color:#1E81D5;background:#1E81D5;margin-top:5px !important;padding:5px !important;border-bottom:1px solid white;}
.forum_user_box {border-bottom:1px solid #1E81D5;}
.forum_content {margin-top:10px !important;}

/* FORUM THREADS OVERVIEW HACK*/
.forum_post {padding-right:2px !important;}
.forum_threads  {
padding: 5px !important;
}
.forum_threads {

border:none !important;
margin:0 !important;
border-top:1px solid #ffffff !important;
border-bottom:1px solid #d5d5d5 !important;
}

#threads {
background:#f5f5f5 !important;
border-bottom:none !important;
}

.forum_post_box h3 {
-moz-border-radius-topleft:5px;
border:1px solid #fafafa;
background:#fafafa;
padding:5px 10px 0 10px !important;
}

.post_meta {
-moz-border-radius-bottomleft:5px;
display:block;
background:#fafafa;
padding:3px 10px !important;
}
#forum_meta {
border-top:1px solid white !important;
background-color:white !important;
display:block !important;
padding:10px 0 !important;
border-bottom:1px solid white;
background-position:top left !important;
background-repeat:repeat-x !important;
}
#threads .forum_thread_delete {display:none !important; }

.signatur_box {
margin-top:10px;
border:none !important;
font-size:11px !important;
padding:2px !important;
color:#999 !important;
}
.new_post {
padding:5px  !important;

display:inline;
-moz-border-radius:3px;
-moz-outline-radius:3px;

}


h1 {margin-bottom:20px !important;}
#sst {
text-align:center;
font-size:12px;
height:15px !important;
padding:3px 20px !important;
color:#ccc;
border:1px solid #fff !important;
}

#search-btn {
outline:1px solid #0055cc;
border:1px solid #33ccff !important;
}

.show_pages a {background:white;padding:3px !important;}
.curlycontainer {background:white;}
}
