css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tppcrpg.net") {
body {background-color:#000000!important;
color:#5C5C5C !important;}
input.BasicInput, input {color:#5C5C5C !important;}
div.header {overflow:hidden !important;}
div {background-color:#000000 !important;}
a {color:#5C5C5C !important;}
a:visited {color:#5C5C5C !important;}
li.header {background-color:#000000 !important;}
li {background-color:#1F1F1F !important;}
div.left ul li a:hover, div.right ul li a:hover {background-color:#7A7A7A !important;}
table.ranks td.NonClear {background-color:#2F2929 !important;}
th {background-color:#4F1814 !important;}
span.hideBox {background-color:#111111 !important;}



/** This part here removes the ads and corrects the layout distortion caused by the ad removal, if for some reason you want to show the ads them removerthe 4 lines of code below this. Also, credit goes to Sci of TPPC for giving me this to use...hopefully. **/

div.header {display:none !important}
div.ads, div.a_d {display:none !important;}
iframe[src="/ads_bottom.html"] {display:none !important;}
div.left {top:100px !important;}
div.right {top:100px !important;}

/** do not delete the text below this line if you are just editing this to show the ads **/

}


@-moz-document url("http://tppcrpg.net/friends.php") {
th {background-color:transparent !important;}
}
