css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.animefanboard.de") {

html
{
   background: #000 url(http://img.netzkult.com/afb/afb-dark/bg.jpg) repeat-x fixed;
   text-align: left !important;
   padding: 0 !important;
}

body
{
   background-image: none !important;
   text-align: left !important;
   margin: 0 !important;
   color: #fff !important;
}

body div
{
   max-width: 1004px !important;
}

fieldset
{
   border: 1px solid #000;
}

thead *, .thead
{
   background: #BE7646 url(http://img.netzkult.com/afb/afb-dark/gradient3.jpg) left top repeat-x !important;
}

.thead span, .thead span a
{
   background: none !important;
}

.tborder
{
   background: #82441B !important;
   border: 1px solid #000 !important;
}

.tcat
{
   background: #C37948 url(http://img.netzkult.com/afb/afb-dark/gradient2.jpg) repeat-x left bottom !important;
}

.tcat span
{
   background: none !important;
}

.tcat a
{
   background: none !important;
}

.tfoot
{
   background: url(http://img.netzkult.com/afb/afb-dark/gradient2.jpg) repeat-x !important;
}

.headerL
{
   display: none !important;
}

.logowrap
{
   display: none !important;
}

.headerwrap
{
   background: url(http://img.netzkult.com/afb/afb-dark/headerbg.jpg) repeat-x !important;
   border: 1px solid #000;
}

.page, .cwrap1
{
   background: #5B3216;
}

.vbmenu_control
{
   background: url(http://img.netzkult.com/afb/afb-dark/gradient1.jpg) repeat-x !important;
}

.alt1, .alt1Active, .vbmenu_option
{
   background: #B76F3F url(http://img.netzkult.com/afb/afb-dark/alt1.jpg) repeat-x left bottom !important;
}

.alt2
{
   background: #A86435 url(http://img.netzkult.com/afb/afb-dark/alt2.jpg) repeat-x left bottom !important;
}

.sticky
{
   background: #8D4E23 !important;
}

#post .alt2, #post hr, #post .alt1, #post .thead
{
   border: 1px solid #000 !important;
}

#post .alt1, #post .alt2
{
   border-left: 0 !important;
}

#post .thead
{
   border: 0 !important;
}

#post .alt1
{
   border-right: 0 !important;
}

#post .alt1, #post .alt2
{
   border-bottom: 0 !important;
}

#post
{
   border-top: 0 !important;
}

.panelsurround
{
   background: #A86435 url(http://img.netzkult.com/afb/afb-dark/alt2.jpg) repeat-x left bottom !important;
}

.panel
{
   background: #B76F3F url(http://img.netzkult.com/afb/afb-dark/alt1.jpg) repeat-x left bottom !important;
   border: 1px solid #000 !important;
}

.vBulletin_editor
{
   background: #A86435 url(http://img.netzkult.com/afb/afb-dark/alt2.jpg) repeat-x left bottom !important;
   border: 1px solid #000 !important;
}

.imagebutton
{
   border: 1px solid #000 !important;
}

.wysiwyg
{
   color: #fff !important;
   background: #44240D !important;
}

.tborder tbody *
{
   border-color: #000 !important;
}

.tborder
{
   border: 0 !important;
}

.vbmenu_popup
{
   border: 1px solid #000 !important;
   background: #A96536 !important;
}

.vbmenu_option_alink
{
   border-top: 1px solid #000 !important;
   background: #A96536 !important;
}

.vbmenu_hilite_alink
{
   border-top: 1px solid #000 !important;
   background: #703B19 !important;
}

.vbmenu_option_alink a
{
   color: #703B19 !important;
}

.vbmenu_hilite_alink a:hover
{
   color: #F8D77A !important;
}


a:hover
{
   color: #F8D77A !important;
}

}
