@-moz-document domain("baidu.com")
{
	/*主体页面部分*/
	/*①设置背景图片、颜色*/
  body
  {
		/*如果没有背景，使用该color*/
    background-color: #FFF !important;
		/*自定义图片背景*/;
  }
	
	/*②设置元素透明度，当背景使用图片时更好看，该部分代码来源于@CWorld的Pure Baidu*/
  .video_list.video_short, #wrapper #rs,
	#wrapper #content_left .result,
	div#wrapper #content_left .result-op.c-container,
	#content_left .result-op,
	#content_right, #s_tab, #page, #foot,
	.container_l #content_right, #wrapper #rs:hover,
	#u_sp .s_bdbri, .s-top-wrap, #s_main, .s-code-blocks,
	#bottom_layer, #s_top_wrap.s-down .s-center-box,
	#s_menu_gurd.s-down,
	#content_left, .c-span4:hover, .s-top-wrap, #s_main,
	#bottom_layer
  {
    background: rgba(255, 255, 255, 0.7) !important;
  }
	
	/*③隐藏元素部分*/
	
	/*隐藏右侧热榜等，不做选项，强制隐藏，否则会导致内容下降*/
  #content_right, .aside.fixheight
  {
    display: none !important;
  }
	
	/*清除自带背景*/
  #content_left, .c-result-content article
  {
    background-color: rgba(0, 0, 0, 0) !important;
  }
	
	/*隐藏个人信息*/
  #u, #userInfo
  {
    display: none !important;
  }
	
	/*隐藏gotopage*/
  #gotoPage
  {
    display: none !important;
  }
	
	/*隐藏相关搜索*/
  .list-footer
  {
    display: none !important;
  }
	
	/*[选项1]：是否隐藏工具栏、站点栏，如果隐藏会更好看,默认开启*/
	/*如果隐藏搜索数量、工具栏，但保留高度以作为搜索框和结果之间的分割*/
	
	/*~~~[1-1]:隐藏工具栏、站点栏~~~*/
	
	/*.head_nums_cont_outer.OP_LOG{*/
	/*	height: 20px;*/
	/*	visibility: hidden;*/
	/*}*/
	/*.s_tab {*/
	/*	height: 10% !important;*/
	/*	border-bottom: none !important;*/
	/*	visibility: hidden;*/
	/*}*/
	/*.s_tab_inner {*/
	/*	display: none !important;*/
	/*}*/
	
	
	/*~~~[1-2]:开启工具栏、隐藏站点栏~~~*/
	
	/*.s_tab {*/
	/*	height: 10% !important;*/
	/*	border-bottom: none !important;*/
	/*	visibility: hidden;*/
	/*}*/
	/*.s_tab_inner {*/
	/*	display: none !important;*/
	/*}*/
	
	
	/*~~~[1-3]:开启工具栏、站点栏~~~*/
	
  .head_nums_cont_outer.OP_LOG
  {
    margin-top: 30px;
  }
	
	
	/*[选项2]：是否使用背景*/
	/*[2-1]:不使用自定背景*/
	/*[2-2]:使用自定背景*/
  body, #head, .s_search
  {
		/*background-image: url('https://s1.ax1x.com/2020/08/21/dNT7Js.jpg');*/
		/*设置图片覆盖方式，cover为一张图盖全页，contain为多张图盖全页*/
    background-size: cover;
		/*设置图片是否重复*/
    background-repeat: no-repeat;
		/*设置图片不随界面滚动*/
    background-attachment: fixed;
  }
}

@-moz-document regexp("https://www.baidu.com/s?[\\S]+")
{
	/*搜索内容部分*/
	/*①左侧搜索结果居中显示*/
	/*设置宽度为全屏*/
  .container_new.container_l, .line, #container, .list-wraper
  {
    width: 100% !important;
  }

  .sam_newgrid.container_l.container_xl, .page-main-inner, .page-main-inner.line
  {
    margin: 0 !important;
    width: 100% !important;
  }

  #container.sam_newgrid #content_left
  {
    width: 560px;
  }

  .page-main-inner
  {
    margin: 0 auto !important;
  }

  .list-wraper
  {
    width: 560px !important;
    margin: 0 auto !important;
    float: none !important;
  }
	
	/*取消左边界*/
  #container.sam_newgrid
  {
    margin-left: 0px !important;
    margin-top: 20px !important;
    padding-left: 0px !important;
  }
	
	/*清空搜索结果原有样式*/
  #content_left
  {
    padding: 0px !important;
    margin: 0 auto!important;
    float: none;
    transform: translate(0,20px);
  }
	
	/*搜索结果样式自动居中*/
  #content_left, .dl
  {
    margin: 0px auto;
		/*补偿由于padding造成的位置偏移*/
    position: relative;
    right: 23px !important;
  }
	
	/*②美化搜索结果*/
	/*调整卡片样式*/
	/*词条建议：北京疫情/国内图床推荐*/
  [srcid]
  {
    border: 1px solid #EEE !important;
    line-height: 1.65 !important;
    background: #fff !important;
    border-radius: 3px !important;
    padding: 25px !important;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    width: 560px !important;
  }
	
	/*视频搜索美化*/
  .video_list.video_short
  {
    border: 1px solid #EEE;
    line-height: 1.65;
    background: #fff;
    border-radius: 3px;
    padding: 25px !important;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
		/*width:560px!important;*/;
  }
	
	
	/*心灵想要大声呼喊等词条，视频在线播放卡片调整*/
  .c-container.aladdin__2AELi
  {
    padding: 0px !important;
  }

  .dis-overflow-none.c-span5.c-span-last
  {
    border-radius: 3px;
  }
	
	/*在线播放按钮背景调整*/
  .c-new-btn
  {
    background-color: #e9e9e9 !important;
  }
	
	/*搜索报错位置居中*/
  .content_none
  {
    width: 500px;
    margin: 0 auto;
  }
	
	/*新闻置顶强制等宽*/
  .xpath-log
  {
    width: 560px !important;
  }

  .c-group-wrapper
  {
    margin: inherit;
  }
	
	/*去除超链接样式*/
  a, a em, .chapter-title,.ec-pc_title h2
  {
    text-decoration: none !important;
  }
	
	/*标题栏距离拉大*/
  .t, .c-title
  {
    padding-bottom: 5px !important;
    width: 560px !important;
  }
	
	/*删除特定卡片边界，如百科，App下载等*/
  .xpath-log .result.c-container, #op_wiseapp
  {
    border: none;
    box-shadow: none;
    padding: 0px !important;
  }
	
	/*价格仅供参考条目居中*/
  .hint_common_restop
  {
    margin: 0 auto 6px !important;
    text-align: center;
    background: rgba(255, 255, 255, .5);
    border-radius: 10px;
  }
	
	/*标题栏选中动画，该部分代码来自于@CWorld的Pure Baidu，略作增添*/
	/*标题栏强制统一样式*/
  #wrapper #content_left .c-container h3 a, #wrapper #content_left .c-container[tpl=soft] .op-soft-title a, #wrapper #content_left .c-container[tpl=soft] .op-soft-title a em, #wrapper #content_left .result h3 a em, .t.c-gap-bottom-small a, .t a, .op-timeliness-title a, .t.c-group-title a, .op_sp_realtime_group_title a, .c-border a, .c-row a, .video_small_intro a, .t.c-gap-bottom-small a em, .t a em, .op-timeliness-title a em, .t.c-group-title a em, .op_sp_realtime_group_title a em, .c-border a em, .c-row a em, .video_small_intro a em, .c-title-text, .c-title-text em, .news-title_1YtI1 em,.ec-pc_title h2
  {
    color: #4e6ef2 !important;
    font-weight: 425 !important;
  }
	
	/*强制标题字体大小*/
  .t a, .t a em, .c-title-text, .c-title-text em,.ec-pc_title h2
  {
    font-size: 16px;
  }
	
	/*回溯官方图标样式*/
  .normal-gf-icon, .read-btn.mt, .OP_LOG_LINK.c-text.c-text-public.c-text-mult.c-gap-left-small
  {
    font-size: 12px !important;
    background-color: #e9e9e9;
    border-radius: 3px !important;
  }
	
	/*软件下载删除*/
  table[srcid]
  {
    display: none!important;
  }
	
	/*设置原本位置*/
  #wrapper #content_left .op-soft-title a, #wrapper #content_left .result h3 a, .t.c-gap-bottom-small a, .t a, .op-timeliness-title a, .video_small_intro a
  {
    position: relative;
  }
	
	/*设置伪类*/
  #wrapper #content_left .op-soft-title a:after, #wrapper #content_left .result h3 a:after, .t.c-gap-bottom-small a:after, .t a:after, .op-timeliness-title a:after, .video_small_intro a:after
  {
    content: "";
    position: absolute;
    border-bottom: 2px solid #1AB7FD;
    border-radius: 2px !important;
    bottom: -2px;
		/*向左偏移隐藏*/
    left: 100%;
    width: 0;
    -webkit-transition: width 350ms, left 350ms;
    -moz-transition: width 350ms, left 350ms;
    transition: width 350ms, left 350ms;
  }
	
	/*设置已访问链接的伪类动画目标*/
  #wrapper #content_left .op-soft-title a:visited:hover:after, #wrapper #content_left .result h3 a:visited:hover:after, .t.c-gap-bottom-small a:visited:hover:after, .t a:visited:hover:after, .op-timeliness-title a:visited:hover:after, .video_small_intro a:visited:hover:after
  {
    left: 0;
    width: 100%;
    -webkit-transition: width 350ms;
    -moz-transition: width 350mst;
    transition: width 350ms;
  }
	
	/*设置未访问链接的伪类动画目标*/
  #wrapper #content_left .op-soft-title a:hover:after, #wrapper #content_left .result h3 a:hover:after, .t.c-gap-bottom-small a:hover:after, .t a:hover:after, .op-timeliness-title a:hover:after, .video_small_intro a:hover:after
  {
    left: 0;
    width: 100%;
    -webkit-transition: width 350ms;
    -moz-transition: width 350ms;
    transition: width 350ms;
  }
	
	/*删除c-border(诸如词典、汉语)自带的边界样式*/
  .c-border
  {
    width: 100%;
    padding: 0px;
    border: none !important;
    box-shadow: none !important;
  }

  #content_left .c-border.ec-pl-container
  {
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14) !important;
  }
	
	/*百度经验样式恢复*/
  .c-result-content article
  {
    padding: 0px;
    margin: 0 auto !important;
    border: none !important;
    box-shadow: none !important;
  }
	
	/*恢复类梅西词条的内容样式*/
  .c-line-clamp3
  {
    color: black !important;
    font-weight: 400 !important;
  }
	
	/*搜索结果悬浮动态*/
  [srcid]:hover
  {
    transition: all 500ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    box-shadow: rgba(0, 0, 0, 0.15) 0px 3px 6px, rgba(0, 0, 0, 0.117647) 0px 1px 4px !important;
  }
	
	/*百度搜图悬浮放大取消*/
  .op-img-address-hoverview
  {
    display: none !important;
  }
	
	/*细节部分修改*/
	/*底部推荐搜索隐藏*/
  #rs
  {
    display: none !important;
  }

  [tpl="app/rs"]
  {
    display: none!important;
    transform: translate(0,20px);
  }

  [tpl="app/page"]
  {
    margin-bottom: 40px;
    transform: translate(0,15px);
  }

  #foot
  {
    display: none !important;
  }
	
	/*经验图重置位置，包括取消原本margin*/
  .wenda-abstract-video-wrap, .wenda-abstract-img-wrap
  {
    margin: 0 !important;
  }
	
	/*小图加圆角*/
  .c-img1, .c-img2, .c-img3, .c-img4, .c-img5, .c-img6, .op-short-video-pc-img.c-img.c-img6, .c-img-img, .c-img.c-img4, .c-gap-bottom-small a img
  {
    border-radius: 5px;
  }
	
	/*百度百科图片遮罩圆角*/
  .op-bk-polysemy-logo
  {
    border-radius: 0px 0px 5px 5px;
  }
	
	/*漫画栏小图标圆角*/
  .c-span4 span
  {
    border-radius: 0px 0px 5px 5px !important;
  }

  .c-numberset.c-gap-top a
  {
    border-radius: 4px !important;
  }
	
	/*小图标圆角*/
  .title-label.c-text-public.c-text-mult.c-gap-left-small, .read-btn.mt, .new-label, .c-img.op-timeliness-img
  {
    border-radius: 5px !important;
  }

  .chapter-title-wrap .new-label
  {
    display: none !important;
  }

  .video_list:first-child
  {
    margin-top: 20px;
  }
	
	/*③Ad-Block 屏蔽竞价排名广告*/
  div[style*="visibility:visible"]
  {
    display: none !important;
    visibility: hidden !important;
    opacity: 0;
    height: 0px;
    overflow: hidden;
  }

  body>div[style*='position: absolute;'],
	body>div[style*='position: fixed;'],
	#contnet_left>div[id="10"]~div[id="1"],
	#contnet_left>div[id="20"]~div[id="1"],
	#contnet_left>div[id="40"]~div[id="1"],
	#contnet_left>div[id="60"]~div[id="1"],
	#contnet_left>div[id="80"]~div[id="1"],
	#contnet_left>div[id="100"]~div[id="1"],
	#contnet_left>div[id="3001"],
	#contnet_left>div[id="3002"],
	#contnet_left>div[id="3003"],
	#contnet_left>div[id="3004"]
  {
    display: none !important;
  }

  #content_left>div[style*="display:block !important"]
  {
    position: absolute !important;
    top: -5000px !important;
  }
	
	/*④识图搜索界面*/
	/*清除搜图框样式*/
  .soutu-url-error
  {
    margin-top: 13px !important;
  }

  .soutu-layer .soutu-url-btn
  {
    height: 31px !important;
    width: 31px !important;
    border-radius: 50px !important;
    background: rgba(0, 0, 0, 0) !important;
    text-indent: -9999999999px;
    margin-top: 7px !important;
    margin-left: 10px !important;
    background-image: url(data:image/svg+xml;base64,) !important;
    background-repeat: no-repeat;
  }

  .soutu-env-new .soutu-layer #soutu-url-kw
  {
    border: none !important;
  }

  .soutu-url-wrap
  {
    width: 558px !important;
    margin-top: 5px;
    height: 36px !important;
    border-radius: 3px !important;
    background: white !important;
  }

  .soutu-url-kw
  {
    width: 484px !important;
    height: 38px !important;
    border: none !important;
    padding: 12px 0px 10px 10px !important;
    border-radius: 3px;
  }
	
	/*重置拖拽搜图样式*/
  .soutu-state-normal
  {
    width: 510px;
  }

  .soutu-close.c-icon.soutu-close-new
  {
    position: relative;
    top: -25px;
    left: 533px;
  }
	
	/*⑤百度图片搜索界面*/
  .cms-area-hidden
  {
    width: 100% !important;
  }

  .s_search
  {
    height: 70px !important;
  }

  .s_nav
  {
    width: 100% !important;
    left: 0px !important;
    margin: 0 auto !important;
  }

  .s_logo
  {
    float: none;
    position: relative;
    left: 300px !important;
  }

  .searchform
  {
    width: 800px !important;
    margin: 0 auto !important;
    padding-left: 250px !important;
    padding-top: 15px !important;
  }

  .sttb
  {
    display: none !important;
  }

  #imgMainSearch
  {
    width: 800px !important;
    margin: 0px auto !important;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
  }

  #bdpcImgTopTab
  {
    width: 800px !important;
    height: 40px;
    margin: 10px auto !important;
    -webkit-animation-duration: 1s;
    animation-duration: 1s;
    -webkit-animation-fill-mode: both;
    animation-fill-mode: both;
    -webkit-animation-name: fadeInUpMini;
    animation-name: fadeInUpMini;
    -webkit-animation-delay: 0.3s;
    animation-delay: 0.3s;
  }

  #topInfoBar
  {
    width: 800px !important;
    border-radius: 10px;
    margin: 10px auto;
    background: rgba(255, 255, 255, 0.8) !important;
  }
	
	/*授时*/
  .op-beijingtime-background
  {
    width: 100%;
  }

  .c-tabs-nav
  {
    border-bottom: none;
  }
	/*⑥滚动条等控件伪类更改样式*/
  ::-webkit-scrollbar
  {
    width: 6px;
    height: 10px;
    background-color: rgba(0, 0, 0, 0);
  }

  ::-webkit-scrollbar-track
  {
    background-color: rgba(0, 0, 0, 0.1);
  }

  ::-webkit-scrollbar-thumb
  {
    border-radius: 3px;
    background-color: rgba(0, 0, 0, 0.2);
    transition: all 0.4s ease;
    -moz-transition: all 0.4s ease;
		/* Firefox 4 */
    -webkit-transition: all 0.4s ease;
		/* Safari 和 Chrome */
    -o-transition: all 0.4s ease;
		/* Opera */;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    border-radius: 3px;
    background-color: rgba(0, 0, 0, 0.4);
    transition: all 0.4s ease;
    -moz-transition: all 0.4s ease;
		/* Firefox 4 */
    -webkit-transition: all 0.4s ease;
		/* Safari 和 Chrome */
    -o-transition: all 0.4s ease;
		/* Opera */;
  }
	
	/*⑦动画*/
  .animated
  {
    -webkit-animation-duration: 1s;
    animation-duration: 1s;
    animation-fill-mode: both;
  }
	
	@-webkit-keyframes fadeInUp
  {
    from {
			opacity: 0;
    -webkit-transform: translate3d(0, 100%, 0);
    transform: translate3d(0, 100%, 0);
  }

  to
  {
    opacity: 1;
    -webkit-transform: none;
    transform: none;
  }
}

@keyframes fadeInUp
{
  from
  {
    opacity: 0;
    -webkit-transform: translate3d(0, 100%, 0);
    transform: translate3d(0, 100%, 0);
  }

  to
  {
    opacity: 1;
    -webkit-transform: none;
    transform: none;
  }
}

.fadeInUp
{
  -webkit-animation-name: fadeInUp;
  animation-name: fadeInUp;
}

@-webkit-keyframes fadeInUpMini
{
  from
  {
    opacity: 0;
    -webkit-transform: translate3d(0, 50%, 0);
    transform: translate3d(0, 50%, 0);
  }

  to
  {
    opacity: 1;
    -webkit-transform: none;
    transform: none;
  }
}

@keyframes fadeInUpMini
{
  from
  {
    opacity: 0;
    height: 20px;
    -webkit-transform: translate3d(0, 50%, 0);
    transform: translate3d(0, 50%, 0);
  }

  to
  {
    opacity: 1;
    height: 38px;
    -webkit-transform: none;
    transform: none;
  }
}

.fadeInUpMini
{
  -webkit-animation-name: fadeInUpMini;
  animation-name: fadeInUpMini;
}

@-webkit-keyframes bounceInRight
{
  from, 60%, 75%, 90%, to
  {
    -webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
    animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
  }

  from
  {
    opacity: 0;
    -webkit-transform: translate3d(3000px, 0, 0);
    transform: translate3d(3000px, 0, 0);
  }

  60%
  {
    opacity: 1;
    -webkit-transform: translate3d(-25px, 0, 0);
    transform: translate3d(-25px, 0, 0);
  }

  75%
  {
    -webkit-transform: translate3d(10px, 0, 0);
    transform: translate3d(10px, 0, 0);
  }

  90%
  {
    -webkit-transform: translate3d(-5px, 0, 0);
    transform: translate3d(-5px, 0, 0);
  }

  to
  {
    -webkit-transform: none;
    transform: none;
  }
}

@keyframes bounceInRight
{
  from, 60%, 75%, 90%, to
  {
    -webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
    animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
  }

  from
  {
    opacity: 0;
    -webkit-transform: translate3d(3000px, 0, 0);
    transform: translate3d(3000px, 0, 0);
  }

  60%
  {
    opacity: 1;
    -webkit-transform: translate3d(-25px, 0, 0);
    transform: translate3d(-25px, 0, 0);
  }

  75%
  {
    -webkit-transform: translate3d(10px, 0, 0);
    transform: translate3d(10px, 0, 0);
  }

  90%
  {
    -webkit-transform: translate3d(-5px, 0, 0);
    transform: translate3d(-5px, 0, 0);
  }

  to
