css:
@-moz-document regexp(".*appliedenergistics\\.github\\.io\\/(?!ae[1-2]-site-archive).*") {
.main-container .container {
   padding: 1.25rem;
}
@media screen and (max-width: 1023px) {
   .main-container .container {
      padding: 0.75rem;
   }
}

.block {
   margin-top: 1.5rem;
}

.navbar {
   position: fixed;
   inset: 0 0 auto 0;
}
.navbar-end {
   flex-grow: 1;
}
.navbar .DocSearch {
   max-width: 20rem;
}
@media screen and (min-width: 1216px) and (max-width: 1407px) {
   .navbar .navbar-item:first-child {
      flex-grow: 1;
      justify-content: end;
   }
   .navbar .DocSearch {
      min-width: unset;
      width: 100%;
   }
}
@media screen and (min-width: 1024px) and (max-width: 1215px) {
   .navbar .navbar-item:first-child {
      flex-grow: 1;
      justify-content: end;
   }
   .navbar .DocSearch {
      min-width: unset;
      width: 100%;
   }
   .navbar .buttons span:first-child:not(:last-child) {
      margin-left: unset;
      margin-right: unset;
   }
   .navbar .buttons span:not(.icon) {
      display: none;
   }
}
@media screen and (max-width: 1023px) {
   .navbar .DocSearch {
      margin-left: 0;
   }
}

.main-container {
   margin-top: 3.25rem;
   align-items: start;
}
.main-container > * {
   overscroll-behavior: contain;
}
.main-container aside.menu {
   position: fixed;
   grid-column: 1/2;
   inset: 3.65rem auto 0 0;
   max-width: 14.25rem;
   padding: 1.25rem;
   overflow-y: scroll;
}
.main-container aside.menu::-webkit-scrollbar {
   width: 5px;
   height: 5px;
}
.main-container aside.menu::-webkit-scrollbar-button {
   width: 0px;
   height: 0px;
}
.main-container aside.menu::-webkit-scrollbar-thumb {
   background: #e1e1e1;
   border: 0px none #ffffff;
   border-radius: 50px;
}
.main-container aside.menu::-webkit-scrollbar-thumb:hover {
   background: #ffffff;
}
.main-container aside.menu::-webkit-scrollbar-thumb:active {
   background: #000000;
}
.main-container aside.menu::-webkit-scrollbar-track {
   background: #666666;
   border: 0px none #ffffff;
   border-radius: 50px;
}
.main-container aside.menu::-webkit-scrollbar-track:hover {
   background: #666666;
}
.main-container aside.menu::-webkit-scrollbar-track:active {
   background: #333333;
}
.main-container aside.menu::-webkit-scrollbar-corner {
   background: transparent;
}
.main-container main {
   grid-column: 2/3;
}

:root {
   --color-background: #36393f;
   --color-background-secondary: #2f3136;
   --color-background-navbar: #202225;
   --color-background-menu: #2f3136;
   --color-background-box: #2f3136;
   --color-box-border: #202225;
   --color-navbar-shadow: #2d2f32;
   --color-text-primary: #dcddd4;
   --color-text-secondary: #fff;
   --color-text-active: #00AFF4;
   --color-text-menu: #8e9297;
   --color-text-menu-hover: #dcddde;
   --color-text-vanilla: #404040;
   --color-menu-background-hover: #4f545c29;
   --color-menu-background-active: #4f545c52;
   --color-text-navbar: #dcddd4;

   color-scheme: dark;
}

html {
   background-color: var(--color-background);
}

body {
   color: var(--color-text-primary);
}

a {
   color: var(--color-text-active);
}
a:hover {
   color: var(--color-text-active);
   text-decoration: underline;
}

.navbar {
   background-color: var(--color-background-menu);
}
.navbar.has-shadow {
   box-shadow: 0 2px 0 0 var(--color-navbar-shadow);
}

.navbar-item,
.navbar-link {
   color: var(--color-text-navbar);
}

a.navbar-item:hover,
a.navbar-item:focus,
a.navbar-link:hover,
a.navbar-link:focus {
   color: var(--color-text-secondary);
   background-color: var(--color-menu-background-active);
   text-decoration: none;
}

.navbar-brand .navbar-item img {
   content: url("https://appliedenergistics.github.io/assets/logo/logo_05.png");
}

@media screen and (max-width: 1023px) {
   .navbar {
      background-color: var(--color-background-navbar);
   }

   .navbar-burger {
      color: var(--color-text-primary);
   }
   .navbar-burger:hover {
      color: var(--color-text-secondary);
      background-color: var(--color-menu-background-hover);
   }
}
.menu,
.navbar-menu {
   background-color: var(--color-background-menu);
}

.menu-label {
   color: var(--color-text-primary);
}

.menu-list a {
   color: var(--color-text-menu);
}
.menu-list a:hover {
   color: var(--color-text-menu-hover);
   background-color: var(--color-menu-background-hover);
}
.menu-list a.is-active {
   background-color: var(--color-menu-background-active);
}

.box {
   background-color: var(--color-background-box);
   color: var(--color-text-primary);
   border: 1px solid var(--color-box-border);
}

code,
pre {
   background-color: var(--color-background-secondary);
   color: var(--color-text-secondary);
   border-radius: 3px;
}

.content table thead td,
.content table thead th {
   color: var(--color-text-primary);
}

.title {
   color: var(--color-text-secondary);
}

.content h1,
.content h2,
.content h3,
.content h4,
.content h5,
.content h6 {
   color: var(--color-text-secondary);
}

strong {
   color: var(--color-text-primary)
}

[class^="recipe_recipeBoxLayout"] > strong {
   color: var( --color-text-vanilla);
   font-size: 1.3rem;
}
}
