css:
@-moz-document domain("rumble.com") {
:root{
    --bgcol0:   #161616;
    --color0:   #eaeaea;
    --bgcol1:   #222;
    --color1:   #eaeaea;
    --rumble-green:   #74a441;
    
    --header-bg:  var(--bgcol0);
    --input-bgcol:   #fff;
    --input-color:   #1c1c1c;
    --button-border:    var(--rumble-green);
    --button-bg:    none;
    
