}

::selection {
    color:#000;
    background-color: var(--rumble-green);
}

html,
body {
    background-color: var(--bgcol0); /* default if can't do gradients */
    
    --bgc2: #131313;
    /*ZigZag background by eCSSpert*/
    background:
    linear-gradient(135deg, var(--bgc2) 25%, transparent 25%) -25px 0,
    linear-gradient(225deg, var(--bgc2) 25%, transparent 25%) -25px 0,
    linear-gradient(315deg, var(--bgc2) 25%, transparent 25%),
    linear-gradient(45deg, var(--bgc2) 25%, transparent 25%);
    background-size: 50px 50px;
    background-color: #111;
    
    color: var(--color0);
}

button:hover,
a:hover {
    opacity: 1;
}

/* Allow window to be filled */
.constrained {
    max-width: 88%;
}

input,
select {
    background: var(--input-bgcol);
    color: var(--input-color);
    border-color: var(--color0);
    border-radius: 0;
}


/*HEADER*/

header {
    background-color: var(--header-bg) !important;
    border-bottom: none;
    box-shadow: 0 0 8px 0 var(--rumble-green);
    
    animation-name: light;
    animation-duration: 4s;
    animation-direction: alternate;
    animation-iteration-count: infinite;
    animation-timing-function: ease-out;
}

/* hide user rumbles (up-cummies) stats */
span.header-user-stat {
    height: 0;
    visibility: hidden;
}

/* menu button (three lines) */
.main-menu-open,
.main-menu-close {
    stroke: white;
}

/* header light flickering animation */
@keyframes light {
    from {box-shadow: 0 0 7px 0 var(--rumble-green);}
    to	 {box-shadow: 0 0 21px 0 #9f6;}
}


.header-logo {
	content:url("https://i.ibb.co/gT9Nd71/rumble-full-logo-white.png");
}

.header-search {
    border-radius: 1rem 3rem 1rem 3rem;
}
.header-search-select, .header-search-field{
    background: none;
    color: #1c1c1c;
}
.header-user-name{color: var(--rumble-green);}
/*HEADER*/
/*MAIN-PAGE*/
.module-title{
    color: var(--rumble-green);
}

/* Tabs */
.tabs {z-index: 0;}
/*.tab-container {margin-left: 0px;}*/
.tab-heading {
    background: #181818;
    color: #444;
    border-radius: .5rem .5rem 0 0;
    z-index: 0;
    transition: .15s;
}
a.tab-heading.module-title.tab-active {
    background: var(--bgcol1);
    color: var(--rumble-green);
    border: 0;
    box-shadow: 5px 5px 5px #111;
    height: 90%;
    z-index: 0;
    transition: .15s;
}

/* Only underline if click navigates to new page */
a.tab-heading.module-title {text-decoration: none;}
a.tab-heading.module-title.tab-active:hover {text-decoration: underline;}

.container,
.video-item {
    background: var(--bgcol1) ;
    border: 0;
    box-shadow: 7px 7px 8px #0009;
    z-index: 1;
}
.mediaList-link-more a,.promo-widget-link{
    background: var(--rumble-green);
    border-color: var(--rumble-green);
    border-radius: 6px;
    color: var(--input-color);
    box-shadow: 7px 7px 8px #111;
}

.mediaList-by-heading,.forum-post-meta,.promo-widget-link:hover,small{
    color: var(--color0);
    text-decoration: none;
}
/*MAIN-PAGE*/
/*ARTICLE-VIDEO*/

/* Rumbles vote count */
.rumbles-vote,.comment-count{
    color: var(--rumble-green);
}

.rumbles-vote-up, .rumbles-vote-up:hover {
    color: white;
    border-radius: 1rem 0 0 3rem;
    opacity: 1;
    
    background: linear-gradient(to bottom, #0000 50%, var(--rumble-green) 50%) top;
    background-size: 100% 200%;
	background-position: top;
    transition: ease-in .2s
}
.rumbles-vote-up.active, .rumbles-vote-up.active:hover {
    background-color: var(--rumble-green); /* default */
    opacity: 1;
    
    background: linear-gradient(to top, var(--rumble-green) 50%, #0000 50%) bottom;
    background-size: 100% 200%;
    background-position: bottom;
    transition: ease-out .2s;
}

.rumbles-vote-down, .rumbles-vote-down:hover {
	color: white;
    border-radius: 0 3rem 1rem 0;
    opacity: 1;
    
    background: linear-gradient(to top, #0000 50%, #d44 50%) bottom;
    background-size: 100% 200%;
    background-position: bottom;
    transition: ease-in .2s;
}
.rumbles-vote-down.active, .rumbles-vote-down.active:hover {
    opacity: 1;
    
    background: linear-gradient(to bottom, #d44 50%, #0000 50%) top;
    background-size: 100% 200%;
    background-position: top;
    transition: ease-out .2s;
}
/* END Rumbles Vote */

.media-video-action{
    background: none;
    border: 1px solid var(--rumble-green);
}
svg.user-image--icon{
    stroke: var(--rumble-green);
}

.media-icon-license, .media-icon-embed {
    stroke: var(--color0);
}

svg.user-image--icon{
    background: none;
    border-color: var(--rumble-green);
}

i.user-image--letter { background: var(--rumble-green);}

/* Subscribe button highlighted while unsubbed - owb */
.round-button.media-subscribe[data-action="subscribe"]{
    background: var(--rumble-green);
    border-color: var(--button-border);
    color: black;
    max-width: 12rem;
    transition: ease-in .1s;
}
/* subscriber count on button */
.round-button.media-subscribe[data-action="subscribe"] > span.subscribe-button-count {
    color: var(--color0);
}

.round-button.media-subscribe[data-action="unsubscribe"] {
    max-width: 14rem;
    transition: ease-in .1s;
    overflow: hidden;
}


.round-button{
    background: var(--button-bg);
    border: 1px solid var(--button-border);
    border-radius: 1rem 2rem 1rem 2rem;
    color: var(--rumble-green);
}

.round-button:hover{
    opacity: 1;
}

button.round-button.locals-button {
    border: 1px solid #e83646;
    background: #e83646;
    color: var(--color0);
}


/* VIDEO */
h1.h1 {
    margin: 0px 0 6px;
    font-size: 16pt;
    font-weight: bold;
    color: var(--rumble-green);
}

div.media-under-title {
    margin: 0 0 6px;
}

video {
    border-radius: 2px;
}

div.streamed-on {
    margin: 12px 0;
}

div.video-footer-buttons {
    margin: 12px 0 12px;
}

div.media-description {
    padding: 1px 20px;
}

p.media-description a {
    color: var(--rumble-green);
}

/* !!! FIX ME !!!
/* COMMENTS*/ 
.comments-create-textarea,
textarea#chat-message-text-input {
    margin-left: 4rem;
    max-width: 94%;
    border-radius: 1rem 3rem 1rem 3rem;
    color: var(--input-color);
}

.comments.comments-1 {
    padding: 1rem;
    margin: 0;
}
.comments.comments-2{
    padding: 2rem 0 0 1rem;
    padding-right: 0rem;
    margin-right: 0rem;
    margin-left: 4rem;
    border-left: 10px;
}

.comment-item.comment-item-first {
    margin: 0;
    padding-left: 0rem;
    padding-top: 0rem;
}

.comments-author-image {
    margin-left: 0;
}

div.comments-meta {
    margin: .25rem 0 1.5rem 4rem;
}

.comment-actions,
.comments-action-reply,
.comments-action-delete {
    float:left;
    padding-top: 0.2rem;
    padding-right: .5rem;
    margin: 0;
    text-align: left;
    font-weight:bold;
}

p.comment-text {
    padding-bottom: .5rem;
}

/* play button */
.bigPlayUIInner {
    background: #1c1c1c !important;
    border-radius: 18px !important;
}


svg.RumbleElm.RumbleSVG-bplay{
	width: 43px !important;
    height: 45px !important;
}

svg.RumbleElm.RumbleSVG-bplay path {
    fill: var(--rumble-green);
    stroke-linejoin: round;
    stroke-width: 0px;
    
    d: path(var(--rumble-logo));
}

div.bigPlayUI:hover div.bigPlayUIInner  {
    background: var(--rumble-green) !important;
    transition: 0.3s;
}
div.bigPlayUI:hover svg.RumbleElm.RumbleSVG-bplay path {
    fill: var(--color0);
    transition: 0.3s;
}


/*HELP*/
.book-body,#book-search-input{
    background: none;
    color: var(--rumble-green);
}
.markdown-section h1,.markdown-section h2,.book-summary ul.summary li a,#book-search-results .search-results .has-results .search-results-item a{
    color: var(--rumble-green);
}
.markdown-section,.book-summary ul.summary li ul li a,#book-search-results .search-results .has-results .search-results-item{
    color: var(--color0);
}
.book-summary{
    background: none;
    border-right: 1px solid var(--rumble-green);
}
.markdown-section table thead tr,.markdown-section table tbody td{
    background: none;
}
.markdown-section table thead th{
    color: var(--rumble-green);
}
.markdown-section code{
    background: none;
    color: purple;
}
.markdown-section pre{
    background: none;
    border: 1px solid var(--rumble-green);
}

/*CORPORATE*/
.header .main-nav a,.corp-section .section-titles h3,.solution-lineup .third-section h4,.brand-box .stats .stats-wrapin .big{
    color: var(--rumble-green);
}
.header .main-nav a:hover,.brand-box .content p,.brand-box .stats .stats-wrapin .small{
    color: var(--color0);
}
.solution-lineup,.solution-lineup .check-list .checkmark,.brand-box{
    background: none;
}
.solution-lineup .check-list .container:hover input ~ .checkmark{
    background: var(--rumble-green);
}
.timeliny .timeliny-dot::after{
    background: none;
    border: 1px solid var(--rumble-green);
    color: var(--color0);
}

/*VIDEO*/
.listing-header--white-bg{
    background: none;
}

/* make all video items go dim when one is being hovered over */
div[style="flex: 1"]:hover > ol > li.video-listing-entry > article,
.tabs:hover li.mediaList-item,
ul.mediaList-list.container:hover li.mediaList-item {
    opacity: .4;
    transition: .3s;
}
/* make hovered over video item full brightness */
article.video-item:hover,
li.mediaList-item:hover {
    opacity: 1 !important;
    background: var(--bgcol1);
}
/* smooth out the reset to unhovered */
div[style="flex: 1"] > ol > li.video-listing-entry > article,
li.mediaList-item {
    opacity: 1;
    transition: .3s;
}

/* remove border acting as separator line in tab box */
li.mediaList-item { border-bottom: none;}


.tuner-box--section,.paginator--link{
    background: none;
}

.tuner-box--section-header{
    color: var(--rumble-green);
}
.tuner-box--link--current,a.tuner-box--link:hover,.paginator--link--current,.paginator--link:hover{
    background: var(--rumble-green);
    color: var(--input-color) !important;
}
.tuner-box--link{
    color: var(--color0);
}
.paginator--ul{
    color: var(--color0);
    border-radius: 0;
}

/*FOOTER*/
footer.footer {
    background: var(--rumble-green);
}


footer div.constrained *,
.footer-terms-link.divider::after {
    color: white;
}

a.footer-nav-link {
    font-weight: bold;
}

a.footer-nav-link:hover,
a.footer-terms-link:hover {
    color: white;
    text-decoration: underline;
}

/*TERMS PAGE*/
.legal-nav--a{
    background: none;
    border: 1px solid var(--rumble-green);
}
.legal-nav--a:hover{
    color: var(--input-color);
}

}
