/* AGENT_SHEET */
/*
 * Google Image Search - Aperture Science Theme
 *
 * Author      : Valacar
 * Editor      : pl0x
 */

@-moz-document
  url(http://images.google.com/),
  url-prefix(http://www.google.com/imghp),
  url-prefix(http://images.google.com/imghp),
  url-prefix(http://images.google.com/images),
  url-prefix(http://images.google.com/imgres),

  url(http://images.google.co.uk/),
  url-prefix(http://www.google.co.uk/imghp),
  url-prefix(http://images.google.co.uk/imghp),
  url-prefix(http://images.google.co.uk/images),
  url-prefix(http://images.google.co.uk/imgres),

  url(http://images.google.ca/),
  url-prefix(http://www.google.ca/imghp),
  url-prefix(http://images.google.ca/imghp),
  url-prefix(http://images.google.ca/images),
  url-prefix(http://images.google.ca/imgres)

{

body {
  background: #262626 url("http://media.moddb.com/images/members/1/245/244331/Aperature_Science_Glow_portal_by_tehboriz.jpg") repeat-x fixed !important;
  color: #fff !important;
}

/* default text color */
div, td { color: #fff !important; }

/* disable dashed focus outline */
:focus { outline: none !important; }

/* reset web form colors */
input *, textarea *, .aAutoComplete {
  background-color: -moz-Field !important;
  color: -moz-fieldtext !important;
}



/* Google bar "menu", Google Suggest, and SafeSearch option menu
----------------------------------------------------------------*/
#gbi, .gac_m, #ss-box, #sc-dropdown {
  background: #000 !important;
  border-color: #444 !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}

a.gb2:hover,
.gac_b .gac_c,
.gac_b .gac_d,
#ss-box a:hover {
  background: #444 !important;
}

.gbd { border-color: #444 !important; }
.gbh { border: none !important; }

.gac_a > .gac_c { color: #fff !important; }

.gac_d, .gac_e { color: rgba(255,255,255,0.5) !important; }

/* ad URL */
.gac_h { color: #ccc !important; }



/* Search results
-----------------*/
.ts img, #thumbnail > img {
  border: 1px solid transparent !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}

#rptgl span, a.gb1, a.gb2, a.gb3 {
  color: #ccc !important;
}

/* Options popout */
#rpsp.rpop, #sc-block .sc {
  background: rgba(0,0,0,0.4) !important;
  border-color: #333 !important;
  border-bottom: 1px solid #333 !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}

/* "Specific color" swatches */
#sc-block .sc a {
  border-color: #777 !important;
  border-style: outset !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}

/* image size and file format line */
.std > .f {
  color: #bbb !important;
}

/* web site line */
.std > .a > cite {
  color: #888 !important;
}



/* Front page
-------------*/
/* thumbnail images */
.ts div[style*="url("] {
  border: 1px solid #262626 !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}



/* Frame
--------*/
/* top bar */
div.std[style*="background"] {
  background: #111 !important;
}

/* frame separator */
#separator {
  background: #000 !important;
}

/* frame separator border */
#outer-separator {
  border-color: #222 !important;
}

/* description */
#details > p {
  color: #ccc !important;
}

/* web site */
#details > p > span[style^="color: green"] {
  color: #888 !important;
}

/* image size and file format line */
#details > p:nth-child(2) {
  color: #bbb !important;
}



/* Bars
-------*/
/* results bar */
.t { background: #353535 url(data:image/png;base64,) repeat-x !important; }

/* bottom bar (after pager) */
table.ft.t.bb.bt {
  background: transparent !important;
}
.bt, .bb { border: none  !important; }



/* Tweaks
---------*/
/* advanced search/preference links */
form[name="f"] td[width="25%"] > font, #ap { font-size: 66% !important; }



/* Replacement images
---------------------*/

/* big logo */
img#logo {
  margin-top: 6% !important;
  width: 0 !important;
  height: 256px !important;
  padding-left: 720px !important;
  background-image: url("http://i45.tinypic.com/qxm73a.jpg") !important;
}

/* small logo */
#sft .tc > a > img {
  width: 0 !important;
  height: 80px !important;
  padding-left: 80px !important;
  background-image: url("http://i48.tinypic.com/xfs3za.png") !important;
}

/* mini logo */
img[src="/images/isr_g.png"] {
  width: 0 !important;
  height: 80px !important;
  padding-left: 80px !important;
  background: url(http://i48.tinypic.com/xfs3za.jpg) no-repeat !important;
}

/* sprite sheet (used by pager, and "remove frame" button) */
.n div, img[src="/images/isr_c.gif"], #rptglbl  {
  background-image: url("data:image/png;base64,") !important;
}

/* "removed frame" button */
img[src="/images/isr_c.gif"] {
  width: 0 !important;
  height: 15px !important;
  padding-left: 15px !important;
  background-position: -104px -57px !important;
}

/* [-] button */
#rptgl.rpop #rptglbl {
  background-position: -105px -74px !important;
}

/* [+] button */
#rptgl #rptglbl {
  background-position: -91px -74px !important;
}

/* defaults for all links */
a:link { color: #FF8000!important; }
a:visited { color: #F5D0A9 !important; }
a:hover{ color: #ffffff !important; }

}
