css:
@-moz-document domain("stopthemingmy.app") {
body {
	color: #BCBCBC;
    background-color: #1C1C1C;
}

.title, .subtitle {
	color: #1C1C1C;
}

.intro {
    background-color: #5F875F;
}

.intro-icon {
    filter: brightness(0) saturate(100%) invert(8%) sepia(64%) saturate(1%) hue-rotate(341deg) brightness(96%) contrast(86%);
}

.notice {
	border-color: #87875F;
    background-color: #FFFFAF;
    color: #1C1C1C;
}

small {
	color: #6C6C6C;
}

a {
	color: #5F875F;
}

.join {
	background-color: #262626;
    color: #BCBCBC;
}

.join a, footer a {
	color: #5F875F;
}

.code-block, footer {
	background-color: #1C1C1C;
	color: #BCBCBC;
}
}
