css:
@-moz-document domain(onedrive.live.com) {
.od-BasePage, .ms-Callout, .od-ItemTile2-icon  {
  background-color: white;
}
.od-Panel {
  box-shadow: none;
  border-left: 1px solid #dadada;
}
.o365cs-base .o365sx-waffle {
  color: #FFFFFF;
  background-color: #2862a0;
}
#leftRegion, .od-SuiteNav div, .ms-Dialog-action button {
  background: #37006e !important;
  color: #FFFFFF;
}
/*text placeholder color*/
 .ms-suiteux-search-box input::placeholder {
  color: #edebe9 !important;
}
.ms-TextField-fieldGroup {
  border: 1px solid #37006e;
}
.od-TopBar-item .ms-Fabric div, .od-TopBar-item .ms-Fabric button {
  background-color: #deecf9;
}
/*dropdown image border*/
 #mectrl_currentAccount_picture {
  border: 1px solid white;
}
.od-BasePage-belowBar, .ms-Tile {
  background-color: #201f1ef7 !important;
}
html[dir=ltr] .isFluent_7a1db838 .metadata_7a1db838 {
  color: white;
}
.ms-Tile-link div {
  /*a bit of overkill but it worked*/
  color: white;
}
.isFluent_7a1db838 .signal_7a1db838 :first-child {
  color: white;
}
/* Removes drag here */
 .od-DragAndDropHint-file, .od-DragAndDropHint-text--grid {
  display: none;
}
/* Hiding other folders */
 .ms-FolderCover-content, .ms-FolderCover-front {
  display: none;
}
/* Change folder image */
 .ms-FolderCover-back {
  content: url(http://icons.iconarchive.com/icons/media-design/hydropro-v2/512/Folder-icon.png);
  height: 70px;
  width: 78px;
}
}
