css:
@namespace url(http://www.w3.org/1999/xhtml);

/****************************************************
 Created by TheEndless in an effort to help make the
 new fark.com a bit more bearable and easier on the
 eyes.
*****************************************************/

@-moz-document domain("fark.com") {

	body {
		font-family: Arial, Verdana, Helvetica !important;
	}

	#commentsArea {
		width: 100% !important;
		text-align: left !important;
	}

	#commentsArea BR {
		margin: -1px !important;
	}

	#commentsArea .ctableTF {
		margin: 0px !important;
		padding: 4px !important;
		padding-left: 185px !important;
		width: 100% !important;
		border: 0px !important;
		background-color: #e0e0e0 !important;
		color: #000000 !important;
	}

	#commentsArea .ctableTF a {
		color: #880000 !important;
	}

	#commentsArea .ctableTF a:visited {
		color: #000088 !important;
	}

	#commentsArea .ctable {
		margin: 0px !important;
		padding: 4px !important;
		padding-left: 185px !important;
		width: 100% !important;
		border: 0px !important;
		background-color: #eeeeee !important;
		color: #000000 !important;
	}

	#commentsArea .ctable a {
		color: #880000 !important;
	}

	#commentsArea .ctable a:visited {
		color: #000088 !important;
	}

	#commentsArea .cdate {
		position: absolute !important;
		left: 3px !important;
		padding-left: 2px !important;
		width: 150px !important;
		height: 14px !important;
		font-size: 10pt !important;
		text-align: left !important;
	}

	#commentsArea .ctext {
		font-family: Verdana, Arial, Helvetica !important;
		margin: 0px !important;
		padding: 2px !important;
		padding-left: 5px !important;
		padding-right: 5px !important;
		width: 99% !important;
		text-align: left !important;
	}
}
