css:
@-moz-document domain("roblox.com")
{
  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  } /*Ad removal */
  
  #header
  {
    background-color: #000000;
  }

   .dark-theme .notification-stream-header, 
.notification-stream-header.dark-theme
   {
    background-color: #000000;
   }

.dark-theme .rbx-tab .rbx-tab-heading
   {
    background-color: #000000
   }

.dark-theme .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading.active
   {
    background-color: #000000
   }

.dark-theme .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading
{
    background-color: #000000
}

.dark-theme .rbx-tabs-horizontal .nav-tabs
{
    background-color: #000000
}

.dark-theme .avatar-card-container
{
    background-color: #000000
}

.dark-theme .avatar-card-btns
{
    background-color: #000000
}

.dark-theme .friend-recommendations-section .friend-recommendations-content .friend-recommendations-container .friend-recommendations-list-item .friend-recommendation-item
{
    background-color: #000000
    /*Ropro UI thing only, for "people you may know"*/
}

.dark-theme .stack .stack-list .stack-row
{
    background-color: #000000
    /*private server page color*/
}

.dark-theme .text-link-secondary
{
    color: #ffffff
    /*text color*/
}

.dark-theme .text, .dark-theme .text-date-hint, .dark-theme .text-default, .dark-theme .text-description, .dark-theme .text-footer, .dark-theme .text-info, .dark-theme .text-label, .dark-theme .text-secondary, .dark-theme p
{
    color: #ffffff
}

.dark-theme .container-footer
{
    background-color: #000000
}

  .dark-theme .game-card-info .info-label
  {
    color: #000000
  }
  
  .navigation .notification-icon,
.rbx-highlight
  {
    background: #000000;
  }

  #chat-header
  {
    background: #000000;
  /*Party header*/
    ;
  }

  .slide-item-container-left,
.profile-avatar-right
  {
    background: #000000;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #000000 inset;
    ;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #000000 !important;
 /*Forum colors and messaging underline color*/
    ;
  }

  #TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor
  {
    display: none !important;
  }

  #navbar-search-input,
#navbar-search-btn
  {
    border: 1px solid #000000;
 /*Search border color*/
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://wallpaperaccess.com/full/3040363.jpg") !important;
 /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #000000, #000000);
  }

  #ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: rgba(12, 12, 12, 0.8) /*Left side navi*/;
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(255, 255, 255, 0.5);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(255, 255, 255, 0.5);
  }

  .dark-theme .people-list .friend .friend-link
  {
    color: #fff;
  }

  .dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img
  {
    background-color: #0c0c0c;
  }
}
