css:
@-moz-document domain("atlassian.net")
{    
    a.ghx-key.js-key-link {
        display: none; 
    }

    .ghx-issue-compact .ghx-row  {
        height: auto; 
    }

    div.ghx-row {
        height: auto;
    }
    div.ghx-issue-content {
        height: auto;
        white-space: normal
    }
    .ghx-backlog-card {
        height: auto;
    }

    .ghx-issue-compact .ghx-row {
        height: auto;
        white-space: normal;
    }

    .ghx-issue-compact .aui-label, .ghx-issue-compact .aui-lozenge {
        height: auto;
        white-space: normal;
    }

    .ghx-plan-main-fields {
        max-width: 80%;

    }

    .ghx-issue-compact .ghx-summary {
        max-width: 80%;
    }

    .ghx-type {
        display: none;
    }

    .ghx-type .items-spacer {
        display: none;
        height: auto !important;
    }

    .ghx-priority {
        display: none;
    }

    #ghx-controls {
        display: none !important;
    }

    .ghx-backlog .ghx-sprint-goal {
        font-size: 18px;
        font-weight: bold;
    }

    .ghx-agile .aui-lozenge.ghx-label {
        max-width: 80% !important;
    }     
}
