css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.webmasterworld.com/") {

body {
width: 100% !important;
height: auto !important;
overflow: auto !important;
margin: 0 !important;
background: #FFF !important;
color: #000 !important;
font: 13px/1 Arial !important;
}

a font {
color: #06C !important;
font-size: 13px !important;
}


table[bgcolor], td[bgcolor], tr[bgcolor] {
background: none !important;
color: #000 !important;
}

font[size="2"] {
color: #000 !important;
font: 12px Tahoma !important;
}

table[width="85%"] td {
height: auto !important;
padding: 10px 0 20px !important;
border-bottom: 1px dotted #CCC !important;
}

td[valign="top"] font[size="3"] a {
font: 18px Tahoma !important;
text-decoration: none !important;
}

table[width="85%"] table[width="100%"] td[valign="top"] font[size="1"] {
display: block;
margin-top: 5px;
padding: 10px 15px;
background: #CFE7B2;
font-size: 13px !important;
}

table[width="85%"] table[width="100%"] {
width: 95% !important;
margin: 0 10px 0 0 !important;
float: left !important;

}

a:hover, a:hover font {
background: none !important;
color: #87B745 !important;
}


}
