/* Define color of selection text and background (global) */
::selection { background:#FCF390; color:#000; }

/* Define color of selection text and background for <pre> and <code> */
pre::selection, code::selection { background:#61A7BD; color:#FFF; }

/* Define color of selection text and background for <a> links */
a::selection { background:#FCF390; color:#8A6F04; }

/* Define color of text and background of active links (clicked) */
a:active { color: #F86110 !important; text-shadow: 1px 1px 5px #F8C910 !important; text-decoration: underline !important; }

/* Define style of active input and texarea (e.g. Google search) */
input[type="text"]:focus, input[name="q"]:focus /* Fix for Google */, textarea:focus { border: 2px solid #E4BB70 !important; }

/* Replacing a system appearance of submit button (if not defined in CSS style of website) */
input[type="submit"] { border: 1px solid #CCCCCC; background-color: #f1f1f1; color: #1C94C4; font-weight: bold; }
input[type="submit"]:active { border: 1px solid #FBCB09; background-color: #FDF5CE; color: #D0743A; font-weight: bold; }
