css:
@-moz-document domain("jeuxvideo.com")
{
@media(min-width: 1000px) {
    .layout {
        --grid-template-columns: 1fr 55rem 20rem 1fr;
    }
}
@media(min-width: 1480px) {
    .layout {
        --grid-template-columns: 1fr 55rem 20rem 1fr;
    }
}
@media(prefers-color-scheme: dark) {
    :root {
        --jv-bg-color: #1c1b1b;
        --jv-block-bg-color: #2a2a2c;
        --jv-block-even-bg-color: #2f2f31;
        --jv-border-even-color: rgba(158, 159, 162, 0.12);
        --jv-border-color: rgba(158, 159, 162, 0.12);
        --jv-link-color: #b8b8ba;
        --jv-text-color: #e2e2e2;
        --jv-text-secondary: #818384;
        --jv-text-modo-color: #8ca73c;
        --jv-input-bg-color: #2a2a2c;
        --jv-bg-color-light: #36363a;
        --jv-text-hover-secondary: #c5765d;
        --jv-text-muted-color: #818384;
    }
    .topic-list {
        --jv-topic-folder1: #838385;
        --jv-topic-folder2: #a3463c;
        --jv-topic-pin-on: #8ca73c;
        --jv-topic-pin-off: #a3463c;
    }
    .topic-list .topic-title:visited {
        color: var(--jv-text-muted-color);
    }
    .btn-repondre-msg, .bloc-spoil-jv .txt-spoil {
        background-color: var(--jv-bg-color);
        color: var(--jv-text-color);
    }
    .bloc-spoil-jv .contenu-spoil, .bloc-spoil-jv.en-ligne .contenu-spoil, .btn-sondage-forum {
        background-color: var(--jv-bg-color-light);
        color: var(--jv-text-color);
    }
    .bloc-message-forum-anchor:target+.bloc-message-forum:not(.seen) {
        background-color: var(--jv-block-bg-color) !important;
        border-color: var(--jv-border-color) !important;
    }
    .bloc-sondage {
        background-color: var(--jv-block-bg-color);
    }
    .bloc-sondage .tab-choix .pourcent {
        background-color: var(--jv-block-even-bg-color);
    }
    .bloc-sondage .tab-choix .back-barre {
        background-color: var(--jv-bg-color-light);
    }
    .bloc-sondage .tab-choix .back-barre span {
        background-color: var(--jv-text-color);
    }
    .titre-head-bloc-sondage .titre-bloc, .bloc-sondage .intitule-sondage, .bloc-sondage .tab-choix .reponse, .bloc-sondage .pied-result, .bloc-sondage .tab-choix .pourcent {
        color: var(--jv-text-color);
    }
    .topic-list .topic-title {
        font-weight: 400;
        font-size:14px;
        margin-left: 0.5625rem;
    }
    .topic-list .topic-img {
        font-size: 1.125rem;
    }
    .topic-list .topic-subject {
        padding: 0.3125rem;
    }
}
}
