css:
@-moz-document 
domain(en.wikibooks.org),
domain(en.wikipedia.org),
domain(en.wikiquote.org),
domain(en.wikisource.org),
domain(en.wikiversity.org),
domain(en.wiktionary.org),
domain(commons.wikimedia.org),
domain(meta.wikimedia.org),
domain(species.wikimedia.org) {

#globalWrapper a:hover {
    color: #4166AF !important;
}

#globalWrapper a.new {
    color: #EF330E !important;
}

#globalWrapper a.new:hover {
    color:#FF795F !important;
}


#globalWrapper #column-one {
    cursor: default !important;
}

#globalWrapper #column-one #p-logo {
    margin: -5px 5px 0 -5px !important;
    padding: 5px 10px !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions):not(#p-logo) h5 {
    height: 3.5em !important;
    margin: 0 !important;
    padding: 0 10px !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions):not(#p-logo):not(#p-personal) {
    margin: 0 !important;
    left: -4em !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions):hover, #globalWrapper #column-one #p-search > h5 > label:hover{
    background: #ececec !important;
    cursor: pointer !important;
}

#globalWrapper #column-one #p-search .pBody {
    cursor: default !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions) .pBody {
    width: 140px !important;
    top: 3.1em !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions) .pBody > ul > li >a {
    padding: 3px 12px !important;
    display: block;
    margin: 0 !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions) .pBody > ul, #globalWrapper #column-one .portlet:not(#p-cactions) .pBody > ul > li, #globalWrapper #column-one .portlet:not(#p-cactions):not(#p-search) .pBody {
    padding: 0 !important;
    margin: 0 !important;
}

#globalWrapper #column-one .portlet:not(#p-cactions) .pBody > ul > li:hover {
    background: #f9f9f9 !important;
}

#globalWrapper #column-one #p-cactions .pBody  > ul > li > a {
    margin: 0;
    padding: 8px 30px !important;
}

#globalWrapper #column-one #p-cactions .pBody  > ul > li > a:hover {
    background: #f3f3f3 !important;
}

#globalWrapper .portlet:not(#p-cactions):not(#p-logo) .pBody {
    -moz-box-shadow: 0px 3px 6px #ddd !important;
    -moz-border-radius: 0 0 5px 5px;
}

#globalWrapper .portlet:not(#p-cactions):not(#p-logo) .pBody ul {
    padding-bottom: 5px !important;
}

#globalWrapper #p-lang .pBody {
    -moz-border-radius: 0 10px 10px 10px !important;
    padding: 5px !important;
}


#globalWrapper #column-one .portlet#p-lang .pBody {
    padding: 10px !important;
    width: 400px !important;
}

#globalWrapper #column-one #p-lang .pBody ul {
    -moz-column-count: 3;
    -moz-column-rule: 1px solid #ddd;
}

#globalWrapper #column-one #p-personal .pBody ul > li > a {
    padding: 8px 10px !important;
}

#globalWrapper #column-one #p-personal .pBody ul > li {
    background-image: none !important;
}

}
