css:
@-moz-document domain("cabinet.ruobr.ru") {
.c-top {
    height: 75px;
    position: fixed;
    left: 0px;
    top: 0px;
    width: 125%;
    background-color: #7c9885;
    /* background: linear-gradient(#7c9885 0%, #373737 100%);*/
    box-shadow: 0px 0px 0px rgb(105 105 105 / 0%);
}
body {
    background: linear-gradient(0deg, #2e2e2e, #d58484);
}
html {
    background-color: #2e2e2e
}
body {
    font-family: system-ui;
    margin: 0px;
    padding: 0px;
}
.left-menu .general-menu li a {
    color: #f2ccc3;
    display: block;
    padding: 20px 20px;
    border-radius: 3px;
    outline: none;
}
.left-menu .general-menu li.active a {
    background-color: #9381ff;
    color: #373737;
    border-radius: 25px;
}
.c-container .c-content {
    display: inline-block;
    vertical-align: top;
    position: relative;
    width: 1080px;
    margin-bottom: 10px;
    padding: 15px;
    background-color: #d9d9d9;
    border: 12px outset #95d8e7;
    border-radius: 45px;
    box-shadow: 0px 0px 0px #dadbdb;
}
.c-btn, .cabinet-btn, .ruobr-btn, .btn {
    display: inline-block;
    cursor: pointer;
    background-color: #2493e5;
    color: #fff;
    text-decoration: none;
    padding: 14px 20px;
    font-size: 16px;
    border-radius: 25px;
    border: none;
    background-image: none;
}
.ruobr-well {
    padding: 10px;
    border-radius: 0px;
    border: 7px outset #d76262;
    margin-bottom: 32px;
    background-color: #d9d9d9;
    color: #4a4a4a;
}
::-webkit-scrollbar-thumb {
    background: linear-gradient(92deg, #165d91, #95d8e7);
}
.left-menu {
    position: fixed;
    width: 160px;
}
}

@-moz-document url-prefix("https://cabinet.ruobr.ru/login/") {
.background {
    background: linear-gradient(0deg, #8f4d4d, #787070);
}
.ui[class*="two column"].grid > .column:not(.row), .ui[class*="two column"].grid > .row > .column {
    width: 100%;
}
.ui.icon.header:first-child {
    margin-top: 0;
    font-size: 35px;
}
.ui.segment:last-child {
    margin-bottom: 0;
    padding: 61px 150px 300px 150px;
}
html {
    font-size: 14px;
    background: #8e4d4d;
}
.ui.ui.ui.ui[class*="left icon"].input > input, .ui.ui.ui.ui[class*="left icon"].input > textarea {
    border-radius: 76px;
}
.ui.primary.button, .ui.primary.buttons .button {
    border-radius: 55px;
}
.ui.fluid.button {
    display: block;
    border-radius: 64px;
}
}

@-moz-document url-prefix("https://cabinet.ruobr.ru/child/mail") {

.ui-dialog .ui-dialog-titlebar {
    border-color: #ffffff00;
    padding: 20px 21px;
    font-size: 24px;
    border-bottom: 1px solid #999;
}

p {
    font-size: 17px;
    font-weight: lighter;
    line-height: 14px;
    padding: 25px 4px 21px 5px;
}

.ui-dialog-buttonpane .ui-dialog-buttonset .ui-button{
    
border-radius: 55px;
}
}
