a:link, a:visited{color:#122B46!important;}
a:hover{text-decoration:underline!important;}

#proximos a{font-size:100%!important;}

body{
background:#261D14 !important;
font-family: Frutiger, "Frutiger Linotype", Univers, Calibri, "Gill Sans", "Gill Sans MT", "Myriad Pro", Myriad, "DejaVu Sans Condensed", "Liberation Sans", "Nimbus Sans L", Tahoma, Geneva, "Helvetica Neue", Helvetica, Arial, sans-serif!important;
font-size:95%!important;
color:#261D14  !important;
}
/*Arregla El padding del maincontent*/
#maincontent{padding-bottom:10px!important;}

/*Resetea Todos los Bordes redondeados*/
#header, .alblog, .usuario, #barralateral, #maincontent, #pieamigo, #footer, .entradaenportada, .entrada, .linktags, .cajacomentarios, .commentmetadata
{
border:none!important;
-moz-border-radius: 0 0 0 0!important;
}
#header, #pieamigo{
border:none!important;
background:#ECEEF1!important;
height:100px!important;
}
#barralateral{
background:#F2F1ED  !important;
}
#publicar h3{
color:#261D14  !important;
}
input.boton{
background-color:#6CA7D8!important;
color:black!important;
padding:3px !important;

}
.tagclouditems a, .tagsextra a{
color:#E32C5E  !important;
}
.tagclouditems a:hover{
color:#F09678 !important;}


.entradaenportada, .entrada{
background:#F2F2F2!important;
min-height:150px!important;
}
.entradaenportada:hover, .entrada:hover{
background:#FFF5BA!important;
}
div.link h1 a{
font-size:65%!important;
font-weight:normal!important;
color:#285E95!important;
text-transform:capitalize!important;
}
div.link h1 a:hover{
color:#EB811A!important;
text-decoration:underline!important;
}
.botonbase{
background:transparent url(http://img697.imageshack.us/img697/5686/botonbase.png)no-repeat top center!important;
}
.botonbase a{
font-weight:200!important;
color:white!important;
text-shadow: #696E64 1px 1px 2px!important;
letter-spacing:0.1em!important;
}
img.gravatar{
background:#FFFFFF!important;
border-bottom:1px solid #DFDFDF!important;
border-right:1px solid #DFDFDF!important;
padding:4px!important;
}
.puntaje, .puntajemini{
background:transparent url(http://img410.imageshack.us/img410/2194/bgmain.png)repeat-x top center!important;
border: solid 1px #cfcfcf!important;
color:black!important;
margin-bottom:5px!important;
}
.analiticscontent, .analiticscontent li{
list-style:none!important;
padding:0!important;margin:0!important;
}
.analiticscontent li a{
background: url(http://img509.imageshack.us/img509/710/rss16.png)no-repeat!important;
padding-left:20px!important;
}
#container{background:transparent url(http://img149.imageshack.us/img149/9968/colfaux.png)repeat-y 250px center!important;
}
}
